import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { createAdmin } from '../services/api';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // ถ้าไม่มี Token ให้ Redirect ไปหน้า Login
    }
  }, [navigate]);

  const handleCreateAdmin = async () => {
    try {
      const res = await createAdmin(email, password);
      setMessage(res.data.message);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to create admin');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // ลบ Token ออกจาก Local Storage
    window.location.href = '/login'; // Redirect ไปหน้า Login
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>📊 Dashboard</h1>
        <button className="logout-button" onClick={handleLogout}>Logout</button>
      </header>

      <div className="dashboard-content">
        <section className="summary">
          <h2>🔹 สรุปข้อมูลธุรกิจ</h2>
          <p>คิวงานที่รอดำเนินการ: 10</p>
          <p>รายได้รวม: 50,000 บาท</p>
          <p>รายจ่ายรวม: 10,000 บาท</p>
        </section>

        <section className="actions">
        
        <button className="dashboard-button" onClick={() => navigate('/appointments')}>📅 จัดการนัดหมาย</button>    
          <button className="dashboard-button">🔄 คิวงานที่กำลังดำเนินการ</button>
          <button className="dashboard-button">📜 ประวัติงานที่เสร็จแล้ว</button>
          <button className="dashboard-button">🏦 รายรับ-รายจ่าย</button>
        </section>

        <section className="admin-management">
          <h2>👤 จัดการผู้ใช้ (เพิ่ม Admin)</h2>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="input-field" />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="input-field" />
          <button className="create-admin-button" onClick={handleCreateAdmin}>Create Admin</button>
          {message && <p className="message-text">{message}</p>}
        </section>
      </div>
    </div>
  );
};

export default Dashboard;
