import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQueue, getAppointments, createAdmin } from '../services/api';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [queue, setQueue] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // ถ้าไม่มี Token ให้ Redirect ไปหน้า Login
    }
    fetchQueueData();
    fetchAppointmentData();
  }, []);

  const fetchQueueData = async () => {
    try {
      const res = await getQueue();
      const filteredQueue = res.data
        .filter(item => ['รับเข้า', 'อยู่ระหว่างทำความสะอาด', 'เตรียมส่ง', 'กำลังจัดส่ง'].includes(item.status))
        .sort((a, b) => new Date(a.delivery_date) - new Date(b.delivery_date)); // เรียงตามวันที่กำหนดส่งที่ใกล้ที่สุด
      setQueue(filteredQueue);
    } catch (error) {
      console.error("🔴 Error fetching queue data:", error);
    }
  };

  const fetchAppointmentData = async () => {
    try {
      const res = await getAppointments();
      const filteredAppointments = res.data
        .filter(appt => ['รอดำเนินการ', 'ยืนยันแล้ว'].includes(appt.status))
        .sort((a, b) => new Date(a.appointment_date) - new Date(b.appointment_date)); // เรียงตามวันนัดหมายที่ใกล้ที่สุด
      setAppointments(filteredAppointments);
    } catch (error) {
      console.error("🔴 Error fetching appointments:", error);
    }
  };

  const handleCreateAdmin = async () => {
    try {
      const res = await createAdmin(email, password);
      setMessage(res.data.message);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to create admin');
    }
  };


  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>📊 Dashboard</h1>        
      </header>

      <div className="dashboard-content">
        <section className="summary">
          <h2>🔹 สรุปข้อมูลธุรกิจ</h2>
          <p>คิวงานที่รอดำเนินการ: {queue.length} รายการ</p>
          <p>นัดหมายที่รอดำเนินการ: {appointments.length} รายการ</p>
        </section>

       

        {/* ✅ แสดงคิวงานที่รอดำเนินการ */}
        <section className="queue-section">
          <h2>🔄 คิวงานที่รอดำเนินการ ({queue.length} รายการ)</h2>
          <table>
            <thead>
              <tr>
                <th>ลูกค้า</th>
                <th>สถานที่</th>
                <th>จำนวนคู่</th>
                <th>วันที่ส่งคืน</th>
                <th>สถานะ</th>
              </tr>
            </thead>
            <tbody>
              {queue.length > 0 ? (
                queue.map(item => (
                  <tr key={item.queue_id}>
                    <td>{item.customer_name}</td>
                    <td>{item.location}</td>
                    <td>{item.total_pairs}</td>
                    <td>{new Date(item.delivery_date).toLocaleDateString()}</td>
                    <td>{item.status}</td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="5">ไม่มีคิวงานที่รอดำเนินการ</td></tr>
              )}
            </tbody>
          </table>
        </section>

        {/* ✅ แสดงนัดหมายที่รอดำเนินการ */}
        <section className="appointment-section">
          <h2>📅 นัดหมายที่รอดำเนินการ ({appointments.length} รายการ)</h2>
          <table>
            <thead>
              <tr>
                <th>ลูกค้า</th>
                <th>เบอร์โทร</th>
                <th>วันนัดหมาย</th>
                <th>สถานะ</th>
              </tr>
            </thead>
            <tbody>
              {appointments.length > 0 ? (
                appointments.map(appt => (
                  <tr key={appt.id}>
                    <td>{appt.customer_name}</td>
                    <td>{appt.phone}</td>
                    <td>{new Date(appt.appointment_date).toLocaleDateString()}</td>
                    <td>{appt.status}</td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="4">ไม่มีนัดหมายที่รอดำเนินการ</td></tr>
              )}
            </tbody>
          </table>
        </section>
        {/* <section className="actions">
          <button className="dashboard-button" onClick={() => navigate('/appointments')}>📅 จัดการนัดหมาย</button>    
          <button className="dashboard-button" onClick={() => navigate('/queue')}>🔄 จัดการคิวงาน</button>
          <button className="dashboard-button">📜 ประวัติงานที่เสร็จแล้ว</button>
          <button className="dashboard-button">🏦 รายรับ-รายจ่าย</button>
        </section> */}
      </div>
    </div>
  );
};

export default Dashboard;
