import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQueue, getAppointments, createAdmin } from '../services/api';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [queue, setQueue] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ Token ‡πÉ‡∏´‡πâ Redirect ‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Login
    }
    fetchQueueData();
    fetchAppointmentData();
  }, []);

  const fetchQueueData = async () => {
    try {
      const res = await getQueue();
      const filteredQueue = res.data
        .filter(item => ['‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤', '‡∏≠‡∏¢‡∏π‡πà‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏ó‡∏≥‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏∞‡∏≠‡∏≤‡∏î', '‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏™‡πà‡∏á', '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏à‡∏±‡∏î‡∏™‡πà‡∏á'].includes(item.status))
        .sort((a, b) => new Date(a.delivery_date) - new Date(b.delivery_date)); // ‡πÄ‡∏£‡∏µ‡∏¢‡∏á‡∏ï‡∏≤‡∏°‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏™‡πà‡∏á‡∏ó‡∏µ‡πà‡πÉ‡∏Å‡∏•‡πâ‡∏ó‡∏µ‡πà‡∏™‡∏∏‡∏î
      setQueue(filteredQueue);
    } catch (error) {
      console.error("üî¥ Error fetching queue data:", error);
    }
  };

  const fetchAppointmentData = async () => {
    try {
      const res = await getAppointments();
      const filteredAppointments = res.data
        .filter(appt => ['‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£', '‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡πÅ‡∏•‡πâ‡∏ß'].includes(appt.status))
        .sort((a, b) => new Date(a.appointment_date) - new Date(b.appointment_date)); // ‡πÄ‡∏£‡∏µ‡∏¢‡∏á‡∏ï‡∏≤‡∏°‡∏ß‡∏±‡∏ô‡∏ô‡∏±‡∏î‡∏´‡∏°‡∏≤‡∏¢‡∏ó‡∏µ‡πà‡πÉ‡∏Å‡∏•‡πâ‡∏ó‡∏µ‡πà‡∏™‡∏∏‡∏î
      setAppointments(filteredAppointments);
    } catch (error) {
      console.error("üî¥ Error fetching appointments:", error);
    }
  };

  const handleCreateAdmin = async () => {
    try {
      const res = await createAdmin(email, password);
      setMessage(res.data.message);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to create admin');
    }
  };


  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>üìä Dashboard</h1>        
      </header>

      <div className="dashboard-content">
        <section className="summary">
          <h2>üîπ ‡∏™‡∏£‡∏∏‡∏õ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ò‡∏∏‡∏£‡∏Å‡∏¥‡∏à</h2>
          <p>‡∏Ñ‡∏¥‡∏ß‡∏á‡∏≤‡∏ô‡∏ó‡∏µ‡πà‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£: {queue.length} ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£</p>
          <p>‡∏ô‡∏±‡∏î‡∏´‡∏°‡∏≤‡∏¢‡∏ó‡∏µ‡πà‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£: {appointments.length} ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£</p>
        </section>

       

        {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏Ñ‡∏¥‡∏ß‡∏á‡∏≤‡∏ô‡∏ó‡∏µ‡πà‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£ */}
        <section className="queue-section">
          <h2>üîÑ ‡∏Ñ‡∏¥‡∏ß‡∏á‡∏≤‡∏ô‡∏ó‡∏µ‡πà‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£ ({queue.length} ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£)</h2>
          <table>
            <thead>
              <tr>
                <th>‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤</th>
                <th>‡∏™‡∏ñ‡∏≤‡∏ô‡∏ó‡∏µ‡πà</th>
                <th>‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ñ‡∏π‡πà</th>
                <th>‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏™‡πà‡∏á‡∏Ñ‡∏∑‡∏ô</th>
                <th>‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞</th>
              </tr>
            </thead>
            <tbody>
              {queue.length > 0 ? (
                queue.map(item => (
                  <tr key={item.queue_id}>
                    <td>{item.customer_name}</td>
                    <td>{item.location}</td>
                    <td>{item.total_pairs}</td>
                    <td>{new Date(item.delivery_date).toLocaleDateString()}</td>
                    <td>{item.status}</td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="5">‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ñ‡∏¥‡∏ß‡∏á‡∏≤‡∏ô‡∏ó‡∏µ‡πà‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£</td></tr>
              )}
            </tbody>
          </table>
        </section>

