import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getQueueDetail, getServices, updateQueue, addQueueItems, deleteQueueItem } from '../services/api';
import './QueueDetail.css';

const QueueDetail = () => {
    const navigate = useNavigate();
    const { queue_id } = useParams();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [selectedService, setSelectedService] = useState('');

    // ✅ เก็บข้อมูลรองเท้า
    const [shoeData, setShoeData] = useState({
        brand: '',
        model: '',
        color: '',
        notes: '',
        images: { front: '', back: '', left: '', right: '', top: '', bottom: '' }
    });

    useEffect(() => {
        fetchQueueDetail();
        fetchServices();
    }, []);

    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(queue_id);
            setQueue(res.data);
        } catch (error) {
            console.error("🔴 Error fetching queue detail:", error);
        }
    };

    const fetchServices = async () => {
        try {
            const res = await getServices();
            setServices(res.data);
        } catch (error) {
            console.error("🔴 Error fetching services:", error);
        }
    };

    // ✅ อัปโหลดรูปภาพ
    const handleUploadImage = (position, file) => {
        if (!file) return;
        const reader = new FileReader();
        reader.onloadend = () => {
            setShoeData(prev => ({
                ...prev,
                images: { ...prev.images, [position]: reader.result }
            }));
        };
        reader.readAsDataURL(file);
    };

    //เพิ่ม onChange ให้สามารถเลือกรูปจากอัลบั้ม / กล้องบนมือถือ
    const handleImageUpload = (event, position) => {
        const file = event.target.files[0];
        if (file) {
            setShoeData(prevData => ({
                ...prevData,
                images: {
                    ...prevData.images,
                    [position]: file
                }
            }));
        }
    };

    
    // ✅ เพิ่มรองเท้าเข้า queue_items
    const handleAddShoe = async () => {
        if (!selectedService || !shoeData.brand || !shoeData.model || !shoeData.color) {
            alert("กรุณากรอกข้อมูลให้ครบถ้วน");
            return;
        }

        const formData = new FormData();
        formData.append("queue_id", queue_id);
        formData.append("service_id", selectedService);
        formData.append("price_per_pair", parseFloat(services.find(s => s.id === parseInt(selectedService))?.base_price || 0));
        formData.append("brand", shoeData.brand);
        formData.append("model", shoeData.model);
        formData.append("color", shoeData.color);
        formData.append("notes", shoeData.notes);

        // ✅ ตรวจสอบค่าที่ส่งไป
        console.log("📌 Debug: Data being sent to API", Object.fromEntries(formData.entries()));

        // ✅ แนบรูปภาพ (ถ้ามี)
        Object.keys(shoeData.images).forEach((key) => {
            if (shoeData.images[key]) {
                formData.append(`image_${key}`, shoeData.images[key]);
            }
        });

        try {
            await addQueueItems(formData);
            alert("✅ เพิ่มรองเท้าเรียบร้อย!");
            fetchQueueDetail(); // โหลดข้อมูลใหม่
        } catch (error) {
            console.error("🔴 Error adding shoe:", error);
        }
    };

    // ✅ ลบรองเท้าออกจาก queue_items
    const handleRemoveShoe = async (queue_item_id) => {
        if (!window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบรองเท้านี้?")) return;
        try {
            await deleteQueueItem(queue_item_id);
            alert("✅ ลบรองเท้าเรียบร้อย!");
            fetchQueueDetail();
        } catch (error) {
            console.error("🔴 Error deleting shoe:", error);
        }
    };

    // ✅ บันทึกการเปลี่ยนแปลง
    const handleSaveChanges = async () => {
        if (!window.confirm("คุณต้องการบันทึกการเปลี่ยนแปลงหรือไม่?")) return;
        try {
            await updateQueue(queue_id);
            alert("✅ บันทึกข้อมูลเรียบร้อยแล้ว!");
            navigate('/queue');
        } catch (error) {
            console.error("🔴 Error updating queue:", error);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    if (!queue) return <p>⏳ กำลังโหลดข้อมูล...</p>;

    return (
        <div className="queue-detail-container">
            <h1>🏷️ Queue Detail (#{queue_id})</h1>

            <div className="queue-info">
                <p>👤 ลูกค้า: {queue.customer_name}</p>
                <p>📞 เบอร์โทร: {queue.phone}</p>
                <p>📍 สถานที่: {queue.location}</p>
                <p>👟 จำนวนคู่: {queue.total_pairs}</p>
                <p>📅 รับเข้า: {formatDate(queue.received_date)}</p>
                <p>🚚 กำหนดส่งคืน: {formatDate(queue.delivery_date)}</p>
            </div>

            <h3>📋 เพิ่มรองเท้าเข้า Queue</h3>
            <div className="shoe-details">
                <select value={selectedService} onChange={(e) => setSelectedService(e.target.value)}>
                    <option value="">เลือกบริการ</option>
                    {services.map(service => (
                        <option key={service.id} value={service.id}>
                            {service.service_name} ({parseFloat(service.base_price || 0).toFixed(2)} บาท/คู่)
                        </option>
                    ))}
                </select>
                <input type="text" placeholder="แบรนด์รองเท้า" value={shoeData.brand} onChange={(e) => setShoeData({ ...shoeData, brand: e.target.value })} />
                <input type="text" placeholder="รุ่นรองเท้า" value={shoeData.model} onChange={(e) => setShoeData({ ...shoeData, model: e.target.value })} />
                <input type="text" placeholder="สี" value={shoeData.color} onChange={(e) => setShoeData({ ...shoeData, color: e.target.value })} />
                <textarea placeholder="หมายเหตุเพิ่มเติม" value={shoeData.notes} onChange={(e) => setShoeData({ ...shoeData, notes: e.target.value })}></textarea>
            </div>

            <h3>📷 อัปโหลดรูปภาพรองเท้า</h3>
            <div className="image-upload">
                {["front", "back", "left", "right", "top", "bottom"].map((position, index) => (
                    <div key={index} className="image-upload-box">
                        <label>{`รูปภาพ ${position}`}</label>
                        <input 
                            type="file" 
                            accept="image/*" 
                            capture="environment" // ✅ เปิดกล้องบนมือถือ
                            onChange={(e) => handleImageUpload(e, position)}
                        />
                        {shoeData.images[position] && 
                            <img src={URL.createObjectURL(shoeData.images[position])} alt={`shoe-${position}`} width="50" />}
                    </div>
                ))}
            </div>


            <button onClick={handleAddShoe}>➕ เพิ่มรองเท้า</button>

            <div className="queue-actions">
                <button onClick={() => navigate('/queue')}>🔄 กลับไปหน้าคิว</button>
                <button onClick={handleSaveChanges}>✅ บันทึกการเปลี่ยนแปลง</button>
            </div>
        </div>
    );
};

export default QueueDetail;
