                    setUserName(user.email || "‡πÑ‡∏°‡πà‡∏ó‡∏£‡∏≤‡∏ö‡∏ä‡∏∑‡πà‡∏≠");
                    setRole(user.isSuperAdmin ? "SuperAdmin" : "Employee");
                    setbranchId(user.branchId);
                    fetchBranches();
                }
                
            } catch (error) {
                console.error("üî¥ Error decoding token:", error);
                localStorage.removeItem("token"); // ‚úÖ ‡∏ñ‡πâ‡∏≤ Token ‡πÉ‡∏ä‡πâ‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ ‡πÉ‡∏´‡πâ‡∏•‡∏ö‡∏≠‡∏≠‡∏Å
                navigate("/login"); // ‚úÖ ‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Login
            }
        }
     else {
        navigate("/login");}
    
    }, [navigate]);

    const fetchBranches = async () => {
            try {
                const res = await getBranchById();
                setBranches(res.data);
            } catch (error) {
                console.error("üî¥ Error fetching branches:", error);
            }
        };

        
    const handleLogout = () => {
        localStorage.removeItem("token"); // ‚úÖ ‡∏•‡∏ö Token
        navigate("/login"); // ‚úÖ Redirect ‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Login
    };

    return (
        <div className={`sidebar-container ${isOpen ? "open" : ""}`}>
            {/* ‡∏õ‡∏∏‡πà‡∏° Toggle ‡πÄ‡∏°‡∏ô‡∏π */}
