import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getQueueDetail, getAllServices, addQueueItems, deleteQueueItem, uploadAfterImages } from '../services/api';
import './QueueDetail.css';
import '../assets/css/bootstrap.min.css';
import AddShoeModal from "../components/AddShoeModal"; 
import ShoeDetailModal from "../components/ShoeDetailModal"; 
import AddExpenseModal from "../components/AddExpenseModal";

const QueueDetail = () => {
    const navigate = useNavigate();
    const { queue_id } = useParams();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [expenses, setExpenses] = useState([]);

    const Backend_URL = 'http://localhost:5000';

    const [showAddShoeModal, setShowAddShoeModal] = useState(false);
    const [showShoeDetailModal, setShowShoeDetailModal] = useState(false);
    const [selectedShoe, setSelectedShoe] = useState(null);

    const [showAddExpenseModal, setShowAddExpenseModal] = useState(false);

    const totalPairs = queue?.queue_items?.length || 0;
    const totalPrice = queue?.queue_items?.reduce((sum, item) => sum + parseFloat(item.price_per_pair), 0 || 0.00);

    const [afterImages, setAfterImages] = useState({
        front: null,
        back: null,
        left: null,
        right: null,
        top: null,
        bottom: null
    });

    const [showUploadAfterModal, setShowUploadAfterModal] = useState(false);
    const [selectedItemId, setSelectedItemId] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        }
        if (queue_id) {
            fetchQueueDetail();
            fetchServices();
           // fetchExpenses();
        }
    }, [queue_id]);

    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(queue_id);
            console.log("ЁЯУМ Debug: р╕Вр╣Йр╕нр╕бр╕╣р╕ер╕Ир╕▓р╕Б API", res.data); // тЬЕ р╕Хр╕гр╕зр╕Ир╕кр╕нр╕Ър╕Вр╣Йр╕нр╕бр╕╣р╕ер╕Чр╕╡р╣Ир╕бр╕▓р╕Ир╕▓р╕Б API
            setQueue(res.data);
        } catch (error) {
            console.error("ЁЯФ┤ Error fetching queue detail:", error);
        }
    };

    const fetchServices = async () => {
        try {
            const res = await getAllServices();            
            setServices(res.data);
        } catch (error) {
            console.error("ЁЯФ┤ Error fetching services:", error.response?.data || error.message);
        }
    };
    
    const fetchExpenses = async () => {
        try {
            const res = await getExpenses(queue_id);
            setExpenses(res.data);
        } catch (error) {
            console.error("ЁЯФ┤ Error fetching expenses:", error);
        }
    };

    const handleAddExpense = async () => {
        if (!newExpense.description || !newExpense.amount) {
            alert("тЭМ р╕Бр╕гр╕╕р╕Ур╕▓р╕Бр╕гр╕нр╕Бр╕Вр╣Йр╕нр╕бр╕╣р╕ер╣Гр╕лр╣Йр╕Др╕гр╕Ър╕Цр╣Йр╕зр╕Щ");
            return;
        }

        try {
            await addExpense(queue_id, newExpense);
            alert("тЬЕ р╣Ар╕Юр╕┤р╣Ир╕бр╕Др╣Ир╕▓р╣Гр╕Кр╣Йр╕Ир╣Ир╕▓р╕вр╣Ар╕гр╕╡р╕вр╕Ър╕гр╣Йр╕нр╕в!");
            fetchExpenses();
            setShowAddExpenseModal(false);
            setNewExpense({ description: "", amount: "" });
        } catch (error) {
