                                </div>
                            ))}
                        </div>
                        <div className="modal-actions">
                            <button onClick={closeUploadAfterModal}>тЭМ р╕Ыр╕┤р╕Ф</button>
                            <button onClick={handleUploadAfterImages}>тЬЕ р╕нр╕▒р╕Ыр╣Вр╕лр╕ер╕Ф</button>
                        </div>
                    </div>
                </div>
            )}


            {showAddShoeModal && (
                <AddShoeModal 
                    show={showAddShoeModal} 
                    onClose={() => {
                        setShowAddShoeModal(false);
                    }} 
                    onAddShoe={(selectedService, shoeData) => {
                        handleAddShoe(selectedService, shoeData); // тЬЕ р╣Ар╕гр╕╡р╕вр╕Б API р╕Чр╕╡р╣Ир╣Ар╕Юр╕┤р╣Ир╕бр╕гр╕нр╕Зр╣Ар╕Чр╣Йр╕▓
                    }} 
                    services={services} 
                />
            )}

            {showShoeDetailModal && (
                <ShoeDetailModal 
                show={showShoeDetailModal} 
                onClose={() => setShowShoeDetailModal(false)} 
                shoe={selectedShoe} 
                queue_id={queue_id} // тЬЕ р╕кр╣Ир╕З queue_id р╣Др╕Ы
                fetchQueueDetail={fetchQueueDetail} 
                uploadAfterImages={uploadAfterImages} 
            />        
            )}

            {/* Modal р╣Ар╕Юр╕┤р╣Ир╕бр╕Др╣Ир╕▓р╣Гр╕Кр╣Йр╕Ир╣Ир╕▓р╕в */}
            <AddExpenseModal
                show={showAddExpenseModal}
                onClose={() => setShowAddExpenseModal(false)}
                onAddExpense={handleAddExpense}
            />


        </div>
    );
};

export default QueueDetail;
