import React, { useState ,useEffect} from 'react';
import { useNavigate } from "react-router-dom";
import { login } from '../services/api';
import Input from '../components/ui/input';   // à¹€à¸›à¸¥à¸µà¹ˆà¸¢à¸™à¹€à¸›à¹‡à¸™ default import
import Button from '../components/ui/button'; // à¹€à¸›à¸¥à¸µà¹ˆà¸¢à¸™à¹€à¸›à¹‡à¸™ default import
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Lock, Mail } from 'lucide-react';
import '../styles/Login.css';


const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token"); // à¸«à¸£à¸·à¸­ sessionStorage.getItem("token")
    if (token) {
      navigate("/dashboard"); // à¸–à¹‰à¸²à¸¡à¸µ Token à¹ƒà¸«à¹‰à¹„à¸›à¸—à¸µà¹ˆà¸«à¸™à¹‰à¸² Dashboard à¸—à¸±à¸™à¸—à¸µ
    }
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await login(email, password);
      localStorage.setItem('token', response.data.token);
      window.location.href = '/dashboard';
    } catch (err) {
      setError('Invalid credentials');
    }
  };
  return (
    <div className="login-container">
      <div className="login-card">
        <Card className="login-card-content">
          <CardHeader>
            <CardTitle className="login-title">ðŸ”‘ Admin Login</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleLogin} className="login-form">
              <div className="input-group">
                <Mail className="input-icon" size={20} />
                <Input 
                  type="email" 
                  placeholder="Email" 
                  value={email} 
                  onChange={(e) => setEmail(e.target.value)} 
                  required 
                  className="input-field"
                />
              </div>
              <div className="input-group">
                <Lock className="input-icon" size={20} />
                <Input 
                  type="password" 
                  placeholder="Password" 
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} 
                  required 
                  className="input-field"
                />
              </div>
              {error && <p className="error-message">{error}</p>}
              <div className="button-container">
                <Button type="submit" className="login-button">Login</Button>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};
export default Login;
