import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQueue, getAppointments, getRevenue } from '../services/api';
import './Dashboard.css';
import '../assets/css/bootstrap.min.css';

const statusColors = {
  'รับเข้า': '#FFCDD2',
  'อยู่ระหว่างทำความสะอาด': '#FFE0B2',
  'เตรียมส่ง': '#FFF9C4',
  'กำลังจัดส่ง': '#F8BBD0',
  'สำเร็จ': '#C8E6C9',
};

const appointmentColors = {
  'รอดำเนินการ': '#FFCDD2',
  'ยืนยันแล้ว': '#FFF9C4',
  'สำเร็จ': '#C8E6C9',
};

const Dashboard = () => {
  const navigate = useNavigate();
  const [queue, setQueue] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [revenue, setRevenue] = useState(0);
  const [totalShoes, setTotalShoes] = useState(0);
  const [currentDate, setCurrentDate] = useState(new Date());

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
    fetchQueueData();
    fetchAppointmentData();
    fetchRevenueData();
  }, []);

  const fetchQueueData = async () => {
    try {
      const res = await getQueue();
      const filteredQueue = res.data.filter(item => Object.keys(statusColors).includes(item.status));
      setQueue(filteredQueue);
      setTotalShoes(filteredQueue.reduce((sum, item) => sum + item.total_pairs, 0));
    } catch (error) {
      console.error('🔴 Error fetching queue data:', error);
    }
  };

  const fetchAppointmentData = async () => {
    try {
      const res = await getAppointments();
      const filteredAppointments = res.data.filter(appt => Object.keys(appointmentColors).includes(appt.status));
      setAppointments(filteredAppointments);
    } catch (error) {
      console.error('🔴 Error fetching appointments:', error);
    }
  };

  const fetchRevenueData = async () => {
    try {
      const res = await getRevenue();
      setRevenue(res.data.revenue);
    } catch (error) {
      console.error('🔴 Error fetching revenue data:', error);
    }
  };

  
  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2>📅 {currentDate.toLocaleDateString()} - ⏰ {currentDate.toLocaleTimeString()}</h2>
      </div>
      
      <div className="dashboard-cards">
        <div className="card">
          <h3>💰 รายรับรวมเดือนนี้</h3>
          <p className="card-value">฿{revenue.toLocaleString()}</p>
        </div>
        <div className="card">
          <h3>👟 จำนวนรองเท้ารวมเดือนนี้</h3>
          <p className="card-value">{totalShoes} คู่</p>
        </div>
      </div>
      
      <h2>🔄 คิวงานที่รอดำเนินการ ({queue.length} รายการ)</h2>
      <table className="table table-hover">
        <thead>
          <tr>
            <th>ลูกค้า</th>
            <th>สถานที่</th>
            <th>จำนวนคู่</th>
            <th>วันที่รับ</th>
            <th>วันที่ส่งคืน</th>
            <th>สถานะ</th>
          </tr>
        </thead>
        <tbody>
          {queue.map(item => (
            <tr key={item.queue_id}>
              <td>{item.customer_name}</td>
              <td>{item.location}</td>
              <td>{item.total_pairs}</td>
              <td>{new Date(item.received_date).toLocaleDateString()}</td>
              <td>{new Date(item.delivery_date).toLocaleDateString()}</td>
              <td><span className="status-label" style={{ backgroundColor: statusColors[item.status] }}>{item.status}</span></td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>📅 นัดหมายที่รอดำเนินการ ({appointments.length} รายการ)</h2>
      <table className="table table-hover">
        <thead>
          <tr>
            <th>ลูกค้า</th>
            <th>เบอร์โทร</th>
            <th>สถานที่</th>
            <th>วัน/เวลานัดหมาย</th>
            <th>สถานะ</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map(appt => (
            <tr key={appt.id}>
              <td>{appt.customer_name}</td>
              <td>{appt.phone}</td>
              <td>{appt.location}</td>
              <td>{new Date(appt.appointment_date).toLocaleDateString()} {appt.appointment_time.slice(0,5)} น.</td>
              <td><span className="status-label" style={{ backgroundColor: appointmentColors[appt.status] }}>{appt.status}</span></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
