import React from "react";
import "../assets/css/bootstrap.min.css";

const UpdateQueueStatusModal = ({ show, handleClose, handleUpdate, queueId, currentStatus }) => {
    const [status, setStatus] = React.useState(currentStatus);

    const handleSubmit = () => {
        handleUpdate(queueId, status);
        handleClose();
    };

    return (
        <Modal show={show} onHide={handleClose} centered>
            <Modal.Header closeButton>
                <Modal.Title>อัปเดตสถานะคิว</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group>
                        <Form.Label>สถานะคิว</Form.Label>
                        <Form.Select value={status} onChange={(e) => setStatus(e.target.value)}>
                            <option value="pending">รอดำเนินการ</option>
                            <option value="in_progress">กำลังดำเนินการ</option>
                            <option value="completed">เสร็จสิ้น</option>
                            <option value="cancelled">ยกเลิก</option>
                        </Form.Select>
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    ปิด
                </Button>
                <Button variant="primary" onClick={handleSubmit}>
                    บันทึกการเปลี่ยนแปลง
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default UpdateQueueStatusModal;
