import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQueue, updateQueueStatus, deleteQueue } from '../services/api';
import './QueueAdmin.css';

const QueueAdmin = () => {
    const navigate = useNavigate();
    const [queue, setQueue] = useState([]);
    const [statusFilter, setStatusFilter] = useState(['รับเข้า', 'อยู่ระหว่างทำความสะอาด']);
    const [filteredQueue, setFilteredQueue] = useState([]);
    const [expandedQueue, setExpandedQueue] = useState(null);

    // ✅ คำนวณวันที่เริ่มต้นและสิ้นสุดของเดือนปัจจุบัน
    const today = new Date();
    const firstDayOfMonth = new Date(today.getFullYear(), today.getMonth(), 1).toISOString().split('T')[0];
    const lastDayOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0).toISOString().split('T')[0];

    const [startDate, setStartDate] = useState(firstDayOfMonth);
    const [endDate, setEndDate] = useState(lastDayOfMonth);

    useEffect(() => {
        fetchQueue();
    }, []);

    useEffect(() => {
        filterQueueData();
    }, [statusFilter, queue, startDate, endDate]);

    const fetchQueue = async () => {
        try {
            const res = await getQueue();
            setQueue(res.data);
        } catch (error) {
            console.error("🔴 Error fetching queue:", error);
        }
    };

    const filterQueueData = () => {
        const filtered = queue.filter(item => 
            statusFilter.includes(item.status) &&
            new Date(item.delivery_date) >= new Date(startDate) &&
            new Date(item.delivery_date) <= new Date(endDate)
        ).sort((a, b) => new Date(b.delivery_date) - new Date(a.delivery_date));

        setFilteredQueue(filtered);
    };

    const handleStatusUpdate = async (id, currentStatus, totalPrice) => {
        if (!id || typeof id !== "number") {
            alert("เกิดข้อผิดพลาด: ไม่พบ Queue ID กรุณาลองใหม่");
            return;
        }
    
        const statusMap = {
            'รับเข้า': 'อยู่ระหว่างทำความสะอาด',
            'อยู่ระหว่างทำความสะอาด': 'เตรียมส่ง',
            'เตรียมส่ง': 'กำลังจัดส่ง',
            'กำลังจัดส่ง': 'จัดส่งสำเร็จ'
        };
    
        const newStatus = statusMap[currentStatus];
        if (!newStatus) return;

        if (!window.confirm(`คุณต้องการเปลี่ยนสถานะเป็น "${newStatus}" หรือไม่?`)) return;
    
        const parsedTotalPrice = parseFloat(totalPrice) || 0;
        if (parsedTotalPrice === 0) {
            alert("❌ กรุณาเพิ่มรายละเอียดก่อนอัปเดตสถานะ!");
            return;
        }            
        
        try {
            await updateQueueStatus(id, newStatus, totalPrice);
            fetchQueue();
        } catch (error) {
            console.error("🔴 Error updating queue status:", error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบคิวนี้?")) {
            try {
                await deleteQueue(id);
                fetchQueue();
            } catch (error) {
                console.error("🔴 Error deleting queue:", error);
            }
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    const handleStatusFilterChange = (status) => {
        setStatusFilter(prevFilters =>
            prevFilters.includes(status)
                ? prevFilters.filter(item => item !== status)
                : [...prevFilters, status]
        );
    };

    return (
        <div className="queue-container">
            <h1>📋 ระบบคิวงาน</h1>
            <button onClick={() => navigate('/dashboard')}>⬅️ กลับไปหน้า Dashboard</button>
            <button onClick={() => navigate('/queue-entry')}>🆕 รับเข้าใหม่</button>

            {/* ✅ ฟิลเตอร์ช่วงวันที่ */}
            <div className="date-filters">
                <label>📅 จาก: <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} /></label>
                <label>📅 ถึง: <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} /></label>
            </div>

            {/* ✅ ฟิลเตอร์สถานะ */}
            <div className="filters">
                {['รับเข้า', 'อยู่ระหว่างทำความสะอาด', 'เตรียมส่ง', 'กำลังจัดส่ง', 'จัดส่งสำเร็จ'].map(status => (
                    <label key={status}>
                        <input
                            type="checkbox"
                            checked={statusFilter.includes(status)}
                            onChange={() => handleStatusFilterChange(status)}
                        /> {status}
                    </label>
                ))}
            </div>

            <table>
                <thead>
                    <tr>
                        <th>ชื่อ</th>
                        <th>เบอร์โทร</th>
                        <th>สถานที่</th>
                        <th>จำนวนคู่</th>                        
                        <th>จำนวนเงิน</th>
                        <th>วันที่รับเข้า</th>
                        <th>วันที่ส่งคืน</th>
                        <th>สถานะ</th>
                        <th>อัปเดต</th>
                        <th>ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredQueue.map(item => (                      
                        <tr key={item.queue_id}>
                            <td>{item.customer_name}</td>
                            <td>{item.phone}</td>
                            <td>{item.location}</td>
                            <td>{item.total_pairs}</td>
                            <td>{item.total_price ? item.total_price.toFixed(2) : '0.00'} บาท</td>
                            <td>{formatDate(item.received_date)}</td>
                            <td>{formatDate(item.delivery_date)}</td>
                            <td>{item.status}</td>
                            <td>
                                {item.status !== 'จัดส่งสำเร็จ' && (
                                    <button onClick={() => handleStatusUpdate(item.queue_id, item.status, item.total_price)}>
                                        อัปเดต
                                    </button>
                                )}
                            </td>
                            <td>
                                {item.status !== 'จัดส่งสำเร็จ' && (
                                    <button onClick={() => handleDelete(item.queue_id)}>ลบ</button>
                                )}
                            </td>
                            <td>
                                {item.status !== 'จัดส่งสำเร็จ' && (
                                    <button onClick={() => navigate(`/queue/${item.queue_id}/detail`)}>รายละเอียด</button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default QueueAdmin;
