                            <button onClick={handleUploadAfterImages}>‚úÖ ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î</button>
                        </div>
                    </div>
                </div>
            )}


            {showAddShoeModal && (
                <AddShoeModal 
                    show={showAddShoeModal} 
