                    }} 
                    services={services} 
                />
            )}

            {showShoeDetailModal && (
                <ShoeDetailModal 
                    show={showShoeDetailModal} 
                    onClose={() => setShowShoeDetailModal(false)} 
                    shoe={selectedShoe} 
                />
            )}


        </div>
    );
};

