import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getQueueDetail, getServices, updateQueue, addQueueItems, deleteQueueItem,uploadAfterImages  } from '../services/api';
import './QueueDetail.css';

import AddShoeModal from "./components/AddShoeModal"; // นำเข้า Modal

const QueueDetail = () => {
    const navigate = useNavigate();
    const { queue_id } = useParams();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [selectedService, setSelectedService] = useState('');
    const API_URL = process.env.REACT_APP_API_URL; //ดึง URL Backend
    console.log("📌 Debug: API_URL =", API_URL);
    
    // ✅ เก็บข้อมูลรองเท้า
    const [shoeData, setShoeData] = useState({
        brand: '',
        model: '',
        color: '',
        notes: '',
        images: { front: '', back: '', left: '', right: '', top: '', bottom: '' }
    });

    // เพิ่ม State เก็บไฟล์รูป After
    const [showModal, setShowModal] = useState(false);
    const [selectedItemId, setSelectedItemId] = useState(null);
    const [afterImages, setAfterImages] = useState({
        front: null,
        back: null,
        left: null,
        right: null,
        top: null,
        bottom: null
    });

    

    useEffect(() => {
        fetchQueueDetail();
        fetchServices();
    }, []);

    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(queue_id);
            console.log("data:",res);
            setQueue(res.data);
        } catch (error) {
            console.error("🔴 Error fetching queue detail:", error);
        }
    };

    const fetchServices = async () => {
        try {
            const res = await getServices();
            setServices(res.data);
        } catch (error) {
            console.error("🔴 Error fetching services:", error);
        }
    };

    // ✅ อัปโหลดรูปภาพ
    const handleUploadImage = (position, file) => {
        if (!file) return;
        const reader = new FileReader();
        reader.onloadend = () => {
            setShoeData(prev => ({
                ...prev,
                images: { ...prev.images, [position]: reader.result }
            }));
        };
        reader.readAsDataURL(file);
    };

    //เพิ่ม onChange ให้สามารถเลือกรูปจากอัลบั้ม / กล้องบนมือถือ
    const handleImageUpload = (event, position) => {
        const file = event.target.files[0];
        if (file) {
            setShoeData(prevData => ({
                ...prevData,
                images: {
                    ...prevData.images,
                    [position]: file
                }
            }));
        }
    };

    
    // ✅ เพิ่มรองเท้าเข้า queue_items
    const handleAddShoe = async () => {
        if (!selectedService || !shoeData.brand || !shoeData.model || !shoeData.color) {
            alert("❌ กรุณากรอกข้อมูลให้ครบถ้วน");
            return;
        }
    
        const formData = new FormData();
        formData.append("queue_id", queue_id);
        formData.append("service_id", selectedService);
        formData.append("price_per_pair", services.find(s => s.id === parseInt(selectedService))?.base_price || 0);
        formData.append("brand", shoeData.brand);
        formData.append("model", shoeData.model);
        formData.append("color", shoeData.color);
        formData.append("notes", shoeData.notes || "");

    
        // ✅ เพิ่มไฟล์รูปเข้า `FormData`
        Object.keys(shoeData.images).forEach((key) => {
            if (shoeData.images[key]) {
                formData.append(key, shoeData.images[key]); // ✅ Key ต้องตรงกับ Backend
            }
        });
    
        // ✅ Debug เช็คว่าไฟล์ถูกเพิ่มเข้าหรือไม่
        /* console.log("📌 Debug: FormData being sent to API:");
        for (let pair of formData.entries()) {
            console.log(pair[0], pair[1]);
        } */
    
        try {
            await addQueueItems(queue_id, formData);
            alert("✅ เพิ่มรองเท้าเรียบร้อย!");
            fetchQueueDetail();
        } catch (error) {
            console.error("🔴 Error adding shoe:", error);
        }
    };     

    

    // ✅ ลบรองเท้าออกจาก queue_items
    const handleRemoveShoe = async (queue_item_id) => {
        if (!queue_item_id) {
            console.error("❌ ไม่พบ queue_item_id");
            return;
        }

        if (!window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบรองเท้านี้?")) return;
        try {
            console.log(`📌 Debug: ลบ queue_item_id: ${queue_item_id}`);
            await deleteQueueItem(queue_id, queue_item_id);
            alert("✅ ลบรองเท้าเรียบร้อย!");
            fetchQueueDetail();
        } catch (error) {
            console.error("🔴 Error deleting shoe:", error);
        }
    };

    // ✅ บันทึกการเปลี่ยนแปลง
    const handleSaveChanges = async () => {
        if (!window.confirm("คุณต้องการบันทึกการเปลี่ยนแปลงหรือไม่?")) return;
        try {
            await updateQueue(queue_id);
            alert("✅ บันทึกข้อมูลเรียบร้อยแล้ว!");
            navigate('/queue');
        } catch (error) {
            console.error("🔴 Error updating queue:", error);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

  

        //------------------modal---------------------
        //ฟังก์ชันเปิด Modal และกำหนด item_id ของรองเท้า
        const openModal = (item_id) => {
            console.log("📌 Debug: เปิด Modal สำหรับ Item ID:", item_id);
            setSelectedItemId(item_id);
            setShowModal(true);
        };
        
        const closeModal = () => {
            setShowModal(false);
            setAfterImages({ front: null, back: null, left: null, right: null, top: null, bottom: null });
        };

        const handleAfterImageUpload = (position, file) => {
            if (!file) return;
            setAfterImages(prev => ({ ...prev, [position]: file }));
        };
        
        const handleUploadAfterImages = async () => {
            if (!selectedItemId) {
                alert("❌ ไม่พบ Item ID");
                return;
            }
        
            try {
                console.log("📌 Debug: กำลังอัปโหลดรูป After...", selectedItemId, afterImages);
                await uploadAfterImages(queue_id, selectedItemId, afterImages);
                alert("✅ อัปโหลดรูป After สำเร็จ!");
                await fetchQueueDetail(); // รีเฟรชข้อมูลใหม่
                closeModal(); // ปิด Modal หลังอัปโหลดสำเร็จ
            } catch (error) {
                console.error("🔴 Error uploading after images:", error);
            }
        };
        
        
         if (!queue) return <p>⏳ กำลังโหลดข้อมูล...</p>;
    return (
        <div className="queue-detail-container">
            <h1>🏷️ Queue Detail (#{queue_id})</h1>
            <div className="queue-actions">
                <button onClick={() => navigate('/queue')}>🔄 กลับไปหน้าคิว</button>
            </div>
            <div className="queue-info">
                <p>👤 ลูกค้า: {queue.customer_name}</p>
                <p>📞 เบอร์โทร: {queue.phone}</p>
                <p>📍 สถานที่: {queue.location}</p>
                <p>👟 จำนวนคู่: {queue.total_pairs}</p>
                <p>📅 รับเข้า: {formatDate(queue.received_date)}</p>
                <p>🚚 กำหนดส่งคืน: {formatDate(queue.delivery_date)}</p>
            </div>

            <h2>👟 รายการรองเท้า</h2>
            <button onClick={() => setShowModal(true)}>➕ เพิ่มรองเท้า</button>

            <AddShoeModal show={showModal} onClose={() => setShowModal(false)} onAddShoe={handleAddShoe} services={services} />
            <table>
                <thead>
                    <tr>
                        <th>แบรนด์</th>
                        <th>รุ่น</th>
                        <th>สี</th>
                        <th>หมายเหตุ</th>
                        <th>รูปภาพ</th>
                        <th>รูป After</th>
                        <th>รายละเอียด</th>
                        <th>ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {queue.queue_items.map((item, index) => (
                        
                        <tr key={index}>
                            <td>{item.brand}</td>
                            <td>{item.model}</td>
                            <td>{item.color}</td>
                            <td>{item.notes}</td>
                            <td>
                                <div className="shoe-images">
                                    {/* ✅ แสดงรูปภาพ Before ถ้ามี */}
                                    {[item.image_before_front]
                                        
                                        .filter(img => img) // ✅ กรองค่าที่เป็น null หรือ undefined
                                        .map((img, i) => (                                            
                                            <img key={i} src={`http://localhost:5000${img}`} alt={`shoe-${index}-${i}`} width="50" />                                      
                                        ))
                                    }
                                </div>
                            </td>
                            <td>
                                <button onClick={() => openModal(item.queue_item_id)}>Upload</button>
                            </td>
                            <td>
                            <button onClick={() => openModal(item.queue_item_id)}>รายละเอียด</button>
                            </td>
                            <td>
                                <button onClick={() => handleRemoveShoe(item.queue_item_id)}>❌</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>


            <h3>📋 เพิ่มรองเท้าเข้า Queue</h3>
            <div className="shoe-details">
                <select value={selectedService} onChange={(e) => setSelectedService(e.target.value)}>
                    <option value="">เลือกบริการ</option>
                    {services.map(service => (
                        <option key={service.id} value={service.id}>
                            {service.service_name} ({parseFloat(service.base_price || 0).toFixed(2)} บาท/คู่)
                        </option>
                    ))}
                </select>
                <input type="text" placeholder="แบรนด์รองเท้า" value={shoeData.brand} onChange={(e) => setShoeData({ ...shoeData, brand: e.target.value })} />
                <input type="text" placeholder="รุ่นรองเท้า" value={shoeData.model} onChange={(e) => setShoeData({ ...shoeData, model: e.target.value })} />
                <input type="text" placeholder="สี" value={shoeData.color} onChange={(e) => setShoeData({ ...shoeData, color: e.target.value })} />
                <textarea placeholder="หมายเหตุเพิ่มเติม" value={shoeData.notes} onChange={(e) => setShoeData({ ...shoeData, notes: e.target.value })}></textarea>
            </div>

            <h3>📷 อัปโหลดรูปภาพรองเท้า</h3>
            <div className="image-upload">
                {["front", "back", "left", "right", "top", "bottom"].map((position, index) => (
                    <div key={index} className="image-upload-box">
                        <label>{`รูปภาพ ${position}`}</label>
                        <input 
                            type="file" 
                            accept="image/*" 
                            capture="environment" // ✅ เปิดกล้องบนมือถือ
                            onChange={(e) => handleImageUpload(e, position)}
                        />
                        {shoeData.images[position] && 
                            <img src={URL.createObjectURL(shoeData.images[position])} alt={`shoe-${position}`} width="50" />}
                    </div>
                ))}
            </div>

            <button onClick={handleAddShoe}>➕ เพิ่มรองเท้า</button>

            

            {showModal && (
    <div className="modal-overlay">
        <div className="modal-content">
            <h2>📷 อัปโหลดรูป After</h2>

            {/* ✅ ทำให้รูปภาพเลื่อนซ้าย-ขวาได้ */}
            <div className="image-upload-container">
                {["front", "back", "left", "right", "top", "bottom"].map((pos, index) => (
                    <div key={index} className="image-upload-box">
                        <label>{`📷 ด้าน ${pos.toUpperCase()}`}</label>
                        <input 
                            type="file" 
                            accept="image/*" 
                            onChange={(e) => handleAfterImageUpload(pos, e.target.files[0])} 
                        />
                        {afterImages[pos] && (
                            <img src={URL.createObjectURL(afterImages[pos])} alt={`after-${pos}`} width="100" />
                        )}
                    </div>
                ))}
            </div>

            {/* ✅ ปุ่มอัปโหลดอยู่ด้านล่าง ไม่โดนดันออกไป */}
            <div className="modal-actions">
                <button onClick={closeModal}>❌ ปิด</button>
                <button onClick={handleUploadAfterImages}>✅ อัปโหลด</button>
                
            </div>
        </div>
    </div>
)}


        </div>
    );
};

export default QueueDetail;
