import React, { useState, useEffect } from "react";
import "../assets/css/bootstrap.min.css";

const PaymentModal = ({ show, onClose, onConfirm, serviceSummary, totalPrice }) => {
    const [discount, setDiscount] = useState(0);
    const [paymentMethod, setPaymentMethod] = useState("เงินสด");

    useEffect(() => {
        setDiscount(0); // ✅ รีเซ็ตค่าเมื่อเปิด Modal
        setPaymentMethod("เงินสด");
    }, [show]);

    if (!show) return null;

    const totalAmount = totalPrice - discount < 0 ? 0 : totalPrice - discount; // ✅ ตรวจสอบไม่ให้เป็นค่าลบ

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h2>💰 สรุปการชำระเงิน</h2>
                <table className="table table-bordered table-striped">
                    <thead>
                        <tr>
                            <th>บริการ</th>
                            <th>จำนวน</th>
                            <th>ราคารวม (บาท)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {serviceSummary.map((service, index) => (
                            <tr key={index}>
                                <td>{service.service_name}</td>
                                <td>{service.total_pairs}</td>
                                <td>{service.total_price.toFixed(2)}</td>
                            </tr>
                        ))}
                        <tr>
                            <td colSpan="2"><strong>ยอดรวม</strong></td>
                            <td><strong>{totalPrice.toFixed(2)}</strong></td>
                        </tr>
                    </tbody>
                </table>

                {/* ✅ ช่องกรอกส่วนลด */}
                <label>🎯 ส่วนลด (บาท):</label>
                <input
                    type="number"
                    className="form-control"
                    value={discount}
                    onChange={(e) => setDiscount(parseFloat(e.target.value) || 0)}
                />

                {/* ✅ เลือกรูปแบบการชำระเงิน */}
                <label className="mt-3">💳 วิธีการชำระเงิน:</label>
                <select className="form-control" value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                    <option value="เงินสด">เงินสด</option>
                    <option value="เงินโอน">เงินโอน</option>
                </select>

                {/* ✅ แสดงยอดสุทธิ */}
                <h3 className="mt-3">💰 ยอดสุทธิ: {totalAmount.toFixed(2)} บาท</h3>

                {/* ✅ ปุ่มยืนยันชำระเงิน */}
                <div className="modal-actions">
                    <button className="btn btn-danger" onClick={onClose}>❌ ปิด</button>
                    <button className="btn btn-success" onClick={() => onConfirm(discount, totalAmount, paymentMethod)}>
                        ✅ ยืนยันชำระเงิน
                    </button>
                </div>
            </div>
        </div>
    );
};

export default PaymentModal;
