import React from "react";
import { Navigate, Outlet } from "react-router-dom";

const ProtectedRoute = () => {
    const token = localStorage.getItem("token");
    const userRole = localStorage.getItem("role"); // ‚úÖ ‡∏î‡∏∂‡∏á Role ‡∏à‡∏≤‡∏Å localStorage

    if (!token) {
        return <Navigate to="/login" />; // üîê ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ Token ‡πÉ‡∏´‡πâ Redirect ‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Login
    }

    // ‚úÖ ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå‡∏Å‡∏≤‡∏£‡πÄ‡∏Ç‡πâ‡∏≤‡∏ñ‡∏∂‡∏á‡∏Ç‡∏≠‡∏á‡πÅ‡∏ï‡πà‡∏•‡∏∞ Role
    const rolePermissions = {
        admin: ["dashboard", "appointments", "queue", "queue-entry", "queue/:id", "queue/:queue_id/detail", "branch", "users", "services", "payments", "employee"],
        manager: ["queue", "queue-entry", "services", "appointments", "payments", "employee"],
        staff: ["queue", "queue-entry", "appointments", "payments"],
    };

    // ‚úÖ ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤ Role ‡∏õ‡∏±‡∏à‡∏à‡∏∏‡∏ö‡∏±‡∏ô‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÄ‡∏Ç‡πâ‡∏≤‡∏ñ‡∏∂‡∏á‡πÄ‡∏™‡πâ‡∏ô‡∏ó‡∏≤‡∏á‡∏ó‡∏µ‡πà‡∏£‡πâ‡∏≠‡∏á‡∏Ç‡∏≠‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    const currentPath = window.location.pathname.replace("/", "");
    const allowedRoutes = rolePermissions[userRole] || [];

    if (!allowedRoutes.includes(currentPath) && userRole !== "admin") {
