import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQueue, updateQueueStatus, deleteQueue } from '../services/api';
import './QueueAdmin.css';
import { generateInvoice } from '../services/api';


const QueueAdmin = () => {
    const navigate = useNavigate();
    const [queue, setQueue] = useState([]);
    const [statusFilter, setStatusFilter] = useState(['‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤', '‡∏≠‡∏¢‡∏π‡πà‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏ó‡∏≥‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏∞‡∏≠‡∏≤‡∏î','‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏™‡πà‡∏á','‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏à‡∏±‡∏î‡∏™‡πà‡∏á']); //set default ‡πÉ‡∏´‡πâ status
    const [filteredQueue, setFilteredQueue] = useState([]);
    const [expandedQueue, setExpandedQueue] = useState(null);

    const [branches, setBranches] = useState([]);
    const [selectedBranch, setSelectedBranch] = useState(null);

    const Backend_URL = 'http://localhost:5000'; // ‡∏î‡∏∂‡∏á URL Backend

    // ‚úÖ ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏Ñ‡πà‡∏≤‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡∏Ç‡∏≠‡∏á‡∏ä‡πà‡∏ß‡∏á‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡πá‡∏ô‡πÄ‡∏î‡∏∑‡∏≠‡∏ô‡∏õ‡∏±‡∏à‡∏à‡∏∏‡∏ö‡∏±‡∏ô
    const today = new Date();
   // const firstDayOfMonth = new Date(today.getFullYear(), today.getMonth(), 1).toISOString().split('T')[0];
    const lastDayOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0).toISOString().split('T')[0];
    const firstDayOfYear = new Date(today.getFullYear(), 0, 1).toISOString().split('T')[0]; // 01/01/YYYY
   // const lastDayOfYear = new Date(today.getFullYear(), 11, 31).toISOString().split('T')[0]; // 31/12/YYYY

    const [startDate, setStartDate] = useState(firstDayOfYear);
    const [endDate, setEndDate] = useState(lastDayOfMonth);

    const [invoiceUrl, setInvoiceUrl] = useState(null);
    const [showInvoiceModal, setShowInvoiceModal] = useState(false);

    const [isSuperAdmin, setIsSuperAdmin] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login'); // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ Token ‡πÉ‡∏´‡πâ Redirect ‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Login
            return;
        }
    
        try {
            const user = jwtDecode(token);
            setIsSuperAdmin(user.isSuperAdmin);
            setSelectedBranch(prev => user.isSuperAdmin ? prev : user.branch_id); 
    
            if (user.isSuperAdmin) {
                fetchBranches();
            } else {
                fetchQueue(user.branch_id);
            }
        } catch (error) {
            console.error("üî¥ Error decoding token:", error);
            navigate("/login");
        }
    }, []);
    
    useEffect(() => {
        if (selectedBranch !== null) {
            fetchQueue(selectedBranch);
        }
    }, [selectedBranch]);
    
    useEffect(() => {
        if (queue.length > 0) {
            filterQueueData();
        }
    }, [queue, statusFilter, startDate, endDate]);

    const fetchBranches = async () => {
        try {
            const res = await getBranches();
            setBranches(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching branches:", error);
        }
    };

    const fetchQueue = async (branchId) => {
        try {
            console.log("üìå Debug: Fetching queue for branchId =", branchId);
            const res = await getQueue(branchId);
            setQueue(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching queue:", error);
        }
    };

    const handleBranchChange = (e) => {
        const branchId = e.target.value;
        setSelectedBranch(branchId);
    };

    


    const filterQueueData = () => {
        const filtered = queue.filter(item => 
            statusFilter.includes(item.status) &&
            new Date(item.received_date) >= new Date(startDate) &&
            new Date(item.received_date) <= new Date(endDate)
        ).sort((b,a) => new Date(b.received_date) - new Date(a.received_date));

        setFilteredQueue(filtered);
    };

    const handleStatusUpdate = async (id, currentStatus, totalPrice) => {
        if (!id || typeof id !== "number") {
            alert("‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î: ‡πÑ‡∏°‡πà‡∏û‡∏ö Queue ID ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏•‡∏≠‡∏á‡πÉ‡∏´‡∏°‡πà");
            return;
        }
    
        const statusMap = {
            '‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤': '‡∏≠‡∏¢‡∏π‡πà‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏ó‡∏≥‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏∞‡∏≠‡∏≤‡∏î',
            '‡∏≠‡∏¢‡∏π‡πà‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏ó‡∏≥‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏∞‡∏≠‡∏≤‡∏î': '‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏™‡πà‡∏á',
            '‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏™‡πà‡∏á': '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏à‡∏±‡∏î‡∏™‡πà‡∏á',
            '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏à‡∏±‡∏î‡∏™‡πà‡∏á': '‡∏à‡∏±‡∏î‡∏™‡πà‡∏á‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à'
        };
    
        const newStatus = statusMap[currentStatus];
        if (!newStatus) return;

        if (!window.confirm(`‡∏Ñ‡∏∏‡∏ì‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡πÄ‡∏õ‡πá‡∏ô "${newStatus}" ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà?`)) return;
    
        const parsedTotalPrice = parseFloat(totalPrice) || 0;
        if (parsedTotalPrice === 0) {
            alert("‚ùå ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏Å‡πà‡∏≠‡∏ô‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞!");
            return;
        }            
        
        try {
            

            await updateQueueStatus(id, newStatus, totalPrice);
            if (newStatus === "‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏™‡πà‡∏á") {
                alert("!!‡∏≠‡∏¢‡πà‡∏≤‡∏•‡∏∑‡∏° ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ After ‡∏ô‡∏∞‡∏Ñ‡∏∞")
               // setShowInvoiceModal(true); // ‚úÖ ‡πÄ‡∏õ‡∏¥‡∏î‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠‡πÅ‡∏à‡πâ‡∏á‡∏£‡∏≤‡∏Ñ‡∏≤
            }
            fetchQueue();
        } catch (error) {
            console.error("üî¥ Error updating queue status:", error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏Ñ‡∏¥‡∏ß‡∏ô‡∏µ‡πâ?")) {
            try {
                await deleteQueue(id);
                fetchQueue();
            } catch (error) {
                console.error("üî¥ Error deleting queue:", error);
            }
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '-';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    const handleStatusFilterChange = (status) => {
        setStatusFilter(prevFilters =>
            prevFilters.includes(status)
                ? prevFilters.filter(item => item !== status)
                : [...prevFilters, status]
        );
    };

    const handleAddCost = () => {
        setAdditionalCosts([...additionalCosts, { name: "", amount: 0 }]);
    };

    const handleCostChange = (index, key, value) => {
        const updatedCosts = [...additionalCosts];
        updatedCosts[index][key] = key === "amount" ? parseFloat(value) : value;
        setAdditionalCosts(updatedCosts);
    };

    // ‚úÖ ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÅ‡∏à‡πâ‡∏á‡∏£‡∏≤‡∏Ñ‡∏≤
    const handleGenerateInvoice = async (queue_id) => {
        try {
            const res = await generateInvoice(queue_id);
            setInvoiceUrl(res.data.image_url); // ‚úÖ ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï URL ‡∏Ç‡∏≠‡∏á‡πÉ‡∏ö‡πÅ‡∏à‡πâ‡∏á‡∏£‡∏≤‡∏Ñ‡∏≤
            console.log("URL",res.data.image_url);
            setShowInvoiceModal(true); // ‚úÖ ‡πÄ‡∏õ‡∏¥‡∏î Modal
        } catch (error) {
            console.error("üî¥ Error generating invoice:", error);
            alert("‚ùå ‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÉ‡∏ö‡πÅ‡∏à‡πâ‡∏á‡∏£‡∏≤‡∏Ñ‡∏≤‡πÑ‡∏î‡πâ");
        }
    };
    
    //‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏™‡∏µ‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞
    const getStatusColor = (status) => {
    switch (status) {
        case '‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤':
            return 'bg-danger text-white';  // üü• ‡πÅ‡∏î‡∏á
        case '‡∏≠‡∏¢‡∏π‡πà‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏ó‡∏≥‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏∞‡∏≠‡∏≤‡∏î':
            return 'bg-warning text-dark';  // üüß ‡∏™‡πâ‡∏°
        case '‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏™‡πà‡∏á':
            return 'bg-warning text-white';  // üü® ‡πÄ‡∏´‡∏•‡∏∑‡∏≠‡∏á
        case '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏à‡∏±‡∏î‡∏™‡πà‡∏á':
            return 'bg-primary text-dark';  // üå∏ ‡∏ä‡∏°‡∏û‡∏π (‡πÉ‡∏ä‡πâ custom CSS)
        case '‡∏à‡∏±‡∏î‡∏™‡πà‡∏á‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à':
            return 'bg-success text-white';  // ‚úÖ ‡πÄ‡∏Ç‡∏µ‡∏¢‡∏ß
        case '‡∏ä‡∏≥‡∏£‡∏∞‡πÄ‡∏á‡∏¥‡∏ô‡πÅ‡∏•‡πâ‡∏ß':
            return 'bg-success text-white';  // ‚úÖ ‡πÄ‡∏Ç‡∏µ‡∏¢‡∏ß
        default:
            return 'bg-light text-dark';  // ‚ö™ ‡∏Ñ‡πà‡∏≤‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô (‡πÄ‡∏ó‡∏≤‡∏≠‡πà‡∏≠‡∏ô)
    }
};

    

    return (
        <div className="queue-container">
            <h2>üìã ‡∏£‡∏∞‡∏ö‡∏ö‡∏Ñ‡∏¥‡∏ß‡∏á‡∏≤‡∏ô</h2>
            <div className="d-flex justify-content-between align-items-center mb-3">
                <button onClick={() => navigate('/dashboard')}>‚¨ÖÔ∏è ‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Dashboard</button>
                <button onClick={() => navigate('/queue-entry')}>üÜï ‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤‡πÉ‡∏´‡∏°‡πà</button>
            </div>
              {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á Dropdown ‡πÉ‡∏´‡πâ SuperAdmin ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏≤‡∏Ç‡∏≤ */}
              {isSuperAdmin && (
                <div className="mb-3">
                    <label className="form-label">‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏≤‡∏Ç‡∏≤</label>
                    <select className="form-control" value={selectedBranch || ''} onChange={handleBranchChange}>
                        <option value="">-- ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏≤‡∏Ç‡∏≤ --</option>
                        {branches.map(branch => (
                            <option key={branch.id} value={branch.id}>{branch.name}</option>
                        ))}
                    </select>
                </div>
            )}
           

            {/* ‚úÖ ‡∏ü‡∏¥‡∏•‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏ä‡πà‡∏ß‡∏á‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà */}
            <div className="date-filters d-flex align-items-center gap-2 mb-3">
                <label>üìÖ ‡∏à‡∏≤‡∏Å:</label>
                <input type="date" className="form-control" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
                <label>üìÖ ‡∏ñ‡∏∂‡∏á:</label>
                <input type="date" className="form-control" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
            </div>

            {/* ‚úÖ ‡∏ü‡∏¥‡∏•‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞ */}
            <div className="filters d-flex flex-wrap gap-2 justify-content-start">
                ‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞ :
                {['‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤', '‡∏≠‡∏¢‡∏π‡πà‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏ó‡∏≥‡∏Ñ‡∏ß‡∏≤‡∏°‡∏™‡∏∞‡∏≠‡∏≤‡∏î', '‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏™‡πà‡∏á', '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏à‡∏±‡∏î‡∏™‡πà‡∏á', '‡∏à‡∏±‡∏î‡∏™‡πà‡∏á‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à'].map(status => (
                    <div key={status} className="form-check d-flex align-items-center gap-1">
                        <input
                            type="checkbox"
                            className="form-check-input"
                            checked={statusFilter.includes(status)}
                            onChange={() => handleStatusFilterChange(status)}
                        />
                        <label className="form-check-label" style={{ color: getStatusColor(status), fontWeight: 'bold' }}>
                            {status}
                        </label>
                    </div>
                ))}
            </div>


            <table className="table table-striped table-bordered table-hover">
                <thead className="table-dark">
                    <tr>
                        <th>‡∏ä‡∏∑‡πà‡∏≠</th>
                        <th>‡∏™‡∏ñ‡∏≤‡∏ô‡∏ó‡∏µ‡πà</th>
                        <th>‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ñ‡∏π‡πà</th>                        
                        <th>‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡πÄ‡∏á‡∏¥‡∏ô</th>
                        <th>‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤</th>
