import React from "react";
const reportNames = {
        income: "📈 รายรับ",
        payout: "📉 รายจ่าย",
        profitloss: "💰 กำไร-ขาดทุน",
        topservices: "🔥 บริการยอดนิยม",
        customer: "🛍️ รายงานลูกค้า",
    };

const ReportTable = ({ reportType, reports }) => {
    const renderTableHeader = () => {
        switch (reportType) {
            case "income":
                return (
                    <tr>
                        <th>วันที่ชำระเงิน</th>
                        <th>ชื่อลูกค้า</th>
                        <th>ยอดรวม</th>
                        <th>ส่วนลด</th>
                        <th>ชำระสุทธิ</th>
                    </tr>
                );
            case "payout":
                return (
                    <tr>
                        <th>วันที่จ่าย</th>
                        <th>ประเภทการจ่าย</th>
                        <th>รายละเอียด</th>
                        <th>จำนวนเงิน</th>
                        <th>หมายเหตุ</th>
                    </tr>
                );
            case "profitloss":
                return (
                    <tr>
                        <th>วันที่</th>
                        <th>รายรับ (Income)</th>
                        <th>รายจ่าย (Expense)</th>
                        <th>กำไร / ขาดทุน</th>
                    </tr>
                );
            case "topservices":
                return (
                    <tr>
                        <th>อันดับ</th>
                        <th>ชื่อบริการ</th>
                        <th>จำนวนครั้งที่ใช้</th>
                        <th>รายได้รวม</th>
                    </tr>
                );
            case "customer":
                return (
                    <tr>
                        <th>ชื่อ</th>
                        <th>เบอร์โทร</th>
                        <th>จำนวนครั้งที่ใช้บริการ</th>
                        <th>ยอดรวมที่ใช้</th>
                    </tr>
                );
            default:
                return (
                    <tr>
                        <th>วันที่</th>
                        <th>รายละเอียด</th>
                        <th>ประเภท</th>
                        <th>จำนวนเงิน</th>
                    </tr>
                );
        }
    };

    const renderTableRows = () => {
        if (reports.length === 0) {
            return (
                <tr>
                    <td colSpan="5" className="text-center text-muted">ไม่มีข้อมูล</td>
                </tr>
            );
        }

        switch (reportType) {
            case "income":
                const totalIncome = reports.reduce((sum, report) => sum + parseFloat(report.total_amount || 0), 0);
                const totalDiscount = reports.reduce((sum, report) => sum + parseFloat(report.discount || 0), 0);
                const totalSum = totalIncome + totalDiscount;
            
                return (
                    <>
                        {reports.map((report, index) => {
                            const totalAmount = parseFloat(report.total_amount) || 0;
                            const discount = parseFloat(report.discount) || 0;
                            const totalSumRow = totalAmount + discount;
            
                            return (
                                <tr key={index}>
                                    <td>{new Date(report.payment_date).toLocaleDateString()}</td>
                                    <td>{report.customer_name}</td>
                                    <td>{totalSumRow.toFixed(2)} ฿</td>
                                    <td>{discount.toFixed(2)} ฿</td>
                                    <td>{totalAmount.toFixed(2)} ฿</td>
                                </tr>
                            );
                        })}
            
                        {/* ✅ แถวรวมยอดท้ายตาราง */}
                        {reports.length > 0 && (
                            <tr className="table-success fw-bold">
                                <td colSpan="2" className="text-end">รวมทั้งหมด:</td>
                                <td>{totalSum.toFixed(2)} ฿</td>
                                <td>{totalDiscount.toFixed(2)} ฿</td>
                                <td>{totalIncome.toFixed(2)} ฿</td>
                            </tr>
                        )}
                    </>
                );
            
            case "payout":
                return reports.map((report, index) => (
                    <tr key={index}>
                        <td>{new Date(report.payout_date).toLocaleDateString()}</td>
                        <td>{report.payout_type}</td>
                        <td>{report.description}</td>
                        <td>{report.amount.toFixed(2)} ฿</td>
                        <td>{report.notes}</td>
                    </tr>
                ));
            case "profitloss":
                return reports.map((report, index) => (
                    <tr key={index}>
                        <td>{new Date(report.date).toLocaleDateString()}</td>
                        <td>{report.income.toFixed(2)} ฿</td>
                        <td>{report.expense.toFixed(2)} ฿</td>
                        <td className={report.profit_loss >= 0 ? "text-success" : "text-danger"}>
                            {report.profit_loss.toFixed(2)} ฿
                        </td>
                    </tr>
                ));
            case "topservices":
                return reports.map((report, index) => (
                    <tr key={index}>
                        <td>{index + 1}</td>
                        <td>{report.service_name}</td>
                        <td>{report.usage_count} ครั้ง</td>
                        <td>{report.total_revenue.toFixed(2)} ฿</td>
                    </tr>
                ));
            case "customer":
                return reports.map((report, index) => (
                    <tr key={index}>
                        <td>{report.customer_name}</td>
                        <td>{report.phone}</td>
                        <td>{report.usage_count} ครั้ง</td>
                        <td>{report.total_spent.toFixed(2)} ฿</td>
                    </tr>
                ));
            default:
                return reports.map((report, index) => (
                    <tr key={index}>
                        <td>{new Date(report.date).toLocaleDateString()}</td>
                        <td>{report.description}</td>
                        <td>{report.type}</td>
                        <td>{report.amount.toFixed(2)} ฿</td>
                    </tr>
                ));
        }
    };

    return (
        <div className="card p-3 shadow">
            <h5>📋 รายงาน {reportNames[selectedReportType] || ""}</h5>
            <table className="table table-hover">
                <thead className="table-dark">
                    {renderTableHeader()}
                </thead>
                <tbody>
                    {renderTableRows()}
                </tbody>
            </table>
        </div>
    );
};

export default ReportTable;
