import React from "react";
const reportNames = {
        income: "üìà ‡∏£‡∏≤‡∏¢‡∏£‡∏±‡∏ö",
        payout: "üìâ ‡∏£‡∏≤‡∏¢‡∏à‡πà‡∏≤‡∏¢",
        profitloss: "üí∞ ‡∏Å‡∏≥‡πÑ‡∏£-‡∏Ç‡∏≤‡∏î‡∏ó‡∏∏‡∏ô",
        topservices: "üî• ‡∏ö‡∏£‡∏¥‡∏Å‡∏≤‡∏£‡∏¢‡∏≠‡∏î‡∏ô‡∏¥‡∏¢‡∏°",
        customer: "üõçÔ∏è ‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤",
    };

const ReportTable = ({ reportType, reports }) => {
    const renderTableHeader = () => {
        switch (reportType) {
            case "income":
                return (
                    <tr>
                        <th>‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏ä‡∏≥‡∏£‡∏∞‡πÄ‡∏á‡∏¥‡∏ô</th>
                        <th>‡∏ä‡∏∑‡πà‡∏≠‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤</th>
                        <th>‡∏¢‡∏≠‡∏î‡∏£‡∏ß‡∏°</th>
                        <th>‡∏™‡πà‡∏ß‡∏ô‡∏•‡∏î</th>
                        <th>‡∏ä‡∏≥‡∏£‡∏∞‡∏™‡∏∏‡∏ó‡∏ò‡∏¥</th>
                    </tr>
                );
            case "payout":
                return (
