import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getQueueDetail, getServices, updateQueue } from '../services/api';
import './QueueDetail.css';

const QueueDetail = () => {
    const navigate = useNavigate();
    const { queue_id } = useParams();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [selectedServices, setSelectedServices] = useState([]); // ✅ รายการบริการที่เลือก
    const [selectedService, setSelectedService] = useState('');
    const [pairCount, setPairCount] = useState(1);

    useEffect(() => {
        fetchQueueDetail();
        fetchServices();
    }, []);

    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(queue_id);
            setQueue(res.data);
            setSelectedServices(res.data.services || []);
        } catch (error) {
            console.error("🔴 Error fetching queue detail:", error);
        }
    };

    const fetchServices = async () => {
        try {
            const res = await getServices();
            setServices(res.data);
        } catch (error) {
            console.error("🔴 Error fetching services:", error);
        }
    };

    //เพิ่มข้อมูลรองเท้า
    const handleAddShoe = async () => {
        if (!selectedService || !shoeData.brand || !shoeData.model || !shoeData.color) {
            alert("กรุณากรอกข้อมูลให้ครบถ้วน");
            return;
        }
    
        try {
            const newShoe = {
                service_id: selectedService,
                price_per_pair: services.find(s => s.id === parseInt(selectedService)).base_price,
                brand: shoeData.brand,
                model: shoeData.model,
                color: shoeData.color,
                notes: shoeData.notes,
                images: shoeData.images
            };
    
            const res = await addQueueItem(queue_id, newShoe);
            alert("✅ เพิ่มรองเท้าเรียบร้อย!");
            fetchQueueDetail();
        } catch (error) {
            console.error("🔴 Error adding shoe:", error);
        }
    };

    //ลบข้อมูลรองเท้า
    const handleRemoveShoe = async (queue_item_id) => {
        if (!window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบรองเท้านี้?")) return;
    
        try {
            await deleteQueueItem(queue_item_id);
            alert("✅ ลบรองเท้าเรียบร้อย!");
            fetchQueueDetail();
        } catch (error) {
            console.error("🔴 Error deleting shoe:", error);
        }
    };
    
    

    // ✅ ปรับให้ไม่เพิ่มบริการซ้ำ แต่เพิ่มจำนวนคู่แทน
    const handleAddService = () => {
        if (!selectedService || pairCount < 1) {
            alert("กรุณาเลือกบริการและระบุจำนวนคู่รองเท้าให้ถูกต้อง");
            return;
        }
    
        const service = services.find(s => s.id === parseInt(selectedService));
        if (!service) return;
    
        setSelectedServices(prev => {
            const existingService = prev.find(s => s.service_id === service.id);
            if (existingService) {
                return prev.map(s =>
                    s.service_id === service.id
                        ? { ...s, pair_count: s.pair_count + pairCount }
                        : s
                );
            } else {
                return [
                    ...prev,
                    { 
                        service_id: service.id, 
                        service_name: service.service_name, 
                        pair_count: pairCount, 
                        price_per_pair: parseFloat(service.base_price) || 0 // ✅ แปลงเป็น number
                    }
                ];
            }
        });
    
        // เคลียร์ค่า input
        setSelectedService('');
        setPairCount(1);
    };
    

    // ✅ ลบบริการออกจาก state เท่านั้น (ยังไม่บันทึกฐานข้อมูล)
    const handleRemoveService = (index) => {
        setSelectedServices(prev => prev.filter((_, i) => i !== index));
    };

    // ✅ เมื่อกด "บันทึกการเปลี่ยนแปลง" จะบันทึกลงฐานข้อมูล
    const handleSaveChanges = async () => {
        if (!window.confirm("คุณต้องการบันทึกการเปลี่ยนแปลงหรือไม่?")) return;
        try {
            await updateQueue(queue_id, { services: selectedServices });
            alert("✅ บันทึกข้อมูลเรียบร้อยแล้ว!");

            // ✅ โหลดข้อมูลใหม่ก่อนเปลี่ยนหน้า
            await fetchQueueDetail();

            // ✅ กลับไปหน้าระบบคิว
            navigate('/queue');
        } catch (error) {
            console.error("🔴 Error updating queue:", error);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    if (!queue) return <p>⏳ กำลังโหลดข้อมูล...</p>;

    // ✅ คำนวณยอดรวมแต่ละแถว
    const serviceTotals = selectedServices.map(service => ({
        ...service,
        total_price_per_service: service.pair_count * service.price_per_pair
    }));

    // ✅ คำนวณยอดรวมทั้งหมด
    const totalPairs = serviceTotals.reduce((sum, service) => sum + service.pair_count, 0);
    const grandTotalPrice = serviceTotals.reduce((sum, service) => sum + service.total_price_per_service, 0);

    return (
        <div className="queue-detail-container">
            <h1>🏷️ Queue Detail (#{queue_id})</h1>

            <div className="queue-info">
                <p>👤 ลูกค้า: {queue.customer_name}</p>
                <p>📞 เบอร์โทร: {queue.phone}</p>
                <p>📍 สถานที่: {queue.location}</p>
                <p>👟 จำนวนคู่: {queue.total_pairs}</p>
                <p>📅 รับเข้า: {formatDate(queue.received_date)}</p>
                <p>🚚 กำหนดส่งคืน: {formatDate(queue.delivery_date)}</p>
            </div>

            <h3>👟 รายการรองเท้า</h3>
            <table>
                <thead>
                    <tr>
                        <th>แบรนด์</th>
                        <th>รุ่น</th>
                        <th>สี</th>
                        <th>หมายเหตุ</th>
                        <th>รูปภาพ</th>
                        <th>ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {shoes.map((shoe, index) => (
                        <tr key={index}>
                            <td>{shoe.brand}</td>
                            <td>{shoe.model}</td>
                            <td>{shoe.color}</td>
                            <td>{shoe.notes}</td>
                            <td>
                                {shoe.images.map((img, i) => img && <img key={i} src={img} alt={`shoe-${index}-${i}`} width="50" />)}
                            </td>
                            <td><button onClick={() => setShoes(prev => prev.filter((_, i) => i !== index))}>❌</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>

 

            <h3>📋 เพิ่มรองเท้าเข้า Queue</h3>
            <div className="shoe-details">
                <input type="text" placeholder="แบรนด์รองเท้า" value={shoeData.brand} onChange={(e) => setShoeData({ ...shoeData, brand: e.target.value })} />
                <input type="text" placeholder="รุ่นรองเท้า" value={shoeData.model} onChange={(e) => setShoeData({ ...shoeData, model: e.target.value })} />
                <input type="text" placeholder="สี" value={shoeData.color} onChange={(e) => setShoeData({ ...shoeData, color: e.target.value })} />
                <textarea placeholder="หมายเหตุเพิ่มเติม" value={shoeData.notes} onChange={(e) => setShoeData({ ...shoeData, notes: e.target.value })}></textarea>
            </div>
            <h3>📷 อัปโหลดรูปภาพรองเท้า</h3>
            <div className="image-upload">
                {["ด้านหน้า", "ด้านหลัง", "ด้านซ้าย", "ด้านขวา", "พื้นรองเท้า"].map((label, index) => (
                    <div key={index} className="image-upload-box">
                        <label>{label}</label>
                        <input type="file" accept="image/*" onChange={(e) => handleUploadImage(index, e.target.files[0])} />
                        {shoeData.images[index] && <img src={shoeData.images[index]} alt={`shoe-${index}`} />}
                    </div>
                ))}
            </div>
            <button onClick={handleAddShoe}>➕ เพิ่มรองเท้า</button>
            <div className="queue-actions">
                <button onClick={() => navigate('/queue')}>🔄 กลับไปหน้าคิว</button>
                <button onClick={handleSaveChanges}>✅ บันทึกการเปลี่ยนแปลง</button>
            </div>

        </div>
        
        
    );
};

export default QueueDetail;
