import React from "react";
import { Navigate, Outlet } from "react-router-dom";
import { jwtDecode } from "jwt-decode";


const ProtectedRoute = () => {
    const token = localStorage.getItem("token");

    if (!token) {
        return <Navigate to="/login" />;
    }

    let userRole;
    let isSuperAdmin = false;
    try {
        const decoded = jwtDecode(token);
        userRole = decoded.role;
        isSuperAdmin = decoded.isSuperAdmin;
    } catch (error) {
        console.error("üî¥ Error decoding token:", error);
        return <Navigate to="/login" />;
    }

    // ‚úÖ SuperAdmin ‡πÄ‡∏Ç‡πâ‡∏≤‡∏ñ‡∏∂‡∏á‡∏ó‡∏∏‡∏Å‡∏≠‡∏¢‡πà‡∏≤‡∏á
    if (isSuperAdmin) {
        return <Outlet />;
    }

    const rolePermissions = {
        admin: ["dashboard", "appointments", "queue", "queue-entry", "branch", "users", "services", "payments", "employee"],
        manager: ["queue", "queue-entry", "services", "appointments", "payments", "employee"],
        staff: ["queue", "queue-entry", "appointments", "payments"],
    };

    const currentPath = window.location.pathname.replace("/", "");
    const allowedRoutes = rolePermissions[userRole] || [];

    if (!allowedRoutes.includes(currentPath) && userRole !== "admin") {
        return <Navigate to="/dashboard" />;
    }

    return <Outlet />;
};

export default ProtectedRoute;
