            const newShoe = {
                service_id: selectedService,
                price_per_pair: parseFloat(services.find(s => s.id === parseInt(selectedService))?.base_price || 0),
                brand: shoeData.brand,
                model: shoeData.model,
                color: shoeData.color,
                notes: shoeData.notes,
                images: shoeData.images
            };

            await addQueueItem(queue_id, newShoe);
            alert("‚úÖ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢!");
            setShoeData({ brand: '', model: '', color: '', notes: '', images: {} });
            fetchQueueDetail();
        } catch (error) {
            console.error("üî¥ Error adding shoe:", error);
        }
    };

    // ‚úÖ ‡∏•‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤
    const handleRemoveShoe = async (queue_item_id) => {
        if (!window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡∏ô‡∏µ‡πâ?")) return;

