import React, { useState, useEffect } from "react";
import { getReports } from "../services/api"; // API เรียกรายงาน
import { jwtDecode } from "jwt-decode";
import "bootstrap/dist/css/bootstrap.min.css";

const ReportManagement = () => {
    const [reports, setReports] = useState([]);
    const [branches, setBranches] = useState([]);
    const [selectedBranch, setSelectedBranch] = useState(null);
    const [selectedReportType, setSelectedReportType] = useState("income"); // Default: รายได้
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [isSuperAdmin, setIsSuperAdmin] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) return;

        try {
            const user = jwtDecode(token);
            setIsSuperAdmin(user.isSuperAdmin);
            setSelectedBranch(user.isSuperAdmin ? "" : user.branch_id);
        } catch (error) {
            console.error("🔴 Error decoding token:", error);
        }
    }, []);

    const fetchReports = async () => {
        try {
           
            const res = await getReports({ branch_id: selectedBranch, report_type: selectedReportType, start_date: startDate, end_date: endDate });
            console.log("data return:",res.data);
            setReports(res.data);
        } catch (error) {
            console.error("🔴 Error fetching reports:", error);
        }
    };

    const ReportTable = ({ reportType, reports }) => {
        const renderTableHeader = () => {
            switch (reportType) {
                case "income_report":
                    return (
                        <tr>
                            <th>วันที่ชำระเงิน</th>
                            <th>Queue ID</th>
                            <th>ยอดชำระ</th>
                            <th>ส่วนลด</th>
                            <th>วิธีชำระ</th>
                            <th>สถานะ</th>
                        </tr>
                    );
                case "payout_report":
                    return (
                        <tr>
                            <th>วันที่จ่าย</th>
                            <th>ประเภทการจ่าย</th>
                            <th>รายละเอียด</th>
                            <th>จำนวนเงิน</th>
                            <th>หมายเหตุ</th>
                        </tr>
                    );
                case "profit_loss_report":
                    return (
                        <tr>
                            <th>วันที่</th>
                            <th>รายรับ (Income)</th>
                            <th>รายจ่าย (Expense)</th>
                            <th>กำไร / ขาดทุน</th>
                        </tr>
                    );
                case "topservices_report":
                    return (
                        <tr>
                            <th>อันดับ</th>
                            <th>ชื่อบริการ</th>
                            <th>จำนวนครั้งที่ใช้</th>
                            <th>รายได้รวม</th>
                        </tr>
                    );
                case "customer_report":
                    return (
                        <tr>
                            <th>ชื่อ</th>
                            <th>เบอร์โทร</th>
                            <th>จำนวนครั้งที่ใช้บริการ</th>
                            <th>ยอดรวมที่ใช้</th>
                        </tr>
                    );
                default:
                    return (
                        <tr>
                            <th>วันที่</th>
                            <th>รายละเอียด</th>
                            <th>ประเภท</th>
                            <th>จำนวนเงิน</th>
                        </tr>
                    );
            }
        }
        };
    
        const renderTableRows = () => {
            if (reports.length === 0) {
                return (
                    <tr>
                        <td colSpan="5" className="text-center text-muted">ไม่มีข้อมูล</td>
                    </tr>
                );
            }
    
            switch (reportType) {
                case "income_report":
                    return reports.map((report, index) => (
                        <tr key={index}>
                            <td>{new Date(report.payment_date).toLocaleDateString()}</td>
                            <td>{report.queue_id}</td>
                            <td>{report.total_amount.toFixed(2)} ฿</td>
                            <td>{report.discount.toFixed(2)} ฿</td>
                            <td>{report.payment_method}</td>
                            <td>{report.payment_status}</td>
                        </tr>
                    ));
                case "payout_report":
                    return reports.map((report, index) => (
                        <tr key={index}>
                            <td>{new Date(report.payout_date).toLocaleDateString()}</td>
                            <td>{report.payout_type}</td>
                            <td>{report.description}</td>
                            <td>{report.amount.toFixed(2)} ฿</td>
                            <td>{report.notes}</td>
                        </tr>
                    ));
                case "profit_loss_report":
                    return reports.map((report, index) => (
                        <tr key={index}>
                            <td>{new Date(report.date).toLocaleDateString()}</td>
                            <td>{report.income.toFixed(2)} ฿</td>
                            <td>{report.expense.toFixed(2)} ฿</td>
                            <td className={report.profit_loss >= 0 ? "text-success" : "text-danger"}>
                                {report.profit_loss.toFixed(2)} ฿
                            </td>
                        </tr>
                    ));
                case "topservices_report":
                    return reports.map((report, index) => (
                        <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{report.service_name}</td>
                            <td>{report.usage_count} ครั้ง</td>
                            <td>{report.total_revenue.toFixed(2)} ฿</td>
                        </tr>
                    ));
                case "customer_report":
                    return reports.map((report, index) => (
                        <tr key={index}>
                            <td>{report.customer_name}</td>
                            <td>{report.phone}</td>
                            <td>{report.usage_count} ครั้ง</td>
                            <td>{report.total_spent.toFixed(2)} ฿</td>
                        </tr>
                    ));
                default:
                    return reports.map((report, index) => (
                        <tr key={index}>
                            <td>{new Date(report.date).toLocaleDateString()}</td>
                            <td>{report.description}</td>
                            <td>{report.type}</td>
                            <td>{report.amount.toFixed(2)} ฿</td>
                        </tr>
                    ));
            }
        };

    return (
        <div className="container mt-4">
            <h1>📊 รายงาน</h1>
            <div className="card p-3 mb-3 shadow">
                <div className="row">
                    {isSuperAdmin && (
                        <div className="col-md-3">
                            <label className="form-label">เลือกสาขา</label>
                            <select className="form-select" value={selectedBranch} onChange={(e) => setSelectedBranch(e.target.value)}>
                                <option value="">-- ทุกสาขา --</option>
                                {branches.map(branch => (
                                    <option key={branch.id} value={branch.id}>{branch.name}</option>
                                ))}
                            </select>
                        </div>
                    )}
                    <div className="col-md-3">
                        <label className="form-label">ประเภทรายงาน</label>
                        <select className="form-select" value={selectedReportType} onChange={(e) => setSelectedReportType(e.target.value)}>
                        <option value="income">📈 รายรับ</option>
                        <option value="payout">📉 รายจ่าย</option>
                        <option value="profitloss">💰 กำไร-ขาดทุน</option>
                        <option value="topservices">🔥 บริการยอดนิยม</option>
                        <option value="customer">🛍️ รายงานลูกค้า</option>
                        </select>
                    </div>
                    <div className="col-md-2">
                        <label className="form-label">จากวันที่</label>
                        <input type="date" className="form-control" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
                    </div>
                    <div className="col-md-2">
                        <label className="form-label">ถึงวันที่</label>
                        <input type="date" className="form-control" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
                    </div>
                    <div className="col-md-2 d-flex align-items-end">
                        <button className="btn btn-primary w-100" onClick={fetchReports}>🔍 ค้นหา</button>
                    </div>
                </div>
            </div>

            {/* ปุ่มดาวน์โหลด */}
            <div className="d-flex justify-content-end mt-3">
                <button className="btn btn-success me-2">📥 ดาวน์โหลด Excel</button>
                <button className="btn btn-danger">📄 ดาวน์โหลด PDF</button>
            </div>
            {/* ตารางรายงาน */}
            <div className="card p-3 shadow">
                <h5>📋 รายการรายงาน</h5>
                <table className="table table-hover">
                    <thead className="table-dark">
                         {renderTableHeader()}
                    </thead>
                    <tbody>
                        {reports.length > 0 ? (
                            renderTableRows()
                        ) : (
                            <tr>
                                <td colSpan="4" className="text-center text-muted">ไม่มีข้อมูล</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>

           
        </div>
    );
};

export default ReportManagement;
