import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { getQueueDetail, getServices, updateQueue } from "../services/api";
import "./QueueDetail.css";

const QueueDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [selectedServices, setSelectedServices] = useState([]);

    useEffect(() => {
        fetchQueueDetail();
        fetchServices();
    }, []);

    // ✅ ดึงข้อมูลคิว + บริการที่ใช้
    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(id);
            setQueue(res.data);
            setSelectedServices(res.data.services || []);
        } catch (error) {
            console.error("🔴 Error fetching queue detail:", error);
        }
    };

    // ✅ ดึงรายการบริการทั้งหมด (Dropdown)
    const fetchServices = async () => {
        try {
            const res = await getServices();
            setServices(res.data);
        } catch (error) {
            console.error("🔴 Error fetching services:", error);
        }
    };

    // ✅ เพิ่มบริการ
    const handleAddService = (service_id) => {
        const service = services.find(s => s.id === parseInt(service_id));
        if (!service) return;

        setSelectedServices(prev => [
            ...prev,
            { service_id: service.id, service_name: service.service_name, pair_count: 1, price_per_pair: service.base_price }
        ]);
    };

    // ✅ ปรับจำนวนคู่รองเท้า
    const handlePairCountChange = (index, value) => {
        const updatedServices = [...selectedServices];
        updatedServices[index].pair_count = value;
        setSelectedServices(updatedServices);
    };

    // ✅ ลบบริการออก
    const handleRemoveService = (index) => {
        setSelectedServices(prev => prev.filter((_, i) => i !== index));
    };

    // ✅ บันทึกข้อมูลทั้งหมดไปยังฐานข้อมูล
    const handleSaveChanges = async () => {
        try {
            await updateQueue(id, { services: selectedServices });
            alert("✅ บันทึกข้อมูลเรียบร้อยแล้ว!");
            fetchQueueDetail(); // รีเฟรชข้อมูลใหม่
        } catch (error) {
            console.error("🔴 Error updating queue:", error);
        }
    };

    if (!queue) return <p>🔄 กำลังโหลดข้อมูล...</p>;

    return (
        <div className="queue-detail-container">
            <h1>🏷️ Queue Detail (# {id})</h1>
            <button onClick={() => navigate('/queue')}>⬅️ กลับไปหน้าคิว</button>

            <div className="queue-info">
                <p>👤 ลูกค้า: {queue.customer_name}</p>
                <p>📞 เบอร์โทร: {queue.phone}</p>
                <p>📍 สถานที่: {queue.location}</p>
                <p>👟 จำนวนคู่: {queue.total_pairs}</p>
                <p>📅 รับเข้า: {queue.received_date}</p>
                <p>🚚 กำหนดส่งคืน: {queue.delivery_date}</p>
            </div>

            <h2>📋 รายการบริการ (Services)</h2>
            <table>
                <thead>
                    <tr>
                        <th>🏷️ บริการ</th>
                        <th>👟 คู่</th>
                        <th>💰 ราคา/คู่</th>
                        <th>🗑️ ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {selectedServices.map((service, index) => (
                        <tr key={index}>
                            <td>{service.service_name}</td>
                            <td>
                                <input
                                    type="number"
                                    min="1"
                                    value={service.pair_count}
                                    onChange={(e) => handlePairCountChange(index, Number(e.target.value))}
                                />
                            </td>
                            <td>{service.price_per_pair} บาท</td>
                            <td><button onClick={() => handleRemoveService(index)}>❌</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h3>➕ เพิ่มบริการ</h3>
            <select onChange={(e) => handleAddService(e.target.value)}>
                <option value="">-- เลือกบริการ --</option>
                {services.map(service => (
                    <option key={service.id} value={service.id}>{service.service_name} ({service.base_price} บาท)</option>
                ))}
            </select>

            <button onClick={handleSaveChanges}>✅ บันทึกการเปลี่ยนแปลง</button>
        </div>
    );
};

export default QueueDetail;
