import React, { useState } from "react";
import "../assets/css/bootstrap.min.css"; // ✅ ใช้ Bootstrap
import "./AddAppointmentModal.css"; // ✅ ไฟล์ CSS เพิ่มเติม

const AddAppointmentModal = ({ show, onClose, onAddAppointment }) => {
    const [customerName, setCustomerName] = useState('');
    const [phone, setPhone] = useState('');
    const [location, setLocation] = useState('');
    const [shoeCount, setShoeCount] = useState(1);
    const [appointmentDate, setAppointmentDate] = useState(() => {
        const tomorrow = new Date();
        tomorrow.setDate(tomorrow.getDate() + 1);
        return tomorrow.toISOString().split('T')[0];
    });
    const [appointmentTime, setAppointmentTime] = useState('');

    // ✅ ฟังก์ชันบันทึกนัดหมาย
    const handleSave = () => {
        if (!customerName || !phone || !appointmentDate || !appointmentTime) {
            alert("❌ กรุณากรอกข้อมูลให้ครบถ้วน");
            return;
        }

        if (!window.confirm("คุณต้องการเพิ่มนัดหมายนี้หรือไม่?")) {
            return;
        }

        onAddAppointment({ customerName, phone, location, shoeCount, appointmentDate, appointmentTime });
        onClose(); // ✅ ปิด Modal หลังจากบันทึก
    };

    if (!show) return null;

    return (
        <div className="modal-overlay">
            <div className="modal-content card p-4 shadow">
                <h2 className="text-center mb-3">➕ เพิ่มนัดหมาย</h2>

                <div className="mb-3">
                    <label className="form-label">ชื่อ</label>
                    <input type="text" className="form-control" placeholder="ชื่อ" 
                        value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
                </div>
                
                <div className="mb-3">
                    <label className="form-label">เบอร์โทร</label>
                    <input type="text" className="form-control" placeholder="เบอร์โทร"
                        value={phone} onChange={(e) => setPhone(e.target.value)} />
                </div>

                <div className="mb-3">
                    <label className="form-label">สถานที่</label>
                    <input type="text" className="form-control" placeholder="สถานที่"
                        value={location} onChange={(e) => setLocation(e.target.value)} />
                </div>

                <div className="mb-3">
                    <label className="form-label">จำนวนรองเท้า</label>
                    <input type="number" className="form-control" placeholder="จำนวนรองเท้า"
                        value={shoeCount} onChange={(e) => setShoeCount(e.target.value)} />
                </div>

                <div className="row">
                    <div className="col-md-6 mb-3">
                        <label className="form-label">วันที่</label>
                        <input type="date" className="form-control"
                            value={appointmentDate} onChange={(e) => setAppointmentDate(e.target.value)} />
                    </div>
                    <div className="col-md-6 mb-3">
                        <label className="form-label">เวลา</label>
                        <input type="time" className="form-control"
                            value={appointmentTime} onChange={(e) => setAppointmentTime(e.target.value)} />
                    </div>
                </div>

                <div className="d-flex justify-content-between mt-3">
                    <button className="btn btn-secondary w-45" onClick={onClose}>
                        ❌ ปิด
                    </button>
                    <button className="btn btn-success w-45" onClick={handleSave}>
                        ✅ บันทึก
                    </button>
                </div>
            </div>
        </div>
    );
};

export default AddAppointmentModal;
