import React, { useRef, useEffect, useState } from "react";
import html2canvas from "html2canvas";
import '../assets/css/bootstrap.min.css';
import './ShoeDetailModal.css';

const ShoeDetailModal = ({ show, onClose, shoe }) => {
    if (!show || !shoe) return null;

    const Backend_URL = 'http://localhost:5000'; // ดึง URL Backend
    const canvasRef = useRef(null); // Ref สำหรับ Canvas
    const [canvasReady, setCanvasReady] = useState(false); // เช็คว่ารูปโหลดเสร็จแล้วหรือยัง

    // ✅ วาดรูปลง Canvas ให้มีขนาดพอดี
    useEffect(() => {
        if (!shoe.image_before_front || !shoe.image_after_front) return;

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const beforeImage = new Image();
        const afterImage = new Image();

        beforeImage.src = `${Backend_URL}${shoe.image_before_front}`;
        afterImage.src = `${Backend_URL}${shoe.image_after_front}`;

        let imgWidth = 300; // กำหนดขนาดรูปภาพที่ต้องการ
        let imgHeight = 300; // ขนาดเท่ากันสำหรับ Before และ After
        let padding = 20; // ระยะห่างระหว่างรูป

        beforeImage.onload = () => {
            afterImage.onload = () => {
                // กำหนดขนาด Canvas ให้พอดีกับรูปทั้งสอง + Padding
                canvas.width = imgWidth * 2 + padding;
                canvas.height = imgHeight;

                ctx.fillStyle = "#fff"; // กำหนดพื้นหลังสีขาว
                ctx.fillRect(0, 0, canvas.width, canvas.height);

                // วาด Before (ซ้าย)
                ctx.drawImage(beforeImage, 0, 0, imgWidth, imgHeight);
                ctx.fillStyle = "black";
                ctx.font = "18px Arial";
                ctx.fillText("Before", 10, 25);

                // วาด After (ขวา)
                ctx.drawImage(afterImage, imgWidth + padding, 0, imgWidth, imgHeight);
                ctx.fillText("After", imgWidth + padding + 10, 25);

                setCanvasReady(true); // อัปเดตว่าโหลดเสร็จแล้ว
            };
        };
    }, [shoe]);

    // ✅ ฟังก์ชันดาวน์โหลดรูปจาก Canvas
    const handleDownloadImage = () => {
        if (!canvasReady) {
            alert("⏳ กรุณารอสักครู่... กำลังโหลดภาพ");
            return;
        }

        const canvas = canvasRef.current;
        const image = canvas.toDataURL("image/png");

        const link = document.createElement("a");
        link.href = image;
        link.download = `before-after-${shoe.brand || "shoe"}.png`;
        link.click();
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h2 className="modal-title">👟 รายละเอียดรองเท้า</h2>
                
                <div className="shoe-info">
                    <p><strong>แบรนด์:</strong> {shoe.brand}</p>
                    <p><strong>รุ่น:</strong> {shoe.model}</p>
                    <p><strong>สี:</strong> {shoe.color}</p>
                    <p><strong>หมายเหตุ:</strong> {shoe.notes || "-"}</p>
                </div>

                {/* ✅ แสดงรูป Before & After ใน Canvas */}
                <h3 className="section-title">📷 ภาพ Before & After (Front)</h3>
                <div className="canvas-container">
                    <canvas ref={canvasRef} className="canvas-frame"></canvas>
                </div>

                {/* ✅ ปุ่มดาวน์โหลดรูป Before-After */}
                <button className="btn-download" onClick={handleDownloadImage}>
                    📥 ดาวน์โหลดรูป Before-After
                </button>

                {/* ✅ แสดงรูป Before & After ทุกมุม */}
                <h3 className="section-title">📷 ภาพ Before & After (ทุกมุม)</h3>
                <table className="shoe-images-table">
                    <thead>
                        <tr>
                            <th>ช็อต</th>
                            <th>Before</th>
                            <th>After</th>
                        </tr>
                    </thead>
                    <tbody>
                        {["front", "back", "left", "right", "top", "bottom"].map((position) => (
                            <tr key={position}>
                                <td><strong>{position.toUpperCase()}</strong></td>
                                <td>
                                    {shoe[`image_before_${position}`] ? (
                                        <img src={`${Backend_URL}${shoe[`image_before_${position}`]}`} alt={`before-${position}`} className="shoe-image-table" />
                                    ) : "-"}
                                </td>
                                <td>
                                    {shoe[`image_after_${position}`] ? (
                                        <img src={`${Backend_URL}${shoe[`image_after_${position}`]}`} alt={`after-${position}`} className="shoe-image-table" />
                                    ) : "-"}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {/* ปุ่มปิด Modal */}
                <div className="modal-actions">
                    <button className="btn-close" onClick={onClose}>❌ ปิด</button>
                </div>
            </div>
        </div>
    );
};

export default ShoeDetailModal;
