import React, { useRef, useEffect, useState } from "react";
import html2canvas from "html2canvas";
import '../assets/css/bootstrap.min.css';
import './ShoeDetailModal.css';

const ShoeDetailModal = ({ show, onClose, shoe }) => {
    if (!show || !shoe) return null;

    const Backend_URL = 'http://localhost:5000'; // ‡∏î‡∏∂‡∏á URL Backend
    const canvasRef = useRef(null); // Ref ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Canvas
    const [canvasReady, setCanvasReady] = useState(false); // ‡πÄ‡∏ä‡πá‡∏Ñ‡∏ß‡πà‡∏≤‡∏£‡∏π‡∏õ‡πÇ‡∏´‡∏•‡∏î‡πÄ‡∏™‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß‡∏´‡∏£‡∏∑‡∏≠‡∏¢‡∏±‡∏á

    // ‚úÖ ‡∏ß‡∏≤‡∏î‡∏£‡∏π‡∏õ‡∏•‡∏á Canvas ‡πÉ‡∏´‡πâ‡∏°‡∏µ‡∏Ç‡∏ô‡∏≤‡∏î‡∏û‡∏≠‡∏î‡∏µ
    useEffect(() => {
        if (!shoe.image_before_front || !shoe.image_after_front) return;

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const beforeImage = new Image();
        const afterImage = new Image();

        beforeImage.src = `${Backend_URL}${shoe.image_before_front}`;
        afterImage.src = `${Backend_URL}${shoe.image_after_front}`;

        let imgWidth = 300; // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏Ç‡∏ô‡∏≤‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£
        let imgHeight = 300; // ‡∏Ç‡∏ô‡∏≤‡∏î‡πÄ‡∏ó‡πà‡∏≤‡∏Å‡∏±‡∏ô‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Before ‡πÅ‡∏•‡∏∞ After
        let padding = 20; // ‡∏£‡∏∞‡∏¢‡∏∞‡∏´‡πà‡∏≤‡∏á‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏£‡∏π‡∏õ

        beforeImage.onload = () => {
            afterImage.onload = () => {
                // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏Ç‡∏ô‡∏≤‡∏î Canvas ‡πÉ‡∏´‡πâ‡∏û‡∏≠‡∏î‡∏µ‡∏Å‡∏±‡∏ö‡∏£‡∏π‡∏õ‡∏ó‡∏±‡πâ‡∏á‡∏™‡∏≠‡∏á + Padding
                canvas.width = imgWidth * 2 + padding;
                canvas.height = imgHeight;

                ctx.fillStyle = "#fff"; // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏û‡∏∑‡πâ‡∏ô‡∏´‡∏•‡∏±‡∏á‡∏™‡∏µ‡∏Ç‡∏≤‡∏ß
                ctx.fillRect(0, 0, canvas.width, canvas.height);

                // ‡∏ß‡∏≤‡∏î Before (‡∏ã‡πâ‡∏≤‡∏¢)
                ctx.drawImage(beforeImage, 0, 0, imgWidth, imgHeight);
                ctx.fillStyle = "black";
                ctx.font = "18px Arial";
                ctx.fillText("Before", 10, 25);

                // ‡∏ß‡∏≤‡∏î After (‡∏Ç‡∏ß‡∏≤)
                ctx.drawImage(afterImage, imgWidth + padding, 0, imgWidth, imgHeight);
                ctx.fillText("After", imgWidth + padding + 10, 25);

                setCanvasReady(true); // ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏ß‡πà‡∏≤‡πÇ‡∏´‡∏•‡∏î‡πÄ‡∏™‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß
            };
        };
    }, [shoe]);

    // ‚úÖ ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏à‡∏≤‡∏Å Canvas
    const handleDownloadImage = () => {
        if (!canvasReady) {
            alert("‚è≥ ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏£‡∏≠‡∏™‡∏±‡∏Å‡∏Ñ‡∏£‡∏π‡πà... ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏†‡∏≤‡∏û");
            return;
        }

        const canvas = canvasRef.current;
        const image = canvas.toDataURL("image/png");

        const link = document.createElement("a");
        link.href = image;
        link.download = `before-after-${shoe.brand || "shoe"}.png`;
        link.click();
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h2 className="modal-title">üëü ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤</h2>
                
                <div className="shoe-info">
                    <p><strong>‡πÅ‡∏ö‡∏£‡∏ô‡∏î‡πå:</strong> {shoe.brand}</p>
                    <p><strong>‡∏£‡∏∏‡πà‡∏ô:</strong> {shoe.model}</p>
                    <p><strong>‡∏™‡∏µ:</strong> {shoe.color}</p>
                    <p><strong>‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏´‡∏ï‡∏∏:</strong> {shoe.notes || "-"}</p>
                </div>

                {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏π‡∏õ Before & After ‡πÉ‡∏ô Canvas */}
                <h3 className="section-title">üì∑ ‡∏†‡∏≤‡∏û Before & After (Front)</h3>
                <div className="canvas-container">
                    <canvas ref={canvasRef} className="canvas-frame"></canvas>
                </div>

                {/* ‚úÖ ‡∏õ‡∏∏‡πà‡∏°‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ Before-After */}
                <button className="btn-download" onClick={handleDownloadImage}>
                    üì• ‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ Before-After
                </button>

                {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏π‡∏õ Before & After ‡∏ó‡∏∏‡∏Å‡∏°‡∏∏‡∏° */}
                <h3 className="section-title">üì∑ ‡∏†‡∏≤‡∏û Before & After (‡∏ó‡∏∏‡∏Å‡∏°‡∏∏‡∏°)</h3>
                <table className="shoe-images-table">
                    <thead>
                        <tr>
                            <th>‡∏ä‡πá‡∏≠‡∏ï</th>
                            <th>Before</th>
                            <th>After</th>
                        </tr>
                    </thead>
                    <tbody>
                        {["front", "back", "left", "right", "top", "bottom"].map((position) => (
                            <tr key={position}>
                                <td><strong>{position.toUpperCase()}</strong></td>
                                <td>
                                    {shoe[`image_before_${position}`] ? (
                                        <img src={`${Backend_URL}${shoe[`image_before_${position}`]}`} alt={`before-${position}`} className="shoe-image-table" />
                                    ) : "-"}
                                </td>
                                <td>
                                    {shoe[`image_after_${position}`] ? (
                                        <img src={`${Backend_URL}${shoe[`image_after_${position}`]}`} alt={`after-${position}`} className="shoe-image-table" />
                                    ) : "-"}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {/* ‡∏õ‡∏∏‡πà‡∏°‡∏õ‡∏¥‡∏î Modal */}
                <div className="modal-actions">
                    <button className="btn-close" onClick={onClose}>‚ùå ‡∏õ‡∏¥‡∏î</button>
                </div>
            </div>
        </div>
    );
};

export default ShoeDetailModal;
