import React, { useState, useEffect } from "react";
import { processPayment, getQueueDetail } from "../services/api";
import "bootstrap/dist/css/bootstrap.min.css";

const PaymentModal = ({ queue, onClose }) => {
    const [QueueItems, setQueueItems] = useState([]);
    const [additionalFees, setAdditionalFees] = useState([]);
    const [paymentMethod, setPaymentMethod] = useState("");


    // ✅ โหลดข้อมูล Queue และรายการรองเท้า
    useEffect(() => {
        if (!queue?.id) return; // ป้องกัน queue.id เป็น undefined
    
        const fetchQueueData = async () => {           
            try {
                const response = await getQueueDetail(queue.id);
                
                // ✅ แก้ตรงนี้ ให้ดึง queue_items แทน
                setQueueItems(response.data.queue_items || []);
            } catch (error) {
                console.error("🔴 Error fetching queue details:", error);
            }
        };
    
        fetchQueueData();
    }, [queue?.id]);
    
    

    if (!queue) return <p>⏳ กำลังโหลดข้อมูล...</p>;

    // ✅ ฟังก์ชันเพิ่มค่าใช้จ่ายเพิ่มเติม
    const handleAddFee = () => {
        setAdditionalFees([...additionalFees, { name: "", amount: 0 }]);
    };

    // ✅ อัปเดตรายการค่าใช้จ่าย
    const handleFeeChange = (index, field, value) => {
        const updatedFees = [...additionalFees];
        updatedFees[index][field] = value;
        setAdditionalFees(updatedFees);
    };

    // ✅ คำนวณยอดรวมของค่าใช้จ่ายเพิ่มเติม
    const totalAdditionalFees = additionalFees.reduce((sum, fee) => sum + (Number(fee.amount) || 0), 0);

    // ✅ คำนวณยอดรวมของค่าบริการรองเท้าแต่ละคู่
    const totalShoePrice = QueueItems.reduce((sum, item) => sum + Number(item.price_per_pair), 0);

    // ✅ ยอดรวมทั้งหมด (ค่ารองเท้า + ค่าใช้จ่ายเพิ่มเติม)
    const totalAmount = totalShoePrice + totalAdditionalFees;

    // ✅ ฟังก์ชันยืนยันการชำระเงิน
    const handleConfirmPayment = async () => {
        if (!paymentMethod) {
            alert("❌ กรุณาเลือกวิธีชำระเงิน");
            return;
        }

        try {
            await processPayment(queue.id, totalAmount, additionalFees, paymentMethod);
            alert("✅ ชำระเงินเรียบร้อย!");
            onClose();
        } catch (error) {
            console.error("🔴 Error processing payment:", error);
        }
    };

    return (
        <div className="modal fade show d-block" tabIndex="-1">
            <div className="modal-dialog modal-lg">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">💳 ชำระเงินสำหรับ {queue.customer_name}</h5>
                        <button type="button" className="btn-close" onClick={onClose}></button>
                    </div>
                    <div className="modal-body">
                        <p><strong>จำนวนคู่:</strong> {queue.total_pairs} คู่</p>

                        {/* ✅ ตารางแสดงรายการรองเท้าและค่าบริการ */}
                        <h6>👟 รายการรองเท้า</h6>
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>แบรนด์</th>
                                    <th>รุ่น</th>
                                    <th>สี</th>
                                    <th>ค่าบริการ (บาท)</th>
                                </tr>
                            </thead>
                            <tbody>
                                {QueueItems.map((item, index) => (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>{item.brand}</td>
                                        <td>{item.model}</td>
                                        <td>{item.color}</td>
                                        <td>{Number(item.price_per_pair).toFixed(2)}</td>
                                    </tr>
                                ))}
                            </tbody>
                            <tfoot>
                                <tr>
                                    <td colSpan="4" className="text-end"><strong>รวมค่าบริการ:</strong></td>
                                    <td><strong>{totalShoePrice.toFixed(2)}</strong></td>
                                </tr>
                            </tfoot>
                        </table>

                        {/* ✅ ฟอร์มเพิ่มค่าใช้จ่ายเพิ่มเติม */}
                        <h6 className="mt-3">💸 ค่าใช้จ่ายเพิ่มเติม</h6>
                        {additionalFees.map((fee, index) => (
                            <div key={index} className="row g-2 align-items-center mb-2">
                                <div className="col">
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="รายละเอียดค่าใช้จ่าย"
                                        value={fee.name}
                                        onChange={(e) => handleFeeChange(index, "name", e.target.value)}
                                    />
                                </div>
                                <div className="col">
                                    <input
                                        type="number"
                                        className="form-control"
                                        placeholder="จำนวนเงิน"
                                        value={fee.amount}
                                        onChange={(e) => handleFeeChange(index, "amount", e.target.value)}
                                    />
                                </div>
                            </div>
                        ))}
                        <button className="btn btn-secondary btn-sm" onClick={handleAddFee}>➕ เพิ่มค่าใช้จ่าย</button>

                        {/* ✅ แสดงยอดรวมทั้งหมด */}
                        <h5 className="mt-3">💰 รวมทั้งสิ้น: {totalAmount.toFixed(2)} บาท</h5>

                        {/* ✅ เลือกวิธีชำระเงิน */}
                        <label className="form-label mt-2">เลือกวิธีชำระเงิน</label>
                        <select className="form-select" value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                            <option value="">-- กรุณาเลือก --</option>
                            <option value="เงินสด">💵 เงินสด</option>
                            <option value="QR Code">📱 QR Code</option>
                        </select>
                    </div>

                    {/* ✅ ปุ่มบันทึกการชำระเงิน */}
                    <div className="modal-footer">
                        <button className="btn btn-secondary" onClick={onClose}>❌ ปิด</button>
                        <button className="btn btn-success" onClick={handleConfirmPayment}>✅ ยืนยันชำระเงิน</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PaymentModal;
