import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAppointments, createAppointment, updateAppointmentStatus } from '../services/api';
import './AppointmentsAdmin.css';

const AppointmentsAdmin = () => {
  const navigate = useNavigate();
  const [appointments, setAppointments] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [phone, setPhone] = useState('');
  const [location, setLocation] = useState('');
  const [shoeCount, setShoeCount] = useState(1);
  const [appointmentDate, setAppointmentDate] = useState('');
  const [appointmentTime, setAppointmentTime] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateFilter, setDateFilter] = useState('');

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await getAppointments();
      setAppointments(res.data);
    } catch (error) {
      console.error("üî¥ Error fetching appointments:", error);
    }
  };

  const handleCreateAppointment = async () => {
    try {
      const newAppointment = {
        customer_name: customerName,
        phone,
        location,
        shoe_count: shoeCount,
        appointment_date: appointmentDate,
        appointment_time: appointmentTime,
      };
      await createAppointment(newAppointment);
      fetchAppointments();
      setCustomerName('');
      setPhone('');
      setLocation('');
      setShoeCount(1);
      setAppointmentDate('');
      setAppointmentTime('');
    } catch (error) {
      console.error("üî¥ Error creating appointment:", error);
    }
  };

  const handleStatusUpdate = async (id, newStatus) => {
    try {
      await updateAppointmentStatus(id, newStatus);
      fetchAppointments();
    } catch (error) {
      console.error("üî¥ Error updating status:", error);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏ô‡∏±‡∏î‡∏´‡∏°‡∏≤‡∏¢‡∏ô‡∏µ‡πâ?")) {
      try {
        await deleteAppointment(id);
        fetchAppointments();
      } catch (error) {
        console.error("üî¥ Error deleting appointment:", error);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
  };

  const filteredAppointments = appointments.filter(appt => {
    return (
      (!statusFilter || appt.status === statusFilter) &&
      (!dateFilter || appt.appointment_date === dateFilter)
    );
  });

  return (
    <div className="appointments-container">
      <h1>üìÖ ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏ô‡∏±‡∏î‡∏´‡∏°‡∏≤‡∏¢</h1>

      <div className="filters">
        <input type="date" value={dateFilter} onChange={(e) => setDateFilter(e.target.value)} />
        <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)}>
          <option value="">‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</option>
          <option value="‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£">‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£</option>
          <option value="‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡πÅ‡∏•‡πâ‡∏ß">‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡πÅ‡∏•‡πâ‡∏ß</option>
          <option value="‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à">‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à</option>
        </select>
      </div>

      <table>
        <thead>
          <tr>
            <th>‡∏ä‡∏∑‡πà‡∏≠</th>
            <th>‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£</th>
            <th>‡∏™‡∏ñ‡∏≤‡∏ô‡∏ó‡∏µ‡πà</th>
            <th>‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤</th>
            <th>‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà</th>
            <th>‡πÄ‡∏ß‡∏•‡∏≤</th>
            <th>‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞</th>
            <th>‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï</th>
            <th>‡∏•‡∏ö</th>
          </tr>
        </thead>
        <tbody>
          {filteredAppointments.map(appt => (
            <tr key={appt.id}>
              <td>{appt.customer_name}</td>
              <td>{appt.phone}</td>
              <td>{appt.location}</td>
              <td>{appt.shoe_count}</td>
              <td>{appt.appointment_date}</td>
              <td>{appt.appointment_time}</td>
              <td>{appt.status}</td>
              <td>
                {appt.status !== '‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à' && (
                  <button onClick={() => handleStatusUpdate(appt.id, appt.status === '‡∏£‡∏≠‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£' ? '‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡πÅ‡∏•‡πâ‡∏ß' : '‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à')}>
                    ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï
                  </button>
                )}
              </td>
              <td>
              {appt.status !== '‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à' && (
                  <button onClick={() => handleDelete(appt.id)}>‡∏•‡∏ö</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>‚ûï ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ô‡∏±‡∏î‡∏´‡∏°‡∏≤‡∏¢</h2>
      <input type="text" placeholder="‡∏ä‡∏∑‡πà‡∏≠" value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
      <input type="text" placeholder="‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <input type="text" placeholder="‡∏™‡∏ñ‡∏≤‡∏ô‡∏ó‡∏µ‡πà" value={location} onChange={(e) => setLocation(e.target.value)} />
      <input type="number" placeholder="‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤" value={shoeCount} onChange={(e) => setShoeCount(e.target.value)} />
      <input type="date" value={appointmentDate} onChange={(e) => setAppointmentDate(e.target.value)} />
      <input type="time" value={appointmentTime} onChange={(e) => setAppointmentTime(e.target.value)} />
      <button onClick={handleCreateAppointment}>‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏ô‡∏±‡∏î‡∏´‡∏°‡∏≤‡∏¢</button>
    </div>
  );
};

export default AppointmentsAdmin;
