import React, { useState, useEffect } from "react";

const AddEmployeeModal = ({ show, onClose, onSave, employeeData }) => {
    const [employee, setEmployee] = useState({
        name: "",
        email: "",
        phone: "",
        role: "",
        branch_id: "",
        password: ""  // ✅ เพิ่มช่องรหัสผ่าน
    });

    useEffect(() => {
        if (employeeData) {
            setEmployee({ ...employeeData, password: "" }); // ✅ รีเซ็ตรหัสผ่านเมื่อแก้ไข
        } else {
            setEmployee({ name: "", email: "", phone: "", role: "", branch_id: "", password: "" });
        }
    }, [employeeData]);

    const handleChange = (e) => {
        setEmployee({ ...employee, [e.target.name]: e.target.value });
    };

    const handleSubmit = () => {
        if (!employee.name || !employee.email || !employee.phone || !employee.role || !employee.branch_id || (!employeeData && !employee.password)) {
            alert("❌ กรุณากรอกข้อมูลให้ครบถ้วน");
            return;
        }
        onSave(employee);
    };

    if (!show) return null;

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>{employeeData ? "✏️ แก้ไขพนักงาน" : "➕ เพิ่มพนักงาน"}</h3>
                <label>ชื่อ:</label>
                <input type="text" name="name" value={employee.name} onChange={handleChange} required />
                
                <label>อีเมล:</label>
                <input type="email" name="email" value={employee.email} onChange={handleChange} required />
                
                <label>เบอร์ติดต่อ:</label>
                <input type="text" name="phone" value={employee.phone} onChange={handleChange} required />
                
                <label>ตำแหน่ง:</label>
                <select name="role" value={employee.role} onChange={handleChange} required>
                    <option value="">-- เลือกตำแหน่ง --</option>
                    <option value="admin">Admin</option>
                    <option value="staff">Staff</option>
                </select>

                <label>สาขา:</label>
                <input type="number" name="branch_id" value={employee.branch_id} onChange={handleChange} required />

                {/* ✅ แสดงช่องใส่รหัสผ่าน เฉพาะตอนเพิ่มพนักงาน */}
                {!employeeData && (
                    <>
                        <label>รหัสผ่าน:</label>
                        <input type="password" name="password" value={employee.password} onChange={handleChange} required />
                    </>
                )}

                <div className="modal-actions">
                    <button className="btn btn-success" onClick={handleSubmit}>💾 บันทึก</button>
                    <button className="btn btn-secondary" onClick={onClose}>❌ ยกเลิก</button>
                </div>
            </div>
        </div>
    );
};

export default AddEmployeeModal;
