import React from "react";
import { Navigate, Outlet, useLocation } from "react-router-dom";
import { jwtDecode } from "jwt-decode"; // ‚úÖ ‡πÉ‡∏ä‡πâ Named Import ‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á

const ProtectedRoute = () => {
    const token = localStorage.getItem("token");
    const location = useLocation();

    if (!token) {
        console.warn("üî¥ No Token Found. Redirecting to /login");
        return <Navigate to="/login" />;
    }

    let userRole;
    let isSuperAdmin = false;
    try {
        const decoded = jwtDecode(token); // ‚úÖ ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö Token
        console.log("üîç Decoded Token:", decoded); // ‚úÖ Debug ‡∏Ñ‡πà‡∏≤ Token
        userRole = decoded.role;
        isSuperAdmin = decoded.isSuperAdmin || false;
        console.log("userRole:",decoded.role);
    } catch (error) {
        console.error("üî¥ Error decoding token:", error);
        return <Navigate to="/login" />;
    }

    // ‚úÖ SuperAdmin ‡πÄ‡∏Ç‡πâ‡∏≤‡∏ñ‡∏∂‡∏á‡∏ó‡∏∏‡∏Å‡∏´‡∏ô‡πâ‡∏≤
    if (isSuperAdmin) {
        return <Outlet />;
    }

    // ‚úÖ Role-based permissions
    const rolePermissions = {
        admin: ["/dashboard", "/appointments", "/queue", "/queue-entry", "/branch", "/users", "/services", "/payments", "/employee"],
        manager: ["/dashboard", "/queue", "/queue-entry", "/services", "/appointments", "/payments", "/employee"],
        staff: ["/dashboard", "/queue", "/queue-entry", "/appointments", "/payments"],
    };

    const allowedRoutes = rolePermissions[userRole] || [];
    console.log("üîç User Role:", userRole);
    console.log("üîç Allowed Routes:", allowedRoutes);

    // ‚úÖ ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå
    if (!allowedRoutes.includes(location.pathname)) {
        console.warn(`üî¥ Access Denied: "${location.pathname}" Not Allowed for Role "${userRole}"`);
        return <Navigate to="/dashboard" />;
    }

    return <Outlet />;
};

export default ProtectedRoute;
