import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQueue, updateQueueStatus, deleteQueue } from '../services/api';
import './QueueAdmin.css';
import { generateInvoice } from '../services/api';


const QueueAdmin = () => {
    const navigate = useNavigate();
    const [queue, setQueue] = useState([]);
    const [statusFilter, setStatusFilter] = useState(['รับเข้า', 'อยู่ระหว่างทำความสะอาด','เตรียมส่ง','กำลังจัดส่ง']); //set default ให้ status
    const [filteredQueue, setFilteredQueue] = useState([]);
    const [expandedQueue, setExpandedQueue] = useState(null);

    const Backend_URL = 'http://localhost:5000'; // ดึง URL Backend

    // ✅ กำหนดค่าเริ่มต้นของช่วงวันที่เป็นเดือนปัจจุบัน
    const today = new Date();
   // const firstDayOfMonth = new Date(today.getFullYear(), today.getMonth(), 1).toISOString().split('T')[0];
    const lastDayOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0).toISOString().split('T')[0];
    const firstDayOfYear = new Date(today.getFullYear(), 0, 1).toISOString().split('T')[0]; // 01/01/YYYY
   // const lastDayOfYear = new Date(today.getFullYear(), 11, 31).toISOString().split('T')[0]; // 31/12/YYYY

    const [startDate, setStartDate] = useState(firstDayOfYear);
    const [endDate, setEndDate] = useState(lastDayOfMonth);

    const [invoiceUrl, setInvoiceUrl] = useState(null);
    const [showInvoiceModal, setShowInvoiceModal] = useState(false);

  

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
        navigate('/login'); // ถ้าไม่มี Token ให้ Redirect ไปหน้า Login
        }
        fetchQueue();
    }, []);

    useEffect(() => {
        filterQueueData();
    }, [statusFilter, queue, startDate, endDate]);

    const fetchQueue = async () => {
        try {
            const res = await getQueue();
            setQueue(res.data);
        } catch (error) {
            console.error("🔴 Error fetching queue:", error);
        }
    };

    const filterQueueData = () => {
        const filtered = queue.filter(item => 
            statusFilter.includes(item.status) &&
            new Date(item.received_date) >= new Date(startDate) &&
            new Date(item.received_date) <= new Date(endDate)
        ).sort((b,a) => new Date(b.received_date) - new Date(a.received_date));

        setFilteredQueue(filtered);
    };

    const handleStatusUpdate = async (id, currentStatus, totalPrice) => {
        if (!id || typeof id !== "number") {
            alert("เกิดข้อผิดพลาด: ไม่พบ Queue ID กรุณาลองใหม่");
            return;
        }
    
        const statusMap = {
            'รับเข้า': 'อยู่ระหว่างทำความสะอาด',
            'อยู่ระหว่างทำความสะอาด': 'เตรียมส่ง',
            'เตรียมส่ง': 'กำลังจัดส่ง',
            'กำลังจัดส่ง': 'จัดส่งสำเร็จ'
        };
    
        const newStatus = statusMap[currentStatus];
        if (!newStatus) return;

        if (!window.confirm(`คุณต้องการเปลี่ยนสถานะเป็น "${newStatus}" หรือไม่?`)) return;
    
         // ✅ เช็คว่ามีรูป After หรือไม่ก่อนเปลี่ยนเป็น "เตรียมส่ง"
        if (currentStatus === "อยู่ระหว่างทำความสะอาด" && (!afterImage || afterImage.length === 0)) {
            alert("❌ กรุณาอัปโหลดรูป After ก่อนเปลี่ยนสถานะเป็น 'เตรียมส่ง'!");
            return;
        }

        const parsedTotalPrice = parseFloat(totalPrice) || 0;
        if (parsedTotalPrice === 0) {
            alert("❌ กรุณาเพิ่มรายละเอียดก่อนอัปเดตสถานะ!");
            return;
        }            
        
        try {
            

            await updateQueueStatus(id, newStatus, totalPrice);
            if (newStatus === "เตรียมส่ง") {
               // setShowInvoiceModal(true); // ✅ เปิดหน้าจอแจ้งราคา
            }
            fetchQueue();
        } catch (error) {
            console.error("🔴 Error updating queue status:", error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบคิวนี้?")) {
            try {
                await deleteQueue(id);
                fetchQueue();
            } catch (error) {
                console.error("🔴 Error deleting queue:", error);
            }
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '-';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    const handleStatusFilterChange = (status) => {
        setStatusFilter(prevFilters =>
            prevFilters.includes(status)
                ? prevFilters.filter(item => item !== status)
                : [...prevFilters, status]
        );
    };

    const handleAddCost = () => {
        setAdditionalCosts([...additionalCosts, { name: "", amount: 0 }]);
    };

    const handleCostChange = (index, key, value) => {
        const updatedCosts = [...additionalCosts];
        updatedCosts[index][key] = key === "amount" ? parseFloat(value) : value;
        setAdditionalCosts(updatedCosts);
    };

    // ✅ ฟังก์ชันแจ้งราคา
    const handleGenerateInvoice = async (queue_id) => {
        try {
            const res = await generateInvoice(queue_id);
            setInvoiceUrl(res.data.image_url); // ✅ อัปเดต URL ของใบแจ้งราคา
            console.log("URL",res.data.image_url);
            setShowInvoiceModal(true); // ✅ เปิด Modal
        } catch (error) {
            console.error("🔴 Error generating invoice:", error);
            alert("❌ ไม่สามารถสร้างใบแจ้งราคาได้");
        }
    };
    
    //กำหนดสีสถานะ
    const getStatusColor = (status) => {
    switch (status) {
        case 'รับเข้า':
            return 'bg-danger text-white';  // 🟥 แดง
        case 'อยู่ระหว่างทำความสะอาด':
            return 'bg-warning text-dark';  // 🟧 ส้ม
        case 'เตรียมส่ง':
            return 'bg-warning text-white';  // 🟨 เหลือง
        case 'กำลังจัดส่ง':
            return 'bg-primary text-dark';  // 🌸 ชมพู (ใช้ custom CSS)
        case 'จัดส่งสำเร็จ':
            return 'bg-success text-white';  // ✅ เขียว
        default:
            return 'bg-light text-dark';  // ⚪ ค่าเริ่มต้น (เทาอ่อน)
    }
};

    

    return (
        <div className="queue-container">
            <h2>📋 ระบบคิวงาน</h2>
            <div className="d-flex justify-content-between align-items-center mb-3">
                <button onClick={() => navigate('/dashboard')}>⬅️ กลับไปหน้า Dashboard</button>
                <button onClick={() => navigate('/queue-entry')}>🆕 รับเข้าใหม่</button>
            </div>
            
           

            {/* ✅ ฟิลเตอร์ช่วงวันที่ */}
            <div className="date-filters d-flex align-items-center gap-2 mb-3">
                <label>📅 จาก:</label>
                <input type="date" className="form-control" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
                <label>📅 ถึง:</label>
                <input type="date" className="form-control" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
            </div>

            {/* ✅ ฟิลเตอร์สถานะ */}
            <div className="filters d-flex flex-wrap gap-2 justify-content-start">
                สถานะ :
                {['รับเข้า', 'อยู่ระหว่างทำความสะอาด', 'เตรียมส่ง', 'กำลังจัดส่ง', 'จัดส่งสำเร็จ'].map(status => (
                    <div key={status} className="form-check d-flex align-items-center gap-1">
                        <input
                            type="checkbox"
                            className="form-check-input"
                            checked={statusFilter.includes(status)}
                            onChange={() => handleStatusFilterChange(status)}
                        />
                        <label className="form-check-label" style={{ color: getStatusColor(status), fontWeight: 'bold' }}>
                            {status}
                        </label>
                    </div>
                ))}
            </div>


            <table className="table table-striped table-bordered table-hover">
                <thead className="table-dark">
                    <tr>
                        <th>ชื่อ</th>
                        <th>สถานที่</th>
                        <th>จำนวนคู่</th>                        
                        <th>จำนวนเงิน</th>
                        <th>วันที่รับเข้า</th>
                        <th>วันที่ส่งคืน</th>
                        <th>สถานะ</th>
                        <th>อัปเดต</th>
                        <th>ลบ</th>
                        <th>รายละเอียด</th>
                        <th>สร้างใบแจ้งราคา</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredQueue.map(item => {  
                        const totalPrice = item.total_price ? parseFloat(item.total_price) : 0;
                        return (
                            <tr key={item.queue_id}>
                                <td>{item.customer_name}</td>
                                <td>{item.location}</td>
                                <td>{item.total_pairs}</td>
                                <td>{item.total_price}</td>
                                <td>{formatDate(item.received_date)}</td>
                                <td>{formatDate(item.delivery_date)}</td>
                                <td>
                                    <span className={`badge ${getStatusColor(item.status)}`}>
                                        {item.status}
                                    </span>
                                </td>
                                <td>
                                    {item.status !== 'จัดส่งสำเร็จ' && (
                                        <button className="btn btn-warning btn-sm" onClick={() => handleStatusUpdate(item.queue_id, item.status, totalPrice)}>
                                            อัปเดต
                                        </button>
                                    )}
                                </td>
                                <td>
                                    {item.status !== 'จัดส่งสำเร็จ' && (
                                        <button className="btn btn-danger btn-sm" onClick={() => handleDelete(item.queue_id)}>
                                            ลบ
                                        </button>
                                    )}
                                </td>
                                <td>
                                    <button className="btn btn-info btn-sm" onClick={() => navigate(`/queue/${item.queue_id}/detail`)}>
                                        รายละเอียด
                                    </button>
                                </td>
                                <td>
                                    <button className="btn btn-success btn-sm" onClick={() => handleGenerateInvoice(item.queue_id)}>
                                        ใบแจ้งราคา
                                    </button>
                                </td>

                            </tr>
                        );
                    })}
                </tbody>
            </table>
            {/* ✅ Modal แสดงใบแจ้งราคา */}
            {showInvoiceModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h2>📜 ใบแจ้งราคา</h2>
                        {invoiceUrl ? (
                            <img src={invoiceUrl} alt="Invoice" style={{ width: "100%", borderRadius: "10px" }} />
                        ) : (
                            <p>⏳ กำลังโหลดใบแจ้งราคา...</p>
                        )}
                        <button onClick={() => setShowInvoiceModal(false)}>❌ ปิด</button>
                    </div>
                </div>
            )}
        </div>
        
    );


};

export default QueueAdmin;
