    };

    const handleDelete = async (id) => {
        if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏Ñ‡∏¥‡∏ß‡∏ô‡∏µ‡πâ?")) {
            try {
                await deleteQueue(id);
                fetchQueue();
            } catch (error) {
                console.error("üî¥ Error deleting queue:", error);
            }
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '-';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    const handleStatusFilterChange = (status) => {
        setStatusFilter(prevFilters =>
            prevFilters.includes(status)
                ? prevFilters.filter(item => item !== status)
                : [...prevFilters, status]
        );
    };

    const handleAddCost = () => {
        setAdditionalCosts([...additionalCosts, { name: "", amount: 0 }]);
