import React, { useState, useEffect } from "react";
import { getReports } from "../services/api"; // API ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô
import { jwtDecode } from "jwt-decode";
import "bootstrap/dist/css/bootstrap.min.css";

const ReportManagement = () => {
    const [reports, setReports] = useState([]);
    const [branches, setBranches] = useState([]);
    const [selectedBranch, setSelectedBranch] = useState(null);
    const [selectedReportType, setSelectedReportType] = useState("income"); // Default: ‡∏£‡∏≤‡∏¢‡πÑ‡∏î‡πâ
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [isSuperAdmin, setIsSuperAdmin] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) return;

        try {
            const user = jwtDecode(token);
            setIsSuperAdmin(user.isSuperAdmin);
            setSelectedBranch(user.isSuperAdmin ? "" : user.branch_id);
        } catch (error) {
            console.error("üî¥ Error decoding token:", error);
        }
    }, []);

    const fetchReports = async () => {
        try {
            const res = await getReports({ branch_id: selectedBranch, report_type: selectedReportType, start_date: startDate, end_date: endDate });
            setReports(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching reports:", error);
        }
    };

    return (
        <div className="container mt-4">
            <h1>üìä ‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô</h1>
            <div className="card p-3 mb-3 shadow">
                <div className="row">
                    {isSuperAdmin && (
                        <div className="col-md-3">
                            <label className="form-label">‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏≤‡∏Ç‡∏≤</label>
                            <select className="form-select" value={selectedBranch} onChange={(e) => setSelectedBranch(e.target.value)}>
                                <option value="">-- ‡∏ó‡∏∏‡∏Å‡∏™‡∏≤‡∏Ç‡∏≤ --</option>
                                {branches.map(branch => (
                                    <option key={branch.id} value={branch.id}>{branch.name}</option>
                                ))}
                            </select>
                        </div>
                    )}
                    <div className="col-md-3">
                        <label className="form-label">‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô</label>
                        <select className="form-select" value={selectedReportType} onChange={(e) => setSelectedReportType(e.target.value)}>
                        <option value="income_report">üìà ‡∏£‡∏≤‡∏¢‡∏£‡∏±‡∏ö</option>
                        <option value="expenses_report">üìâ ‡∏£‡∏≤‡∏¢‡∏à‡πà‡∏≤‡∏¢</option>
                        <option value="profit_loss_report">üí∞ ‡∏Å‡∏≥‡πÑ‡∏£-‡∏Ç‡∏≤‡∏î‡∏ó‡∏∏‡∏ô</option>
                        <option value="topservices_report">üî• ‡∏ö‡∏£‡∏¥‡∏Å‡∏≤‡∏£‡∏¢‡∏≠‡∏î‡∏ô‡∏¥‡∏¢‡∏°</option>
                        <option value="customer_report">üõçÔ∏è ‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤ (Customer Report)</option>
                        </select>
                    </div>
                    <div className="col-md-2">
                        <label className="form-label">‡∏à‡∏≤‡∏Å‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà</label>
                        <input type="date" className="form-control" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
                    </div>
                    <div className="col-md-2">
                        <label className="form-label">‡∏ñ‡∏∂‡∏á‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà</label>
                        <input type="date" className="form-control" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
                    </div>
                    <div className="col-md-2 d-flex align-items-end">
                        <button className="btn btn-primary w-100" onClick={fetchReports}>üîç ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤</button>
                    </div>
                </div>
            </div>

            {/* ‡∏õ‡∏∏‡πà‡∏°‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î */}
            <div className="d-flex justify-content-end mt-3">
                <button className="btn btn-success me-2">üì• ‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î Excel</button>
                <button className="btn btn-danger">üìÑ ‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î PDF</button>
            </div>
            {/* ‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô */}
            <div className="card p-3 shadow">
                <h5>üìã ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô</h5>
                <table className="table table-hover">
                    <thead className="table-dark">
                        <tr>
                            <th>‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà</th>
                            <th>‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î</th>
                            <th>‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó</th>
                            <th>‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡πÄ‡∏á‡∏¥‡∏ô</th>
                        </tr>
                    </thead>
                    <tbody>
                        {reports.length > 0 ? (
                            reports.map((report, index) => (
                                <tr key={index}>
                                    <td>{new Date(report.date).toLocaleDateString()}</td>
                                    <td>{report.description}</td>
                                    <td>{report.type}</td>
                                    <td>{report.amount.toFixed(2)} ‡∏ø</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="4" className="text-center text-muted">‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>

           
        </div>
    );
};

export default ReportManagement;
