            const user = jwtDecode(token);
            setIsSuperAdmin(user.isSuperAdmin);

            if (user.isSuperAdmin) {
                setSelectedBranch(""); // ‚úÖ SuperAdmin ‡∏ï‡πâ‡∏≠‡∏á‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏≤‡∏Ç‡∏≤‡∏Å‡πà‡∏≠‡∏ô‡πÅ‡∏™‡∏î‡∏á‡∏ú‡∏•
