import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQueue, updateQueueStatus, deleteQueue } from '../services/api';
import './QueueAdmin.css';

const QueueAdmin = () => {
    const navigate = useNavigate();
    const [queue, setQueue] = useState([]);
    const [statusFilter, setStatusFilter] = useState(['รับเข้า', 'อยู่ระหว่างทำความสะอาด']);
    const [filteredQueue, setFilteredQueue] = useState([]); // ✅ เพิ่มตัวแปรนี้


    useEffect(() => {
        fetchQueue();
    }, []);

    useEffect(() => {
        setFilteredQueue(queue.filter(item => statusFilter.includes(item.status)));
    }, [statusFilter, queue]);
   
    const fetchQueue = async () => {
        try {
            const res = await getQueue();
            setQueue(res.data);
        } catch (error) {
            console.error("🔴 Error fetching queue:", error);
        }
    };

    const handleStatusUpdate = async (id, newStatus) => {
        let totalPrice = null;
        if (newStatus === "เตรียมส่ง") {
            totalPrice = prompt("กรุณากรอกจำนวนเงินทั้งหมด:");
            if (!totalPrice || isNaN(totalPrice)) {
                alert("จำนวนเงินไม่ถูกต้อง!");
                return;
            }
        }
        
        try {
            await updateQueueStatus(id, newStatus, totalPrice);
            fetchQueue();
        } catch (error) {
            console.error("🔴 Error updating queue status:", error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบคิวนี้?")) {
            try {
                await deleteQueue(id);
                fetchQueue();
            } catch (error) {
                console.error("🔴 Error deleting queue:", error);
            }
        }
    };

    const [expandedQueue, setExpandedQueue] = useState(null);

    const toggleQueueDetail = (id) => {
        setExpandedQueue(expandedQueue === id ? null : id);
    };


    return (
        <div className="queue-container">
            <h1>📋 ระบบคิวงาน</h1>
            <button onClick={() => navigate('/dashboard')}>⬅️ กลับไปหน้า Dashboard</button>
            <button onClick={() => navigate('/queue-entry')}>🆕 รับเข้าใหม่</button>

            <div className="filters">
                <label>
                    <input type="checkbox" checked={statusFilter.includes('รับเข้า')} onChange={() => setStatusFilter(['รับเข้า'])} /> รับเข้า
                </label>
                <label>
                    <input type="checkbox" checked={statusFilter.includes('อยู่ระหว่างทำความสะอาด')} onChange={() => setStatusFilter(['อยู่ระหว่างทำความสะอาด'])} /> อยู่ระหว่างทำ
                </label>
                <label>
                    <input type="checkbox" checked={statusFilter.includes('เตรียมส่ง')} onChange={() => setStatusFilter(['เตรียมส่ง'])} /> เตรียมส่ง
                </label>
                <label>
                    <input type="checkbox" checked={statusFilter.includes('กำลังจัดส่ง')} onChange={() => setStatusFilter(['กำลังจัดส่ง'])} /> กำลังจัดส่ง
                </label>
                <label>
                    <input type="checkbox" checked={statusFilter.includes('จัดส่งสำเร็จ')} onChange={() => setStatusFilter(['จัดส่งสำเร็จ'])} /> จัดส่งสำเร็จ
                </label>
            </div>

            <table>
                <thead>
                    <tr>
                        <th>ชื่อ</th>
                        <th>เบอร์โทร</th>
                        <th>สถานที่</th>
                        <th>จำนวนคู่</th>
                        <th>วันที่รับเข้า</th>  
                        <th>วันที่ส่งคืน</th>
                        <th>สถานะ</th>
                        <th>บริการ</th>
                        <th>อัปเดต</th>
                        <th>ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredQueue.map(item => ( 
                        
                        <tr key={item.id}>
                            <td>{item.customer_name}</td>
                            <td>{item.phone}</td>
                            <td>{item.location}</td>
                            <td>{item.total_pairs}</td>
                            <td>{item.received_date}</td>
                            <td>{item.delivery_date}</td>
                            <td>{item.status}</td>
                            <td>
                                {item.services.map(service => (
                                    <p key={service.id}>{service.service_type} ({service.pair_count} คู่)</p>
                                ))}
                            </td>
                            <td>
                                {item.status !== 'จัดส่งสำเร็จ' && (
                                    <button onClick={() => handleStatusUpdate(item.id, 
                                        item.status === 'รับเข้า' ? 'อยู่ระหว่างทำความสะอาด' :
                                        item.status === 'อยู่ระหว่างทำความสะอาด' ? 'เตรียมส่ง' :
                                        item.status === 'เตรียมส่ง' ? 'กำลังจัดส่ง' : 'จัดส่งสำเร็จ')}>
                                        อัปเดต
                                    </button>
                                )}
                            </td>
                            <td>
                                {item.status !== 'จัดส่งสำเร็จ' && (
                                    <button onClick={() => handleDelete(item.id)}>ลบ</button>
                                )}
                            </td>
                            <td>
                                <button onClick={() => toggleQueueDetail(appt.id)}>
                                    {expandedQueue === appt.id ? '🔼 ซ่อนรายละเอียด' : '🔽 ดูรายละเอียด'}
                                </button>
                            </td>

                        </tr>

                        
                    ))}
                    console.log({item});
                </tbody>
            </table>
        </div>
    );
};

export default QueueAdmin;
