import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getQueueDetail, getServices, updateQueue, addQueueItems, deleteQueueItem,uploadAfterImages  } from '../services/api';
import './QueueDetail.css';

import AddShoeModal from "../components/AddShoeModal"; // ‚úÖ Import Modal
import ShoeDetailModal from "../components/ShoeDetailModal"; 

const QueueDetail = () => {
    const navigate = useNavigate();
    const { queue_id } = useParams();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [selectedService, setSelectedService] = useState('');
    
    const Backend_URL = 'http://localhost:5000'; // ‡∏î‡∏∂‡∏á URL Backend

    //state modal ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤
    const [showAddShoeModal, setShowAddShoeModal] = useState(false);
    const [showUploadAfterModal, setShowUploadAfterModal] = useState(false);

    //state modal ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤
    const [showShoeDetailModal, setShowShoeDetailModal] = useState(false);
    const [selectedShoe, setSelectedShoe] = useState(null);

    // ‚úÖ ‡πÄ‡∏Å‡πá‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤
    const [shoeData, setShoeData] = useState({
        brand: '',
        model: '',
        color: '',
        notes: '',
        images: { front: '', back: '', left: '', right: '', top: '', bottom: '' }
    });

    // ‡πÄ‡∏û‡∏¥‡πà‡∏° State ‡πÄ‡∏Å‡πá‡∏ö‡πÑ‡∏ü‡∏•‡πå‡∏£‡∏π‡∏õ After
    const [showModal, setShowModal] = useState(false);
    const [selectedItemId, setSelectedItemId] = useState(null);
    const [afterImages, setAfterImages] = useState({
        front: null,
        back: null,
        left: null,
        right: null,
        top: null,
        bottom: null
    });

    const openUploadAfterModal = (item_id) => {
        console.log("üìå Debug: ‡πÄ‡∏õ‡∏¥‡∏î Modal ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Item ID:", item_id);
        setSelectedItemId(item_id);
        setShowUploadAfterModal(true);
    };
    
    const closeUploadAfterModal = () => {
        setShowUploadAfterModal(false);
        setAfterImages({ front: null, back: null, left: null, right: null, top: null, bottom: null });
    };

    

    useEffect(() => {
        fetchQueueDetail();
        fetchServices();
    }, []);

    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(queue_id);
            console.log("data:",res);
            setQueue(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching queue detail:", error);
        }
    };

    const fetchServices = async () => {
        try {
            const res = await getServices();
            setServices(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching services:", error);
        }
    };

    // ‚úÖ ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û
    const handleUploadImage = (position, file) => {
        if (!file) return;
        const reader = new FileReader();
        reader.onloadend = () => {
            setShoeData(prev => ({
                ...prev,
                images: { ...prev.images, [position]: reader.result }
            }));
        };
        reader.readAsDataURL(file);
    };

    //‡πÄ‡∏û‡∏¥‡πà‡∏° onChange ‡πÉ‡∏´‡πâ‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏£‡∏π‡∏õ‡∏à‡∏≤‡∏Å‡∏≠‡∏±‡∏•‡∏ö‡∏±‡πâ‡∏° / ‡∏Å‡∏•‡πâ‡∏≠‡∏á‡∏ö‡∏ô‡∏°‡∏∑‡∏≠‡∏ñ‡∏∑‡∏≠
    const handleImageUpload = (event, position) => {
        const file = event.target.files[0];
        if (file) {
            setShoeData(prevData => ({
                ...prevData,
                images: {
                    ...prevData.images,
                    [position]: file
                }
            }));
        }
    };

    
    // ‚úÖ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡πÄ‡∏Ç‡πâ‡∏≤ queue_items
    const handleAddShoe = async (selectedService, shoeData) => {
        if (!selectedService || !shoeData.brand || !shoeData.model || !shoeData.color) {
            alert("‚ùå ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô");
            return;
        }

    
        const formData = new FormData();
        formData.append("queue_id", queue_id);
        formData.append("service_id", selectedService);
        formData.append("price_per_pair", services.find(s => s.id === parseInt(selectedService))?.base_price || 0);
        formData.append("brand", shoeData.brand);
        formData.append("model", shoeData.model);
        formData.append("color", shoeData.color);
        formData.append("notes", shoeData.notes || "");
    
        // ‚úÖ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÑ‡∏ü‡∏•‡πå‡∏£‡∏π‡∏õ‡πÄ‡∏Ç‡πâ‡∏≤ `FormData`
        Object.keys(shoeData.images).forEach((key) => {
            if (shoeData.images[key]) {
                formData.append(key, shoeData.images[key]); // ‚úÖ Key ‡∏ï‡πâ‡∏≠‡∏á‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö Backend
            }
        });
    
        try {
            await addQueueItems(queue_id, formData);
            alert("‚úÖ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢!");
            fetchQueueDetail(); // ‚úÖ ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡∏°‡πà
        } catch (error) {
            console.error("üî¥ Error adding shoe:", error);
        }
    };
         

    

    // ‚úÖ ‡∏•‡∏ö‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å queue_items
    const handleRemoveShoe = async (queue_item_id) => {
        if (!queue_item_id) {
            console.error("‚ùå ‡πÑ‡∏°‡πà‡∏û‡∏ö queue_item_id");
            return;
        }

        if (!window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡∏ô‡∏µ‡πâ?")) return;
        try {
            console.log(`üìå Debug: ‡∏•‡∏ö queue_item_id: ${queue_item_id}`);
            await deleteQueueItem(queue_id, queue_item_id);
            alert("‚úÖ ‡∏•‡∏ö‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢!");
            fetchQueueDetail();
        } catch (error) {
            console.error("üî¥ Error deleting shoe:", error);
        }
    };

    // ‚úÖ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Å‡∏≤‡∏£‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡πÅ‡∏õ‡∏•‡∏á
    const handleSaveChanges = async () => {
        if (!window.confirm("‡∏Ñ‡∏∏‡∏ì‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Å‡∏≤‡∏£‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡πÅ‡∏õ‡∏•‡∏á‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà?")) return;
        try {
            await updateQueue(queue_id);
            alert("‚úÖ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß!");
            navigate('/queue');
        } catch (error) {
            console.error("üî¥ Error updating queue:", error);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    //‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡πà‡∏ô‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤
    const handleShowShoeDetail = (shoe) => {
        setSelectedShoe(shoe);
        setShowShoeDetailModal(true);
    };
  

        //------------------modal Upload After images---------------------
        //‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏õ‡∏¥‡∏î Modal ‡πÅ‡∏•‡∏∞‡∏Å‡∏≥‡∏´‡∏ô‡∏î item_id ‡∏Ç‡∏≠‡∏á‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤
        const openModal = (item_id) => {
            console.log("üìå Debug: ‡πÄ‡∏õ‡∏¥‡∏î Modal ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Item ID:", item_id);
            setSelectedItemId(item_id);
            setShowModal(true);
        };
        
        const closeModal = () => {
            setShowModal(false);
            setAfterImages({ front: null, back: null, left: null, right: null, top: null, bottom: null });
        };

        const handleAfterImageUpload = (position, file) => {
            if (!file) return;
            setAfterImages(prev => ({ ...prev, [position]: file }));
        };
        
        const handleUploadAfterImages = async () => {
            if (!selectedItemId) {
                alert("‚ùå ‡πÑ‡∏°‡πà‡∏û‡∏ö Item ID");
                return;
            }
        
            try {
                console.log("üìå Debug: ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ After...", selectedItemId, afterImages);
                await uploadAfterImages(queue_id, selectedItemId, afterImages);
                alert("‚úÖ ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ After ‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!");
                await fetchQueueDetail(); // ‡∏£‡∏µ‡πÄ‡∏ü‡∏£‡∏ä‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡∏°‡πà
                closeModal(); // ‡∏õ‡∏¥‡∏î Modal ‡∏´‡∏•‡∏±‡∏á‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à
            } catch (error) {
                console.error("üî¥ Error uploading after images:", error);
            }
        };
        
        //------Function ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö ‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡∏£‡∏ß‡∏°‡∏¢‡∏≠‡∏î
        
        const getServiceSummary = () => {
            const serviceMap = {};
        
            queue.queue_items.forEach(item => {
                if (!serviceMap[item.service_id]) {
                    serviceMap[item.service_id] = {
                        service_name: item.service_name,
                        total_pairs: 0,
                        total_price: 0
                    };
                }
                serviceMap[item.service_id].total_pairs += 1;
                serviceMap[item.service_id].total_price += parseFloat(item.price_per_pair) || 0;
            });
        
            return Object.values(serviceMap);
        };

        
         if (!queue) return <p>‚è≥ ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•...</p>;
    return (
        <div className="queue-detail-container">
            <h1>üè∑Ô∏è Queue Detail (#{queue_id})</h1>
            <div className="queue-actions">
                <button onClick={() => navigate('/queue')}>üîÑ ‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤‡∏Ñ‡∏¥‡∏ß</button>
            </div>
            <div className="queue-info">
                <p>üë§ ‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤: {queue.customer_name}</p>
                <p>üìû ‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£: {queue.phone}</p>
                <p>üìç ‡∏™‡∏ñ‡∏≤‡∏ô‡∏ó‡∏µ‡πà: {queue.location}</p>
                <p>üëü ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ñ‡∏π‡πà: {queue.total_pairs}</p>
                <p>üìÖ ‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤: {formatDate(queue.received_date)}</p>
                <p>üöö ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏™‡πà‡∏á‡∏Ñ‡∏∑‡∏ô: {formatDate(queue.delivery_date)}</p>
            </div>

            <h2>üëü ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤</h2>
            <button onClick={() => setShowAddShoeModal(true)}>‚ûï ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤</button>

                {showAddShoeModal && (
                    <AddShoeModal 
                        show={showAddShoeModal} 
                        onClose={() => setShowAddShoeModal(false)} 
                        onAddShoe={handleAddShoe} 
                        services={services} 
                    />
                )}

            <table>
                <thead>
                    <tr>
                        <th>‡πÅ‡∏ö‡∏£‡∏ô‡∏î‡πå</th>
                        <th>‡∏£‡∏∏‡πà‡∏ô</th>
                        <th>‡∏™‡∏µ</th>
                        <th>‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏´‡∏ï‡∏∏</th>
                        <th>BEFORE</th>
                        <th>AFTER</th>
                        <th>‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î</th>
                        <th>‡∏•‡∏ö</th>
                    </tr>
                </thead>
                <tbody>
                    {queue.queue_items.map((item, index) => (
                        
                        <tr key={index}>
                            <td>{item.brand}</td>
                            <td>{item.model}</td>
                            <td>{item.color}</td>
                            <td>{item.notes}</td>
                            <td>
                                <div className="shoe-images">
                                    {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û Before ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ */}
                                    {[item.image_before_front]
                                        
                                        .filter(img => img) // ‚úÖ ‡∏Å‡∏£‡∏≠‡∏á‡∏Ñ‡πà‡∏≤‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡πá‡∏ô null ‡∏´‡∏£‡∏∑‡∏≠ undefined
                                        .map((img, i) => (                                            
                                            <img key={i} src={`${Backend_URL}${img}`} alt={`shoe-${index}-${i}`} width="50" />                                      
                                        ))
                                    }
                                </div>
                            </td>
                            <td>
                                {item.image_after_front ? (
                                    <img src={`${Backend_URL}${item.image_after_front}`} alt={`after-${index}`} width="50" />
                                ) : (
                                    <button onClick={(e) => { 
                                        e.stopPropagation(); 
                                        openUploadAfterModal(item.queue_item_id);
                                    }}>Upload</button>
                                )}
                            </td>
                            <td>
                                <button onClick={() => handleShowShoeDetail(item)}>‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î</button>
                            </td>
                            <td>
                                <button onClick={() => handleRemoveShoe(item.queue_item_id)}>‚ùå</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
           
            {showUploadAfterModal  && (
                <div className="modal-overlay">
