import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAppointments, createAppointment, updateAppointmentStatus,deleteAppointment } from '../services/api';
import AddAppointmentModal from "../components/AddAppointmentModal"; // ✅ Import Modal
import './AppointmentsAdmin.css';
import '../assets/css/bootstrap.min.css';

const AppointmentsAdmin = () => {
    const navigate = useNavigate();
    const [appointments, setAppointments] = useState([]);
    const [customerName, setCustomerName] = useState('');
    const [showAddModal, setShowAddModal] = useState(false);

    const [phone, setPhone] = useState('');
    const [location, setLocation] = useState('');
    const [shoeCount, setShoeCount] = useState(1);
    const [appointmentDate, setAppointmentDate] = useState(() => {
        const tomorrow = new Date();
        tomorrow.setDate(tomorrow.getDate() + 1);
        return tomorrow.toISOString().split('T')[0]; // แปลงเป็น YYYY-MM-DD
      });
    const [appointmentTime, setAppointmentTime] = useState('');

    const [statusFilter, setStatusFilter] = useState(['รอดำเนินการ', 'ยืนยันแล้ว']);
    const [editId, setEditId] = useState(null);
    const [filteredAppointments, setFilteredAppointments] = useState([]);
    

    useEffect(() => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login'); // ถ้าไม่มี Token ให้ Redirect ไปหน้า Login
      }
        fetchAppointments();
    }, []);

    useEffect(() => {
        setFilteredAppointments(appointments.filter(appt => statusFilter.includes(appt.status)));
    }, [statusFilter, appointments]);
        
  const fetchAppointments = async () => {
    try {
      const res = await getAppointments();
      const sortedAppointments = res.data.sort((a, b) => {
        const statusOrder = { 'รอดำเนินการ': 1, 'ยืนยันแล้ว': 2, 'สำเร็จ': 3 };
        return statusOrder[a.status] - statusOrder[b.status] || new Date(b.appointment_date) - new Date(a.appointment_date);
      });
      setAppointments(sortedAppointments);
    } catch (error) {
      console.error("🔴 Error fetching appointments:", error);
    }
  };
/* 
  const handleCreateAppointment = async (newAppointment) => {
    if (!newAppointment) {
        console.error("🔴 Error: newAppointment is undefined!");
        alert("❌ ไม่สามารถบันทึกนัดหมายได้: ข้อมูลไม่ถูกต้อง");
        return;
    }

    console.log("📌 Debug: ค่าที่ส่งไปจาก Modal", newAppointment); // ✅ Debug ค่าที่ส่งไปจาก Modal

    if (!window.confirm("คุณต้องการเพิ่มนัดหมายนี้หรือไม่?")) {
      return;
    }
    try {
        await createAppointment(newAppointment);
        fetchAppointments();
        setShowAddModal(false);
    } catch (error) {
        console.error("🔴 Error creating appointment:", error);
        alert(`❌ ไม่สามารถสร้างนัดหมายได้: ${error.message}`);
    }
  };
 */


   const handleCreateAppointment = async (newAppointment) => {
    try {
       const newAppointment = {
        customer_name: customerName,
        phone,
        location,
        shoe_count: shoeCount,
        appointment_date: appointmentDate,
        appointment_time: appointmentTime,
      }; 

      if (!newAppointment) {
        console.error("🔴 Error: newAppointment is undefined!");
        alert("❌ ไม่สามารถบันทึกนัดหมายได้: ข้อมูลไม่ถูกต้อง");
        return;
    }

      console.log("📌 Debug: ค่าที่ส่งไปยัง Backend", newAppointment); // ✅ Debug ค่าที่ส่งไป

      if (!window.confirm("คุณต้องการเพิ่มนัดหมายนี้หรือไม่?")) {
        return;
      }

      await createAppointment(newAppointment);
      fetchAppointments();
      setCustomerName('');
      setPhone('');
      setLocation('');
      setShoeCount(1);
      setAppointmentDate('');
      setAppointmentTime('');
    } catch (error) {
      console.error("🔴 Error creating appointment:", error);
    }
  }; 

  const handleStatusUpdate = async (id, newStatus) => {
    try {
      await updateAppointmentStatus(id, newStatus);
      fetchAppointments();
    } catch (error) {
      console.error("🔴 Error updating status:", error);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบนัดหมายนี้?")) {
      try {
        await deleteAppointment(id);
        fetchAppointments();
      } catch (error) {
        console.error("🔴 Error deleting appointment:", error);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
  };

  const handleStatusFilterChange = (status) => {
    setStatusFilter(prevFilters => 
        prevFilters.includes(status) 
            ? prevFilters.filter(item => item !== status) 
            : [...prevFilters, status]
    );
};


  return (
    <div className="appointments-container">
      <h1>📅 รายการนัดหมาย</h1>
      <button onClick={() => navigate('/dashboard')}>⬅️ กลับไปหน้า Dashboard</button>
      
      <div className="filters">
        <label>
            <input type="checkbox" checked={statusFilter.includes('รอดำเนินการ')} onChange={() => handleStatusFilterChange('รอดำเนินการ')} /> รอดำเนินการ
        </label>
        <label>
            <input type="checkbox" checked={statusFilter.includes('ยืนยันแล้ว')} onChange={() => handleStatusFilterChange('ยืนยันแล้ว')} /> ยืนยันแล้ว
        </label>
        <label>
            <input type="checkbox" checked={statusFilter.includes('สำเร็จ')} onChange={() => handleStatusFilterChange('สำเร็จ')} /> สำเร็จ
        </label>
     </div>
      
      <table>
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>เบอร์โทร</th>
            <th>สถานที่</th>
            <th>จำนวนรองเท้า</th>
            <th>วันที่</th>
            <th>เวลา</th>
            <th>สถานะ</th>
            <th>อัปเดต</th>
            <th>ลบ</th>
          </tr>
        </thead>
        <tbody>
        {filteredAppointments
                        .sort((b, a) => new Date(b.appointment_date) - new Date(a.appointment_date))
                        .map(appt => (
            <tr key={appt.id}>
              <td>{appt.customer_name}</td>
              <td>{appt.phone}</td>
              <td>{appt.location}</td>
              <td>{appt.shoe_count}</td>
              <td>{formatDate(appt.appointment_date)}</td>                
              <td>{appt.appointment_time}</td>
              <td>{appt.status}</td>
              <td>
                {appt.status !== 'สำเร็จ' && (
                  <button onClick={() => handleStatusUpdate(appt.id, appt.status === 'รอดำเนินการ' ? 'ยืนยันแล้ว' : 'สำเร็จ')}>
                    อัปเดต
                  </button>
                )}
              </td>
              <td>
              {appt.status !== 'สำเร็จ' && (
                  <button onClick={() => handleDelete(appt.id)}>ลบ</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

     {/*  <h2>➕ เพิ่มนัดหมาย</h2>
      <input type="text" placeholder="ชื่อ" value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
      <input type="text" placeholder="เบอร์โทร" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <input type="text" placeholder="สถานที่" value={location} onChange={(e) => setLocation(e.target.value)} />
      <input type="number" placeholder="จำนวนรองเท้า" value={shoeCount} onChange={(e) => setShoeCount(e.target.value)} />
      <input
            type="date"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
            />
      <input type="time" value={appointmentTime} onChange={(e) => setAppointmentTime(e.target.value)} />
      <button onClick={handleCreateAppointment}>บันทึกนัดหมาย</button>
        */}


      <div className="container mt-4">
            {/* ปุ่ม "กลับไป Dashboard" และ "เพิ่มนัดหมาย" */}
            <div className="d-flex justify-content-between mb-3">
                <button className="btn btn-secondary" onClick={() => navigate('/dashboard')}>
                    ⬅️ กลับไปหน้า Dashboard
                </button>
                <button className="btn btn-primary" onClick={() => setShowAddModal(true)}>
                    ➕ เพิ่มนัดหมาย
                </button>
            </div>

            {/* ฟิลเตอร์สถานะ */}
            <div className="card p-3 mb-3 shadow">
                <h5>📌 กรองสถานะ</h5>
                {['รอดำเนินการ', 'ยืนยันแล้ว', 'สำเร็จ'].map(status => (
                    <div key={status} className="form-check">
                        <input
                            type="checkbox"
                            className="form-check-input"
                            checked={statusFilter.includes(status)}
                            onChange={() => setStatusFilter(prev => prev.includes(status) ? prev.filter(s => s !== status) : [...prev, status])}
                        />
                        <label className="form-check-label">{status}</label>
                    </div>
                ))}
            </div>

            {/* ตารางนัดหมาย */}
            <table className="table table-bordered table-striped">
                <thead>
                    <tr>
                        <th>ชื่อ</th>
                        <th>เบอร์โทร</th>
                        <th>สถานที่</th>
                        <th>จำนวนรองเท้า</th>
                        <th>วันที่</th>
                        <th>เวลา</th>
                        <th>สถานะ</th>
                        <th>อัปเดต</th>
                        <th>ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredAppointments.map(appt => (
                        <tr key={appt.id}>
                            <td>{appt.customer_name}</td>
                            <td>{appt.phone}</td>
                            <td>{appt.location}</td>
                            <td>{appt.shoe_count}</td>
                            <td>{formatDate(appt.appointment_date)}</td>
                            <td>{appt.appointment_time}</td>
                            <td>{appt.status}</td>
                            <td>
                                {appt.status !== 'สำเร็จ' && (
                                    <button className="btn btn-warning btn-sm" onClick={() => handleStatusUpdate(appt.id, appt.status === 'รอดำเนินการ' ? 'ยืนยันแล้ว' : 'สำเร็จ')}>
                                        อัปเดต
                                    </button>
                                )}
                            </td>
                            <td>
                                {appt.status !== 'สำเร็จ' && (
                                    <button className="btn btn-danger btn-sm" onClick={() => handleDelete(appt.id)}>
                                        ❌ ลบ
                                    </button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* แสดง Modal เมื่อกดปุ่ม "เพิ่มนัดหมาย" */}
            {showAddModal && (
                <AddAppointmentModal
                    show={showAddModal}
                    onClose={() => setShowAddModal(false)}
                    onAddAppointment={handleCreateAppointment}
                />
            )}
        </div>
    </div>
  );
};

export default AppointmentsAdmin;
