import React, { useState, useEffect } from "react";
import { getPayouts, createPayout, updatePayout, deletePayout } from "../services/api";
import "bootstrap/dist/css/bootstrap.min.css";

const THAI_MONTHS = [
    "มกราคม", "กุมภาพันธ์", "มีนาคม", "เมษายน", "พฤษภาคม", "มิถุนายน",
    "กรกฎาคม", "สิงหาคม", "กันยายน", "ตุลาคม", "พฤศจิกายน", "ธันวาคม"
];

const Payouts = () => {
    const today = new Date();
    const currentYear = today.getFullYear();
    const currentMonth = today.getMonth() + 1; // ✅ เดือนเริ่มจาก 0 จึงต้อง +1

    const [payouts, setPayouts] = useState([]);
    const [selectedYear, setSelectedYear] = useState(currentYear);
    const [selectedMonth, setSelectedMonth] = useState(currentMonth);
    const [loading, setLoading] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const [form, setForm] = useState({
        id: null,
        payout_type: "",
        description: "",
        amount: "",
        notes: "",
        payout_date: "",
    });

    // ✅ ฟังก์ชันสร้างรายการปี (ย้อนหลัง 5 ปี)
    const years = Array.from({ length: 6 }, (_, i) => currentYear - i); 

    useEffect(() => {
        fetchPayouts(); // ✅ โหลดข้อมูลเมื่อ Component เริ่มต้น
    }, [selectedYear, selectedMonth]);

    const fetchPayouts = async () => {
        try {
            setLoading(true);

            // ✅ คำนวณ StartDate และ EndDate จาก selectedYear และ selectedMonth
            const startDate = `${selectedYear}-${String(selectedMonth).padStart(2, "0")}-01`;
            const endDate = new Date(selectedYear, selectedMonth, 0).toISOString().split("T")[0];

            console.log(`📌 Fetching Payouts for: ${selectedYear} ${THAI_MONTHS[selectedMonth - 1]} (${startDate} to ${endDate})`);

            // ✅ ส่ง request ไปที่ API
            const res = await getPayouts({
                start_date: startDate,
                end_date: endDate,
            });

            setPayouts(res.data);
        } catch (error) {
            console.error("🔴 Error fetching payouts:", error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (form.id) {
                await updatePayout(form.id, form);
                alert("✅ อัปเดตรายจ่ายเรียบร้อย!");
            } else {
                await createPayout(form);
                alert("✅ เพิ่มรายจ่ายเรียบร้อย!");
            }
            fetchPayouts();
            setShowModal(false);
            setForm({ id: null, payout_type: "", description: "", amount: "", notes: "", payout_date: "" });
        } catch (error) {
            console.error("🔴 Error saving payout:", error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบรายการนี้?")) {
            try {
                await deletePayout(id);
                alert("✅ ลบรายการเรียบร้อย!");
                fetchPayouts();
            } catch (error) {
                console.error("🔴 Error deleting payout:", error);
            }
        }
    };

    // ✅ คำนวณยอดรวม
    const totalAmount = payouts.reduce((sum, payout) => sum + parseFloat(payout.amount || 0), 0);

    return (
        <div className="container mt-4">
            <h1>📉 รายจ่าย</h1>
            <div className="card p-3 mb-3 shadow">
                <div className="row">
                    {/* ✅ เลือกปี */}
                    <div className="col-md-3">
                        <label className="form-label">เลือกปี</label>
                        <select 
                            className="form-select" 
                            value={selectedYear} 
                            onChange={(e) => setSelectedYear(Number(e.target.value))}
                        >
                            {years.map((year) => (
                                <option key={year} value={year}>{year}</option>
                            ))}
                        </select>
                    </div>

                    {/* ✅ เลือกเดือน */}
                    <div className="col-md-3">
                        <label className="form-label">เลือกเดือน</label>
                        <select 
                            className="form-select" 
                            value={selectedMonth} 
                            onChange={(e) => setSelectedMonth(Number(e.target.value))}
                        >
                            {THAI_MONTHS.map((month, index) => (
                                <option key={index + 1} value={index + 1}>
                                    {`${month}`}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="col-md-2 d-flex align-items-end">
                        <button className="btn btn-primary w-100" onClick={fetchPayouts}>
                            🔍 ค้นหา
                        </button>
                    </div>
                </div>
            </div>
            
            {/* แสดงข้อมูลรายจ่าย */}
            <div className="card p-3 shadow">
                <h5>📋 รายจ่าย ประจำเดือน {THAI_MONTHS[selectedMonth - 1]} {selectedYear}</h5>
                {loading ? (
                    <div className="text-center py-3">⏳ กำลังโหลดข้อมูล...</div>
                ) : (
                    <table className="table table-hover">
                        <thead className="table-dark">
                            <tr>
                                <th>วันที่</th>
                                <th>ประเภท</th>
                                <th>รายละเอียด</th>
                                <th>จำนวนเงิน</th>
                            </tr>
                        </thead>
                        <tbody>
                            {payouts.length > 0 ? (
                                payouts.map((payout, index) => (
                                    <tr key={index}>
                                        <td>{new Date(payout.payout_date).toLocaleDateString()}</td>
                                        <td>{payout.payout_type}</td>
                                        <td>{payout.description}</td>
                                        <td>{parseFloat(payout.amount).toFixed(2)} ฿</td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="4" className="text-center text-muted">ไม่มีข้อมูลรายจ่าย</td>
                                </tr>
                            )}
                        </tbody>
                        {payouts.length > 0 && (
                            <tfoot>
                                <tr className="table-dark">
                                    <td colSpan="3" className="text-end fw-bold">รวม</td>
                                    <td className="fw-bold">{totalAmount.toFixed(2)} ฿</td>
                                </tr>
                            </tfoot>
                        )}
                    </table>
                )}
            </div>

            {/* Modal เพิ่ม/แก้ไข รายจ่าย */}
            {showModal && (
                <div className="modal show d-block" tabIndex="-1">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h4 className="modal-title">{form.id ? "🔄 อัปเดตรายจ่าย" : "➕ เพิ่มรายจ่าย"}</h4>
                                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={handleSubmit}>
                                    <div className="mb-3">
                                        <label className="form-label">ประเภท</label>
                                        <select className="form-control" value={form.payout_type} onChange={(e) => setForm({ ...form, payout_type: e.target.value })} required>
                                            <option value="">-- เลือกประเภท --</option>
                                            <option value="รายเดือน">รายเดือน</option>
                                            <option value="อุปกรณ์-น้ำยา">อุปกรณ์-น้ำยา</option>
                                            <option value="อื่นๆ">อื่นๆ</option>
                                        </select>
                                    </div>
                                    <button type="submit" className="btn btn-success w-100">{form.id ? "🔄 อัปเดต" : "➕ เพิ่ม"}</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Payouts;
