import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getEmployees, createEmployee, updateEmployee, deleteEmployee, getBranches } from "../services/api";
import AddEmployeeModal from "../components/AddEmployeeModal";
import { jwtDecode } from "jwt-decode";
import "../assets/css/bootstrap.min.css";

const EmployeeManagement = () => {
    const navigate = useNavigate();
    const [employees, setEmployees] = useState([]);
    const [branches, setBranches] = useState([]);
    const [selectedBranch, setSelectedBranch] = useState("");
    const [isSuperAdmin, setIsSuperAdmin] = useState(false);
    const [employeeId, setEmployeeId] = useState(null);
    const [showAddModal, setShowAddModal] = useState(false);
    const [editEmployee, setEditEmployee] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login");
            return;
        }

        try {
            const user = jwtDecode(token);
            setIsSuperAdmin(user.isSuperAdmin);
            setEmployeeId(user.id);
            setSelectedBranch(user.isSuperAdmin ? "" : user.branch_id);

            if (user.isSuperAdmin) fetchBranches();
        } catch (error) {
            console.error("🔴 Error decoding token:", error);
        }
    }, []);

    useEffect(() => {
        if (selectedBranch || isSuperAdmin) {
            fetchEmployees();
        }
    }, [selectedBranch]);

    const fetchBranches = async () => {
        try {
            const res = await getBranches();
            setBranches(res.data);
        } catch (error) {
            console.error("🔴 Error fetching branches:", error);
        }
    };

    const fetchEmployees = async () => {
        try {
            const res = await getEmployees(selectedBranch);
            const filteredEmployees = res.data.filter(emp => emp.id !== employeeId); // ✅ ไม่แสดงตัวเอง
            setEmployees(filteredEmployees);
        } catch (error) {
            console.error("🔴 Error fetching employees:", error);
        }
    };

    const handleCreateOrUpdateEmployee = async (employeeData) => {
        try {
            employeeData.branch_id = isSuperAdmin ? employeeData.branch_id : selectedBranch;
            if (!employeeData.name || !employeeData.email || !employeeData.phone || !employeeData.role || !employeeData.branch_id) {
                alert("❌ กรุณากรอกข้อมูลให้ครบถ้วน");
                return;
            }

            let response;
            if (employeeData.id) {
                response = await updateEmployee(employeeData.id, employeeData);
            } else {
                response = await createEmployee(employeeData);
            }

            if (response.status === 201 || response.status === 200) {
                alert("✅ บันทึกสำเร็จ!");
                fetchEmployees();
                setShowAddModal(false);
                setEditEmployee(null);
            } else {
                alert("❌ ไม่สามารถบันทึกข้อมูลได้");
            }
        } catch (error) {
            console.error("🔴 Error saving employee:", error);
            alert("❌ เกิดข้อผิดพลาด ไม่สามารถบันทึกข้อมูลพนักงานได้");
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบพนักงานนี้?")) {
            return;
        }

        try {
            const response = await deleteEmployee(id);

            if (response.status === 200) {
                alert("✅ ลบพนักงานเรียบร้อย");
                fetchEmployees();
            } else {
                alert("❌ ลบพนักงานไม่สำเร็จ!");
            }
        } catch (error) {
            console.error("🔴 Error deleting employee:", error);
            alert("❌ เกิดข้อผิดพลาด ไม่สามารถลบพนักงานได้");
        }
    };

    return (
        <div className="container mt-4">
            <h2>👥 ระบบจัดการพนักงาน</h2>

            <div className="card p-3 mb-3 shadow">
                <div className="row">
                    {isSuperAdmin && (
                        <div className="col-md-4">
                            <label className="form-label">เลือกสาขา</label>
                            <select 
                                className="form-select"
                                value={selectedBranch}
                                onChange={(e) => setSelectedBranch(e.target.value)}
                            >
                                <option value="">-- ทุกสาขา --</option>
                                {branches.map(branch => (
                                    <option key={branch.id} value={branch.id}>
                                        {branch.name}
                                    </option>
                                ))}
                            </select>
                        </div>
                    )}
                    <div className="col-md-4 d-flex align-items-end">
                        <button className="btn btn-primary w-100" onClick={() => setShowAddModal(true)}>
                            ➕ เพิ่มพนักงาน
                        </button>
                    </div>
                </div>
            </div>

            {/* ตารางพนักงาน */}
            <div className="card p-3 shadow">
                <h5>📋 รายชื่อพนักงาน</h5>
                <table className="table table-hover">
                    <thead className="table-dark">
                        <tr>
                            <th>ชื่อ</th>
                            <th>อีเมล</th>
                            <th>เบอร์ติดต่อ</th>
                            <th>ตำแหน่ง</th>
                            <th>สาขา</th>
                            <th>จัดการ</th>
                        </tr>
                    </thead>
                    <tbody>
                        {employees.length > 0 ? (
                            employees.map(employee => (
                                <tr key={employee.id}>
                                    <td>{employee.name}</td>
                                    <td>{employee.email}</td>
                                    <td>{employee.phone}</td>
                                    <td>{employee.role}</td>
                                    <td>{employee.branch_name || "-"}</td>
                                    <td>
                                        <button className="btn btn-warning btn-sm" onClick={() => { setEditEmployee(employee); setShowAddModal(true); }}>✏️ แก้ไข</button>
                                        {isSuperAdmin && (
                                            <button className="btn btn-danger btn-sm ms-2" onClick={() => handleDelete(employee.id)}>❌ ลบ</button>
                                        )}
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="6" className="text-center text-muted">ไม่มีข้อมูลพนักงาน</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>

            {/* Modal เพิ่ม/แก้ไขพนักงาน */}
            {showAddModal && (
                <AddEmployeeModal
                    show={showAddModal}
                    onClose={() => {
                        setShowAddModal(false);
                        setEditEmployee(null);
                    }}
                    onSave={handleCreateOrUpdateEmployee}
                    employeeData={editEmployee}
                />
            )}
        </div>
    );
};

export default EmployeeManagement;
