import React, { useState } from "react";
import "../assets/css/bootstrap.min.css";

const UpdateQueueStatusModal = ({ show, handleClose, handleUpdate, queueId, currentStatus }) => {
    const statusOptions = [
        { value: "รับเข้า", label: "รับเข้า" },
        { value: "อยู่ระหว่างทำความสะอาด", label: "อยู่ระหว่างทำความสะอาด" },
        { value: "เตรียมส่ง", label: "เตรียมส่ง" },
        { value: "กำลังจัดส่ง", label: "กำลังจัดส่ง" },
        { value: "จัดส่งสำเร็จ", label: "จัดส่งสำเร็จ" }
    ];

    // ค้นหาสถานะปัจจุบัน และตั้งค่า default เป็นสถานะถัดไป
    const currentIndex = statusOptions.findIndex(option => option.value === currentStatus);
    const defaultStatus = currentIndex >= 0 && currentIndex < statusOptions.length - 1 ? statusOptions[currentIndex + 1].value : currentStatus;
    const [status, setStatus] = React.useState(defaultStatus);

    React.useEffect(() => {
        setStatus(defaultStatus);
    }, [currentStatus]);

    const handleSubmit = () => {
        handleUpdate(queueId, status);
        handleClose();
    };

    return (
        <Modal show={show} onHide={handleClose} centered>
            <Modal.Header closeButton>
                <Modal.Title>อัปเดตสถานะคิว</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group>
                        <Form.Label>สถานะคิว</Form.Label>
                        <Form.Select value={status} onChange={(e) => setStatus(e.target.value)}>
                            {statusOptions.map(option => (
                                <option key={option.value} value={option.value}>{option.label}</option>
                            ))}
                        </Form.Select>
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    ปิด
                </Button>
                <Button variant="primary" onClick={handleSubmit}>
                    บันทึกการเปลี่ยนแปลง
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default UpdateQueueStatusModal;
