import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAppointments, createAppointment, updateAppointmentStatus, updateAppointment } from '../services/api';
import './AppointmentsAdmin.css';

const AppointmentsAdmin = () => {
  const navigate = useNavigate();
  const [appointments, setAppointments] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [phone, setPhone] = useState('');
  const [location, setLocation] = useState('');
  const [shoeCount, setShoeCount] = useState(1);
  const [appointmentDate, setAppointmentDate] = useState('');
  const [appointmentTime, setAppointmentTime] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateFilter, setDateFilter] = useState('');
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await getAppointments();
      setAppointments(res.data);
    } catch (error) {
      console.error("🔴 Error fetching appointments:", error);
    }
  };

  const handleCreateOrUpdateAppointment = async () => {
    if (!customerName || !phone || !location || !shoeCount || !appointmentDate || !appointmentTime) {
      alert("กรุณากรอกข้อมูลให้ครบถ้วน");
      return;
    }

    try {
      const newAppointment = {
        customer_name: customerName,
        phone,
        location,
        shoe_count: shoeCount,
        appointment_date: appointmentDate,
        appointment_time: appointmentTime,
      };
      
      if (editId) {
        await updateAppointment(editId, newAppointment); // ✅ ใช้ API อัปเดตข้อมูล
        setEditId(null);
      } else {
        await createAppointment(newAppointment);
      }
      fetchAppointments();
      setCustomerName('');
      setPhone('');
      setLocation('');
      setShoeCount(1);
      setAppointmentDate('');
      setAppointmentTime('');
    } catch (error) {
      console.error("🔴 Error saving appointment:", error);
    }
  };

  const handleEdit = (appointment) => {
    setEditId(appointment.id);
    setCustomerName(appointment.customer_name);
    setPhone(appointment.phone);
    setLocation(appointment.location);
    setShoeCount(appointment.shoe_count);
    setAppointmentDate(appointment.appointment_date);
    setAppointmentTime(appointment.appointment_time);
  };

  const handleStatusUpdate = async (id, newStatus) => {
    try {
      await updateAppointmentStatus(id, newStatus);
      fetchAppointments();
    } catch (error) {
      console.error("🔴 Error updating status:", error);
    }
  };

  return (
    <div className="appointments-container">
      <h1>📅 รายการนัดหมาย</h1>
      <table>
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>เบอร์โทร</th>
            <th>สถานที่</th>
            <th>จำนวนรองเท้า</th>
            <th>วันที่</th>
            <th>เวลา</th>
            <th>สถานะ</th>
            <th>จัดการ</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map(appt => (
            <tr key={appt.id}>
              <td>{appt.customer_name}</td>
              <td>{appt.phone}</td>
              <td>{appt.location}</td>
              <td>{appt.shoe_count}</td>
              <td>{appt.appointment_date}</td>
              <td>{appt.appointment_time}</td>
              <td>{appt.status}</td>
              <td>
                <button onClick={() => handleEdit(appt)}>✏️ แก้ไข</button>
                {appt.status !== 'สำเร็จ' && (
                  <button onClick={() => handleStatusUpdate(appt.id, appt.status === 'รอดำเนินการ' ? 'ยืนยันแล้ว' : 'สำเร็จ')}>
                    อัปเดต
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>{editId ? "✏️ แก้ไขนัดหมาย" : "➕ เพิ่มนัดหมาย"}</h2>
      <input type="text" placeholder="ชื่อ" value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
      <input type="text" placeholder="เบอร์โทร" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <input type="text" placeholder="สถานที่" value={location} onChange={(e) => setLocation(e.target.value)} />
      <input type="number" placeholder="จำนวนรองเท้า" value={shoeCount} onChange={(e) => setShoeCount(e.target.value)} />
      <input type="date" value={appointmentDate} onChange={(e) => setAppointmentDate(e.target.value)} />
      <input type="time" value={appointmentTime} onChange={(e) => setAppointmentTime(e.target.value)} />
      <button onClick={handleCreateOrUpdateAppointment}>{editId ? "บันทึกการแก้ไข" : "บันทึกนัดหมาย"}</button>
    </div>
  );
};

export default AppointmentsAdmin;
