import axios from 'axios';
import { data } from 'react-router-dom';

// ✅ ตั้งค่า API Base URL
const API = axios.create({
  baseURL: 'http://localhost:5000/api',
});

//-------Auth----------
// ✅ ล็อกอินพนักงาน (Employee)
export const loginEmployee = (email, password) => API.post("/auth/login", { email, password });

// ✅ ล็อกอินแอดมิน (Admin)
export const loginAdmin = (email, password) => API.post("/auth/admin/login", { email, password });

// ✅ สร้าง Admin ใหม่
export const createAdmin = (email, password) => API.post("/auth/create-admin", { email, password });

// ✅ ดึงข้อมูลผู้ใช้ (อาจใช้ได้เฉพาะ Admin)
export const getUsers = async () => {
  return await API.get("/users");
};
//-------Appointments----------
export const getAppointments = () => API.get('/appointments');
export const createAppointment = (appointmentData) => API.post('/appointments/create', appointmentData);
export const updateAppointmentStatus = (id, status) => API.put(`/appointments/${id}/status`, { status });
export const updateAppointment = (id, updatedData) => API.put(`/appointments/${id}`, updatedData);
export const deleteAppointment = (id) => API.delete(`/appointments/${id}`);

//-------Queue----------
export const createQueue = (queueData) => API.post("/queue", queueData);
export const getQueue = () => API.get("/queue");
//export const getQueueDetail = (id) => API.get(`/queue/${id}`);
export const updateQueueStatus = (id, status, total_price) => API.put(`/queue/${id}/status`, { status, total_price });
export const deleteQueue = (id) => API.delete(`/queue/${id}`);

// ✅ เพิ่มบริการเข้า queue_items
// ✅ เพิ่มรองเท้าพร้อมรูปภาพ
export const addQueueItems = (queue_id, shoeData) => {
  const formData = new FormData();

  // ✅ ตรวจสอบค่าก่อนเพิ่มลง FormData
  console.log("📌 Debug: shoeData before appending", shoeData);

  formData.append("queue_id", queue_id);
  formData.append("service_id", shoeData.get("service_id") || "");
  formData.append("price_per_pair", shoeData.get("price_per_pair") || "0");
  formData.append("brand", shoeData.get("brand") || "");
  formData.append("model", shoeData.get("model") || "");
  formData.append("color", shoeData.get("color") || "");
  formData.append("notes", shoeData.get("notes") || "");

  // ✅ เพิ่มไฟล์ภาพลงใน FormData (ถ้ามี)
  if (shoeData.get("front")) formData.append("image_front", shoeData.get("front"));
  if (shoeData.get("back")) formData.append("image_back", shoeData.get("back"));
  if (shoeData.get("left")) formData.append("image_left", shoeData.get("left"));
  if (shoeData.get("right")) formData.append("image_right", shoeData.get("right"));
  if (shoeData.get("top")) formData.append("image_top", shoeData.get("top"));
  if (shoeData.get("bottom")) formData.append("image_bottom", shoeData.get("bottom"));

  // ✅ ตรวจสอบค่าที่อยู่ใน FormData
  console.log("📌 Debug: FormData Entries");
  for (let pair of formData.entries()) {
    console.log(`${pair[0]}:`, pair[1]);
  }

  // ✅ ส่งข้อมูลไปยัง API โดยใช้ multipart/form-data
  return API.post(`/queue/${queue_id}/items`, formData, {   
    headers: { "Content-Type": "multipart/form-data" },
  });
};

// การอัพโหลดรูป Ater
export const uploadAfterImages = async (queue_id, item_id, afterImages) => {
  const formData = new FormData();
  
  // ✅ Loop เพิ่มไฟล์ลงใน FormData
  Object.keys(afterImages).forEach((key) => {
    if (afterImages[key]) {
        formData.append(`image_after_${key}`, afterImages[key]); // ✅ ต้องตรงกับ Backend
    }
});

  // ✅ Debug เช็คค่าที่ถูกส่งไป API
  console.log("📌 Debug: FormData Entries (uploadAfterImages)");
  for (let pair of formData.entries()) {
      console.log(`${pair[0]}:`, pair[1]);
  }

  try {
      const response = await API.post(`/queue/${queue_id}/items/${item_id}/upload-after`, formData, {
          headers: { "Content-Type": "multipart/form-data" }
      });

      console.log("📌 Debug: API Response", response.data);
      return response.data;
  } catch (error) {
      console.error("🔴 Error in uploadAfterImages:", error.response?.data || error.message);
      throw error;
  }
};




// ✅ ดึงรายการบริการใน queue_items ตาม queue_id
export const getQueueItems = (queue_id) => API.get(`/queue/${queue_id}/items`);

// ✅ ดึงข้อมูลรายละเอียดคิว
export const getQueueDetail = (id) => API.get(`/queue/${id}`);

// ✅ ดึงบริการทั้งหมด
export const getServices = () => API.get('/services');

// ✅ อัปเดตรายละเอียดคิว (เพิ่ม-ลบ บริการ)
export const updateQueue = async (id, updatedData) => {
  console.log("📌 Debug: Sending to API", `/queue/${id}`, updatedData);
  return API.put(`/queue/${id}`, updatedData);
};

// ✅ ลบบริการออกจากคิว
export const deleteQueueItem = (queue_id, item_id) => API.delete(`/queue/${queue_id}/items/${item_id}`);


// ✅ ฟังก์ชันสร้างใบแจ้งราคา
export const generateInvoice = (queue_id) => API.post(`/queue/${queue_id}/generate-invoice`);

/*-------------Services---------------*/
// ✅ ดึงข้อมูลบริการทั้งหมด
export const getAllServices = () => API.get("/services");

// ✅ ดึงข้อมูลบริการตาม ID
export const getServiceById = (id) => API.get(`/services/${id}`);

// ✅ เพิ่มบริการใหม่
export const createService = (serviceData) => API.post("/services", serviceData);

// ✅ แก้ไขบริการ
export const updateService = (id, serviceData) => API.put(`/services/${id}`, serviceData);

// ✅ ลบบริการ
export const deleteService = (id) => API.delete(`/services/${id}`);

/*-------------Payment---------------*/
// ✅ ดึงคิวที่ต้องชำระเงิน
export const getCompletedQueues = () => API.get("/payments/completed-queues");

// ✅ บันทึกการชำระเงิน 
export const processPayment = (queue_id, additional_fees,discount, total_amount, payment_method) => {
    return API.post("/payments", { queue_id, additional_fees,discount, total_amount, payment_method });
};

// ✅ ดึงข้อมูลการชำระเงินของ Queue
export const getPaymentByQueueId = (queue_id) => API.get(`/payments/${queue_id}`);

//ดึงยอดรวมรายได้
export const getRevenue = () => API.get('/revenue');


/*-------------Branch---------------*/
// ✅ ดึงรายชื่อสาขาทั้งหมด
export const getBranches = () => API.get("/branches");

// ✅ เพิ่มสาขาใหม่
export const createBranch = (branchData) => API.post("/branches", branchData);

// ✅ อัปเดตข้อมูลสาขา
export const updateBranch = (id, branchData) => API.put(`/branches/${id}`, branchData);

// ✅ ลบสาขา
export const deleteBranch = (id) => API.delete(`/branches/${id}`);

/*-------------Employee---------------*/
// ✅ ดึงรายชื่อพนักงานทั้งหมด
export const getEmployees = () => API.get("/employees");

// ✅ เพิ่มพนักงานใหม่
export const createEmployee = (employeeData) => API.post("/employees", employeeData);

// ✅ อัปเดตข้อมูลพนักงาน
export const updateEmployee = (id, employeeData) => API.put(`/employees/${id}`, employeeData);

// ✅ ลบพนักงาน
export const deleteEmployee = (id) => API.delete(`/employees/${id}`);
