import React, { useState } from "react";

const ShoeDetailModal = ({ show, onClose, shoe }) => {
    if (!show || !shoe) return null;

    const Backend_URL = 'http://localhost:5000'; // ‡∏î‡∏∂‡∏á URL Backend

    return (
        <div className="modal-overlay" >
            <div className="modal-content" style={{ backgroundColor: "#fff"}}>
                <h2>üëü ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤</h2>
                
                {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤ */}
                <p><strong>‡πÅ‡∏ö‡∏£‡∏ô‡∏î‡πå:</strong> {shoe.brand}</p>
                <p><strong>‡∏£‡∏∏‡πà‡∏ô:</strong> {shoe.model}</p>
                <p><strong>‡∏™‡∏µ:</strong> {shoe.color}</p>
                <p><strong>‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏´‡∏ï‡∏∏:</strong> {shoe.notes || "-"}</p>

                {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏π‡∏õ Before & After */}
                <h3>üì∑ ‡∏†‡∏≤‡∏û Before & After</h3>
                <table className="shoe-images-table">
                    <thead className="dark">
                        <tr>
                            <th>‡∏ä‡πá‡∏≠‡∏ï</th>
                            <th>Before</th>
                            <th>After</th>
                        </tr>
                    </thead>
                    <tbody>
                        {["front", "back", "left", "right", "top", "bottom"].map((position) => (
                            <tr key={position}>
                                <td><strong>{position.toUpperCase()}</strong></td>
                                <td>
                                    {shoe[`image_before_${position}`] ? (
                                        <img src={`${Backend_URL}${shoe[`image_before_${position}`]}`} alt={`before-${position}`} width="100" />
                                    ) : "-"}
                                </td>
                                <td>
                                    {shoe[`image_after_${position}`] ? (
                                        <img src={`${Backend_URL}${shoe[`image_after_${position}`]}`} alt={`after-${position}`} width="100" />
                                    ) : "-"}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {/* ‡∏õ‡∏∏‡πà‡∏°‡∏õ‡∏¥‡∏î Modal */}
                <div className="modal-actions">
                    <button onClick={onClose}>‚ùå ‡∏õ‡∏¥‡∏î</button>
                </div>
            </div>
        </div>
    );
};

export default ShoeDetailModal;
