import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getQueueDetail, getServices, updateQueue, deleteQueueItem } from '../services/api';
import './QueueDetail.css';

const QueueDetail = () => {
    const navigate = useNavigate();
    const { queue_id } = useParams();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [selectedService, setSelectedService] = useState('');
    const [pairCount, setPairCount] = useState(1);
    
    useEffect(() => {
        fetchQueueDetail();
        fetchServices();
    }, []);

    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(queue_id);
            setQueue(res.data);
        } catch (error) {
            console.error("🔴 Error fetching queue detail:", error);
        }
    };

    const fetchServices = async () => {
        try {
            const res = await getServices();
            setServices(res.data);
        } catch (error) {
            console.error("🔴 Error fetching services:", error);
        }
    };

    const handleAddService = async () => {
        if (!selectedService || pairCount < 1) {
            alert("กรุณาเลือกบริการและระบุจำนวนคู่รองเท้าให้ถูกต้อง");
            return;
        }

        try {
            const updatedData = { service_id: selectedService, pair_count: pairCount };
            await updateQueue(queue_id, updatedData);
            fetchQueueDetail(); // โหลดข้อมูลใหม่
        } catch (error) {
            console.error("🔴 Error adding service:", error);
        }
    };

    const handleDeleteService = async (service_id) => {
        if (!window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบบริการนี้?")) return;

        try {
            await deleteQueueItem(queue_id, service_id);
            fetchQueueDetail();
        } catch (error) {
            console.error("🔴 Error deleting service:", error);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    if (!queue) return <p>⏳ กำลังโหลดข้อมูล...</p>;

    return (
        <div className="queue-detail-container">
            <h1>🏷️ Queue Detail (#{queue_id})</h1>

            <div className="queue-info">
                <p>👤 ลูกค้า: {queue.customer_name}</p>
                <p>📞 เบอร์โทร: {queue.phone}</p>
                <p>📍 สถานที่: {queue.location}</p>
                <p>👟 จำนวนคู่: {queue.total_pairs}</p>
                <p>📅 รับเข้า: {formatDate(queue.received_date)}</p>
                <p>🚚 กำหนดส่งคืน: {formatDate(queue.delivery_date)}</p>
            </div>

            <h2>📋 รายการบริการ (Services)</h2>
            <table>
                <thead>
                    <tr>
                        <th>🏷️ บริการ</th>
                        <th>👟 คู่</th>
                        <th>💰 ราคา/คู่</th>
                        <th>🗑️ ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {queue.services.map(service => (
                        <tr key={service.service_id}>
                            <td>{service.service_name}</td>
                            <td>{service.pair_count}</td>
                            <td>{service.price_per_pair} บาท</td>
                            <td>
                                <button onClick={() => handleDeleteService(service.service_id)}>❌</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h3>➕ เพิ่มบริการ</h3>
            <div className="service-selection">
                <select value={selectedService} onChange={(e) => setSelectedService(e.target.value)}>
                    <option value="">เลือกบริการ</option>
                    {services.map(service => (
                        <option key={service.id} value={service.id}>{service.service_name} ({service.base_price} บาท/คู่)</option>
                    ))}
                </select>
                <input type="number" min="1" value={pairCount} onChange={(e) => setPairCount(Number(e.target.value))} />
                <button onClick={handleAddService}>เพิ่ม</button>
            </div>

            <div className="queue-actions">
                <button onClick={() => navigate('/queue')}>🔄 กลับไปหน้าคิว</button>
                <button onClick={fetchQueueDetail}>✅ บันทึกการเปลี่ยนแปลง</button>
            </div>
        </div>
    );
};

export default QueueDetail;
