import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getQueueDetail, getServices, deleteQueueItem } from '../services/api';
import '../assets/css/bootstrap.min.css';
import './QueueDetail.css';

import AddShoeModal from "../components/AddShoeModal";
import ShoeDetailModal from "../components/ShoeDetailModal";

const QueueDetail = () => {
    const navigate = useNavigate();
    const { queue_id } = useParams();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [showAddShoeModal, setShowAddShoeModal] = useState(false);
    const [showShoeDetailModal, setShowShoeDetailModal] = useState(false);
    const [selectedShoe, setSelectedShoe] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) navigate('/login');
        fetchQueueDetail();
        fetchServices();
    }, []);

    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(queue_id);
            setQueue(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching queue detail:", error);
        }
    };

    const fetchServices = async () => {
        try {
            const res = await getServices();
            setServices(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching services:", error);
        }
    };

    const handleRemoveShoe = async (queue_item_id) => {
        if (!window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡∏ô‡∏µ‡πâ?")) return;
        try {
            await deleteQueueItem(queue_id, queue_item_id);
            alert("‚úÖ ‡∏•‡∏ö‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢!");
            fetchQueueDetail();
        } catch (error) {
            console.error("üî¥ Error deleting shoe:", error);
        }
    };

    if (!queue) return <p>‚è≥ ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•...</p>;

    return (
        <div className="container mt-4">
            {/* ‡∏õ‡∏∏‡πà‡∏° "‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤‡∏Ñ‡∏¥‡∏ß" */}
            <div className="d-flex justify-content-between mb-3">
                <button className="btn btn-secondary" onClick={() => navigate('/queue')}>
                    ‚¨ÖÔ∏è ‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤‡∏Ñ‡∏¥‡∏ß
                </button>
            </div>

            {/* ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏Ñ‡∏¥‡∏ß */}
            <div className="card p-3 shadow mb-4">
                <h4>üè∑Ô∏è ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏Ñ‡∏¥‡∏ß #{queue_id}</h4>
                <p>üë§ ‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤: <strong>{queue.customer_name}</strong></p>
                <p>üìû ‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£: {queue.phone}</p>
                <p>üìç ‡∏™‡∏ñ‡∏≤‡∏ô‡∏ó‡∏µ‡πà: {queue.location}</p>
                <p>üìÖ ‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤: {queue.received_date}</p>
                <p>üöö ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏™‡πà‡∏á‡∏Ñ‡∏∑‡∏ô: {queue.delivery_date}</p>
            </div>

            {/* ‚úÖ ‡∏õ‡∏∏‡πà‡∏° "‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤" ‡∏≠‡∏¢‡∏π‡πà‡∏ö‡∏ô‡∏ï‡∏≤‡∏£‡∏≤‡∏á */}
            <div className="d-flex justify-content-between align-items-center mb-2">
                <h4>üëü ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤</h4>
                <button className="btn btn-primary" onClick={() => setShowAddShoeModal(true)}>
                    ‚ûï ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤
                </button>
            </div>

            {/* ‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤ */}
            <table className="table table-bordered table-striped">
                <thead>
                    <tr>
                        <th>‡πÅ‡∏ö‡∏£‡∏ô‡∏î‡πå</th>
                        <th>‡∏£‡∏∏‡πà‡∏ô</th>
                        <th>‡∏™‡∏µ</th>
                        <th>‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏´‡∏ï‡∏∏</th>
                        <th>BEFORE</th>
                        <th>AFTER</th>
                        <th>‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î</th>
                        <th>‡∏•‡∏ö</th>
                    </tr>
                </thead>
                <tbody>
                    {queue.queue_items.map((item, index) => (
                        <tr key={index}>
                            <td>{item.brand}</td>
                            <td>{item.model}</td>
                            <td>{item.color}</td>
                            <td>{item.notes}</td>
                            <td>
                                {item.image_before_front && (
                                    <img src={`http://localhost:5000${item.image_before_front}`} alt={`shoe-${index}`} width="50" />
                                )}
                            </td>
                            <td>
                                {item.image_after_front ? (
                                    <img src={`http://localhost:5000${item.image_after_front}`} alt={`after-${index}`} width="50" />
                                ) : (
                                    <button className="btn btn-warning btn-sm">
                                        Upload
                                    </button>
                                )}
                            </td>
                            <td>
                                <button className="btn btn-info btn-sm" onClick={() => setSelectedShoe(item)}>
                                    ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î
                                </button>
                            </td>
                            <td>
                                <button className="btn btn-danger btn-sm" onClick={() => handleRemoveShoe(item.queue_item_id)}>
                                    ‚ùå
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Modal ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤ */}
            {showAddShoeModal && (
                <AddShoeModal 
                    show={showAddShoeModal} 
                    onClose={() => setShowAddShoeModal(false)} 
                    services={services} 
                    onAddShoe={fetchQueueDetail} 
                />
            )}

            {/* Modal ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤ */}
            {showShoeDetailModal && (
                <ShoeDetailModal 
                    show={showShoeDetailModal} 
                    onClose={() => setShowShoeDetailModal(false)} 
                    shoe={selectedShoe} 
                />
            )}
        </div>
    );
};

export default QueueDetail;
