            setShowModal(false); // ‚úÖ ‡∏õ‡∏¥‡∏î Modal
        } catch (error) {
            console.error("üî¥ Error saving service:", error);
        }
    };
    

    const handleEdit = (service) => {
        setForm(service);
        setShowModal(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏ö‡∏£‡∏¥‡∏Å‡∏≤‡∏£‡∏ô‡∏µ‡πâ?")) {
