import React, { useState } from "react";

const AddShoeModal = ({ show, onClose, onAddShoe, services }) => {
  const [shoeData, setShoeData] = useState({
    service_id: "",
    brand: "",
    model: "",
    color: "",
    notes: "",
    images: { front: null, back: null, left: null, right: null, top: null, bottom: null }
  });

  const handleImageUpload = (e, position) => {
    const file = e.target.files[0];
    if (file) {
      setShoeData((prev) => ({ ...prev, images: { ...prev.images, [position]: file } }));
    }
  };

  const handleSubmit = () => {
    if (!shoeData.service_id || !shoeData.brand || !shoeData.model || !shoeData.color) {
      alert("❌ กรุณากรอกข้อมูลให้ครบถ้วน");
      return;
    }
    onAddShoe(shoeData); // ส่งค่ากลับไปหน้า QueueDetail
    onClose(); // ปิด Modal
  };

  if (!show) return null; // ถ้า show เป็น false ไม่ต้องแสดง Modal

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>➕ เพิ่มรองเท้า</h2>
        <select value={shoeData.service_id} onChange={(e) => setShoeData({ ...shoeData, service_id: e.target.value })}>
          <option value="">เลือกบริการ</option>
          {services.map((service) => (
            <option key={service.id} value={service.id}>
              {service.service_name} ({service.base_price} บาท/คู่)
            </option>
          ))}
        </select>
        <input type="text" placeholder="แบรนด์รองเท้า" value={shoeData.brand} onChange={(e) => setShoeData({ ...shoeData, brand: e.target.value })} />
        <input type="text" placeholder="รุ่นรองเท้า" value={shoeData.model} onChange={(e) => setShoeData({ ...shoeData, model: e.target.value })} />
        <input type="text" placeholder="สี" value={shoeData.color} onChange={(e) => setShoeData({ ...shoeData, color: e.target.value })} />
        <textarea placeholder="หมายเหตุเพิ่มเติม" value={shoeData.notes} onChange={(e) => setShoeData({ ...shoeData, notes: e.target.value })}></textarea>

        <h3>📷 อัปโหลดรูปภาพ</h3>
        <div className="image-upload">
          {["front", "back", "left", "right", "top", "bottom"].map((position, index) => (
            <div key={index} className="image-upload-box">
              <label>{`รูปภาพ ${position}`}</label>
              <input type="file" accept="image/*" onChange={(e) => handleImageUpload(e, position)} />
              {shoeData.images[position] && <img src={URL.createObjectURL(shoeData.images[position])} alt={`shoe-${position}`} width="50" />}
            </div>
          ))}
        </div>

        <div className="modal-actions">
          <button onClick={handleSubmit}>✅ เพิ่มรองเท้า</button>
          <button onClick={onClose}>❌ ปิด</button>
        </div>
      </div>
    </div>
  );
};

export default AddShoeModal;
