import React, { useState, useEffect } from "react";
import { getPayouts, createPayout, updatePayout, deletePayout } from "../services/api";
import "bootstrap/dist/css/bootstrap.min.css";

const Payouts = () => {
    const [payouts, setPayouts] = useState([]);
    const [form, setForm] = useState({ id: null, payout_type: "", description: "", amount: "", notes: "", payout_date: "" });
    const [showModal, setShowModal] = useState(false);
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [loading, setLoading] = useState(false);
    
    useEffect(() => {
        fetchPayouts();
    }, []);

    const fetchPayouts = async () => {
        try {
            setLoading(true);
            const res = await getPayouts(startDate, endDate);
            setPayouts(res.data);
        } catch (error) {
            console.error("🔴 Error fetching payouts:", error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (form.id) {
                await updatePayout(form.id, form);
                alert("✅ อัปเดตรายจ่ายเรียบร้อย!");
            } else {
                await createPayout(form);
                alert("✅ เพิ่มรายจ่ายเรียบร้อย!");
            }
            fetchPayouts();
            setShowModal(false);
            setForm({ id: null, payout_type: "", description: "", amount: "", notes: "", payout_date: "" });
        } catch (error) {
            console.error("🔴 Error saving payout:", error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบรายการนี้?")) {
            try {
                await deletePayout(id);
                alert("✅ ลบรายการเรียบร้อย!");
                fetchPayouts();
            } catch (error) {
                console.error("🔴 Error deleting payout:", error);
            }
        }
    };

    return (
        <div className="container mt-4">
            <h1>💸 รายการรายจ่าย</h1>
            <div className="d-flex justify-content-between mb-3">
                <button className="btn btn-primary" onClick={() => setShowModal(true)}>➕ เพิ่มรายการ</button>
            </div>
            
            <table className="table table-hover">
                <thead className="table-dark">
                    <tr>
                        <th>วันที่</th>
                        <th>ประเภท</th>
                        <th>รายละเอียด</th>
                        <th>จำนวนเงิน</th>
                        <th>หมายเหตุ</th>
                        <th>แก้ไข</th>
                        <th>ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {loading ? (
                        <tr><td colSpan="7" className="text-center">⏳ กำลังโหลดข้อมูล...</td></tr>
                    ) : payouts.length > 0 ? (
                        payouts.map((payout) => (
                            <tr key={payout.id}>
                                <td>{new Date(payout.payout_date).toLocaleDateString()}</td>
                                <td>{payout.payout_type}</td>
                                <td>{payout.description}</td>
                                <td>{payout.amount} ฿</td>
                                <td>{payout.notes}</td>
                                <td><button className="btn btn-warning btn-sm" onClick={() => { setForm(payout); setShowModal(true); }}>✏️</button></td>
                                <td><button className="btn btn-danger btn-sm" onClick={() => handleDelete(payout.id)}>❌</button></td>
                            </tr>
                        ))
                    ) : (
                        <tr><td colSpan="7" className="text-center text-muted">ไม่มีข้อมูล</td></tr>
                    )}
                </tbody>
            </table>

            {showModal && (
                <div className="modal show d-block" tabIndex="-1">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h4 className="modal-title">{form.id ? "🔄 อัปเดตรายจ่าย" : "➕ เพิ่มรายจ่าย"}</h4>
                                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={handleSubmit}>
                                    <div className="mb-3">
                                        <label className="form-label">ประเภท</label>
                                        <select className="form-control" value={form.payout_type} onChange={(e) => setForm({ ...form, payout_type: e.target.value })} required>
                                            <option value="">-- เลือกประเภท --</option>
                                            <option value="รายเดือน">รายเดือน</option>
                                            <option value="อุปกรณ์-น้ำยา">อุปกรณ์-น้ำยา</option>
                                            <option value="อื่นๆ">อื่นๆ</option>
                                        </select>
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">รายละเอียด</label>
                                        <input type="text" className="form-control" value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} required />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">จำนวนเงิน (บาท)</label>
                                        <input type="number" className="form-control" value={form.amount} onChange={(e) => setForm({ ...form, amount: e.target.value })} required />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">หมายเหตุ</label>
                                        <textarea className="form-control" value={form.notes} onChange={(e) => setForm({ ...form, notes: e.target.value })}></textarea>
                                    </div>
                                    <button type="submit" className="btn btn-success w-100">{form.id ? "🔄 อัปเดต" : "➕ เพิ่ม"}</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

