import React, { useRef } from "react";
import html2canvas from "html2canvas";

const ShoeDetailModal = ({ show, onClose, shoe }) => {
    if (!show || !shoe) return null;

    const Backend_URL = 'http://localhost:5000'; // ‡∏î‡∏∂‡∏á URL Backend
    const imageContainerRef = useRef(null); // ‡πÉ‡∏ä‡πâ Ref ‡∏à‡∏±‡∏ö‡∏û‡∏∑‡πâ‡∏ô‡∏ó‡∏µ‡πà‡∏£‡∏ß‡∏°‡∏£‡∏π‡∏õ

    // ‚úÖ ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏£‡∏ß‡∏°‡∏£‡∏π‡∏õ Before & After ‡πÅ‡∏•‡∏∞‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î
    const handleDownloadImage = async () => {
        if (!imageContainerRef.current) return;
        const canvas = await html2canvas(imageContainerRef.current);
        const image = canvas.toDataURL("image/png");

        const link = document.createElement("a");
        link.href = image;
        link.download = `before-after-${shoe.brand || "shoe"}.png`;
        link.click();
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content" style={{ backgroundColor: "#fff"}}>
                <h2>üëü ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤</h2>
                
                {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤ */}
                <p><strong>‡πÅ‡∏ö‡∏£‡∏ô‡∏î‡πå:</strong> {shoe.brand}</p>
                <p><strong>‡∏£‡∏∏‡πà‡∏ô:</strong> {shoe.model}</p>
                <p><strong>‡∏™‡∏µ:</strong> {shoe.color}</p>
                <p><strong>‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏´‡∏ï‡∏∏:</strong> {shoe.notes || "-"}</p>

                {/* ‚úÖ ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏£‡∏π‡∏õ Front (Before & After) */}
                <h3>üì∑ ‡∏†‡∏≤‡∏û Before & After (Front)</h3>
                <div ref={imageContainerRef} style={{ display: "flex", gap: "10px", border: "2px solid #000", padding: "10px", justifyContent: "center" }}>
                    <div>
                        <h4>Before</h4>
                        {shoe.image_before_front ? (
                            <img src={`${Backend_URL}${shoe.image_before_front}`} alt="before-front" width="200" />
                        ) : "-"}
                    </div>
                    <div>
                        <h4>After</h4>
                        {shoe.image_after_front ? (
                            <img src={`${Backend_URL}${shoe.image_after_front}`} alt="after-front" width="200" />
                        ) : "-"}
                    </div>
                </div>

                {/* ‚úÖ ‡∏õ‡∏∏‡πà‡∏°‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ Before-After (Front) */}
                <button onClick={handleDownloadImage}>üì• ‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ Before-After</button>

                {/* ‡∏õ‡∏∏‡πà‡∏°‡∏õ‡∏¥‡∏î Modal */}
                <div className="modal-actions">
                    <button onClick={onClose}>‚ùå ‡∏õ‡∏¥‡∏î</button>
                </div>
            </div>
        </div>
    );
};

export default ShoeDetailModal;
